float t;

float sphere(vec3 r, float a)
{
    return length(r) - a;
}

float torus(vec3 r, vec2 a)
{
    vec2 p = vec2(length(r.xz) - a.x, r.y);
    return length(p) - a.y;
}

float plane(vec3 r, vec3 o, vec3 n) {
    return dot(r - o, n);
}

float cylinder(vec3 r, vec2 a)
{
	vec2 p = abs(vec2(length(r.xz), r.y)) - a;

	return min(max(p.x, p.y), 0.0) + length(max(p, 0.));
}

float bud(vec3 r, float a)
{
	r.xz += cos((r.y + r.z) * 3.) * 0.2;

	r.y += sin(length(r.xzx) * 6.) * 0.2;

    return length(r) - a + sin(length(r.xz) * 12.) * 0.4;
}

float stem(vec3 r, vec2 a)
{
	r.xz += cos(r.y) * 0.15 + sin(r.y * 1.5) * 0.3;
	r.xz += cos(r.y * 0.25) * 1.;

	vec2 p = abs(vec2(length(r.xz), r.y)) - a;

	return min(max(p.x, p.y), 0.0) + length(max(p, 0.));
}

float leaf(vec3 r, vec3 a)
{
	r.xz += sin(length(r.xz + 1.) * 1.5);
	r.y += (r.x + r.z) * 0.5;
	r.x *= 0.9;

    vec3 p = abs(r) - a;

    return length(max(p, 0.));
}

float box(vec3 r, vec3 a)
{
    vec3 p = (abs(r) - a);
    return length(max(p, 0.));
}

float shade(vec3 n, vec3 rd)
{
    return clamp(max(dot(n, -rd), 0.) + 1., 0., 1.);
}

vec3 fog(float z, vec3 col, vec3 fogCol)
{
    return mix(fogCol, col, exp(-z));
}

float hash(vec2 r) {
    return fract(sin(dot(r, vec2(15.5921, 96.654654))) * 23626.3663);
}

mat3 obj;

vec2 map(vec3 r)
{
    float b = 0.05;
    vec3 rr = vec3(0.);
    rr.x = floor(r.x / b) * b;
    rr.y = floor(r.y / b) * b;
    rr.z = floor(r.z / b) * b;

    vec3 rb1 = obj * rr;

    vec2 b1 = vec2(
        bud(rotX(0.6) * rotZ(-0.1) * rb1, 2.),
        (int(sin(rb1.xyz * 5.) * cos(rb1.yxz * 5.) * 2.) % 2)
    );
	vec2 s1 = vec2(
        stem(rb1 + vec3(0., 7., 1.), vec2(.3, 6.81)),
        (int(sin(rb1.y * 3.1) * 2.) % 2) + 2
    );

	vec2 l1 = vec2(
        leaf(rb1 + vec3(1.5, 5., 2.2), vec3(1., 0.1, 1.0)),
        (int(sin(rb1.x * rb1.z * 4.1) * 2.) % 2) + 4
    );

    return minx2(l1, minx2(b1, s1));
}

vec3 matCol(vec2 o)
{
    if (o.y == 0.)
        return normalize(vec3(1., 0., .0));

    if (o.y == 1.)
        return normalize(vec3(1.1, 0., .6));

    if (o.y == 2.)
        return (vec3(0.4, .5, 0.));

    if (o.y == 3.)
        return normalize(vec3(.2, 1., 0.4));

    if (o.y == 4.)
        return (vec3(0.8, .5, 0.));

    if (o.y == 5.)
        return normalize(vec3(.5, .5, 0.));

    return vec3(0.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    t = -iTime * 0.4;

    obj = rotY(t);
    obj *= mat3(2.);

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    mat3 cam = rotY(-PI) * rotX(0.3);

    vec3 ro = vec3(0., 0.5, -3.3);
    vec3 rd = cam * normalize(vec3(uv * 2., -1.));
    vec3 r = ro;

    vec3 bcol = normalize(vec3(.8, 2., 0.7)) * (.7 + cos(length(uv.xy) * 1.5) * .6) * 0.8;
    vec4 col = vec4(0.);
    col.rgb = bcol;

    float sh = 1.;

    for (int i = 0; i < MAXIT; ++i) {
        vec2 d = map(r);
        float z = length(r - ro);

        if (d.x < EPSILON) {
            col.rgb = mix(col.rgb, 
				matCol(d), 
				shade(normalize(r), rd));
            col.rgb = fog(z * 0.1, col.rgb, bcol);
            break;
        }

        d.x *= 0.7 - 0.1 * hash(uv);
		r += rd * d.x * 0.15;

        sh = (float(i) / float(MAXIT));
    }

    if (sh < 0.8)
		col.rgb *= exp(-sqrt(sh) * 1.2);

    fragColor = vec4(col.rgb, 1.);
}

float sierpinski(vec3 p)
{
    const int iter_cnt = 20;
    float scale = clamp(sin((iTime - 7.0) * 0.2) + 2.02, 1.0, 2.0);
    float offset = 1.0;
    for (int n = 0; n < iter_cnt; n++)
    {
       if(p.x + p.y < 0.0) p.xy = -p.yx; // fold 1
       if(p.x + p.z < 0.0) p.xz = -p.zx; // fold 2
       if(p.y + p.z < 0.0) p.zy = -p.yz; // fold 3	
       p = p * scale - offset * (scale - 1.0);
    }
    return (length(p)) * pow(scale, -float(iter_cnt));
}

vec3 rotate(vec3 p, float angleX, float angleY)
{
    float cosA = cos(angleY);
	float sinA = sin(angleY);
	vec3 r = vec3(p.x, sinA * p.z + cosA * p.y, cosA * p.z - sinA * p.y);
    cosA = cos(angleX);
	sinA = sin(angleX);
	return (-vec3(cosA * r.x - sinA * r.z, r.y, sinA * r.x + cosA * r.z));
}

vec3 hsv2rgb(vec3 c)
{
    vec3 rgb = clamp(abs(mod(c.x * 6.0 + vec3(0.0,4.0,2.0),6.0) - 3.0) - 1.0, 0.0, 1.0);
	return (c.z * mix(vec3(1.0), rgb, c.y));
}

float distSphere(vec3 p, float r)
{
	return (length(p) - r);
}

float distPlane(vec3 p, vec4 n)
{
	return (dot(p, n.xyz) + n.w);
}

bool bgS = false;
bool bgP = false;
float distanceEstimate(vec3 p)
{
	float dSphere = -distSphere(p, 30.0);	
	float dFractal = sierpinski(p);
    float dPlane = distPlane(p, normalize(vec4(-10.0, -10.0, 10.0, 10.1)));
    bgP = dPlane < dFractal ? true : false;
    bgS = dSphere < dPlane ? true : false;
	return (min(min(dFractal, dPlane), dSphere));
}

vec3 getNormal(vec3 pos, float dist)
{
    vec2 eps = vec2(0.001, 0.0);
	return normalize(vec3(
           distanceEstimate(pos + eps.xyy),
           distanceEstimate(pos + eps.yxy),
           distanceEstimate(pos + eps.yyx)) - dist);
}

float softShadow(in vec3 ro, in vec3 rd, in float k)
{
    float res = 1.0;
    float t = 0.0;
    for(int i = 0; i < 64; i++)
    {
        float d = distanceEstimate(ro + rd * t);
        res = min(res, k * d/t);
        if(res < 0.001)
            break;
        t += clamp(d, 0.01, 0.2);
    }
    return (clamp(res, 0.0, 1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
