const int pt_count = 8;

const float pi = 3.14159265359;


vec3 hsv_to_rgb(float h, float s, float v) {
    float c = v * s;
    float m = v - c;    
    float x = c * (1.0 - abs(mod(h / (pi / 3.0), 2.0) - 1.0));

    vec3 pure;
    if (h < (pi/3.0)) {
        pure = vec3(c, x, 0.0);
    } else if (h < (2.0 * pi / 3.0)) {
        pure = vec3(x, c, 0.0);
    } else if (h < pi) {
        pure = vec3(0.0, c, x);
    } else if (h < (4.0 * pi / 3.0)) {
        pure = vec3(0.0, x, c);
    } else if (h < (5.0 * pi / 3.0)) {
        pure = vec3(x, 0.0, c);
    } else {
    	pure = vec3(c, 0.0, x);
    }

    return m + pure;
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.yy;
    vec2 center = vec2(iResolution.x / iResolution.y / 2.0, 0.5);

    float pixel_size = 3.0 / iResolution.x;

    vec3 col = vec3(0.0, 0.0, 0.0);
   	float min_dist = length(iResolution.xy);

    for (int i = 0; i < pt_count; ++i) {
        float prop = float(i);
        float hue = prop / float(pt_count) * 2.0 * pi;

        float time = iTime + hue;
        float radius = cos(time * 3.0) * 0.25 + 0.25;
        vec2 pt = vec2(cos(time) * radius, sin(time) * radius) + center;
        vec3 pt_color = hsv_to_rgb(hue, 1.0, 1.0);

        float dist = length(pt - uv);
        if (dist < min_dist) {
            min_dist = dist;
            if (dist < pixel_size) {
                col = vec3(1.0, 1.0, 1.0);
            } else {
                col = pt_color;
            }
        }
    }

    fragColor = vec4(col,1.0);
}

#define EPSILON 0.001

vec2 rand2d(vec2 p) {
	return fract(sin(vec2(
        dot(p, vec2(31.516, 42.567)),
        dot(p, vec2(74.52, 636.642))
    )) * 10000.0);
}

vec2 ballProject(vec3 p) {
    return vec2(p.x / (1.0 - p.z), p.y / (1.0 - p.z)); 
}

float perlin(vec2 p) {
    vec2 u = floor(p);
    vec2 f = fract(p);
    vec2 s = smoothstep(0.0, 1.0, f);

    vec2 a = rand2d(u);
    vec2 b = rand2d(u + vec2(1.0, 0.0));
    vec2 c = rand2d(u + vec2(0.0, 1.0));
    vec2 d = rand2d(u + vec2(1.0, 1.0));

    return mix(mix(dot(a, f), dot(b, f - vec2(1.0, 0.0)), s.x),
               mix(dot(c, f - vec2(0.0, 1.0)), dot(d, f - vec2(1.0, 1.0)), s.x),
               s.y) * 0.5 + 0.5;
}

float sphereSDF(vec3 p) {
    return length(p) - 1.0;
}

float sceneSDF(vec3 p) {
 	return sphereSDF(p);   
}

vec3 rayDirection(float fov, vec2 size, vec2 coord) {
    vec2 xy = coord - size / 2.0;
    float z = size.y / tan(radians(fov) / 2.0);
    return normalize(vec3(xy, z));
}

vec3 normalOf(vec3 p) {
    return normalize(vec3(
    	sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

vec3 phong(vec3 p, vec3 eye) {
    vec3 lightPos = vec3(sin(iTime) * 30.0, 10.0, cos(iTime) * 30.0),
        lightColor = vec3(1.0, 1.0, 1.0),
        objColor = vec3(1.0, 0.5, 0.0);

    vec3 n = normalOf(p);
    float shinyness = 32.0;
    float specularMultiplier = 0.0;

    vec2 proj = ballProject(p);
    float perl = perlin(proj);

    if (perl < 0.45) {
        objColor = vec3(0.1, 0.2, 0.7);
        n = n + normalize(p - eye) * perl * 0.3;
        specularMultiplier = 1.0;
    } else if (perl < 0.55) {
        objColor = vec3(0.3, 0.9, 0.3);
        n = n + vec3(rand2d(proj), rand2d(proj + 1.0).x) * 0.1;
    } else {
        objColor = vec3(1.0, 1.0, 1.0);
        n = n - normalize(p - eye) * perl * 0.5;
        specularMultiplier = 1.3;
    }


    vec3 lightDir = normalize(lightPos - p);
    vec3 refl = normalize(reflect(-lightDir, n));
    vec3 eyeDir = normalize(eye - p);

    float diffuse = dot(lightDir, n);
    if (diffuse < 0.0) {
        return vec3(0.0, 0.0, 0.0);
    }

    vec3 o = objColor * (0.1                                       // ambient
        + dot(lightDir, n)                                         // diffuse
        + pow(dot(eyeDir, refl), shinyness) * specularMultiplier); // specular
    return o;
}

float march(vec3 eye, vec3 direction, float start, float end) {
    float depth = start;

    for (int i = 0; i < 200; i++) {
    	float dist = sceneSDF(eye + direction * depth);
        if (dist <= EPSILON) {
            return depth + dist;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    vec3 dir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(0.0, 0.0, -6.0);
    float dist = march(eye, dir, 0.0, 200.0);
    if (dist >= 200.0) {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
        return;  
    }

    vec3 p = eye + dir * dist;
    fragColor = vec4(phong(p, eye), 1.0);
}

vec4 sphere = vec4(0.0, 0.0, 1.0, 0.4);
vec3 lightPos = vec3(-9.0, 8.0, -8.0);
vec3 lightCol = vec3(0.8, 0.9, 1.0);

float sphereDist1(vec3 ro, vec3 rd)
{
	float R = sphere.w;
	vec3 d1 = sphere.xyz - ro;
	float b = dot(rd, d1);
	float d2 = dot(d1, d1) - b * b;
	float h2 = R * R - d2;
    if (h2 <= 0.0)
        return (-1.0);
    return (b - sqrt(h2));
}

float sphereDist(vec3 p)
{
 return (length(p - sphere.xyz) - sphere.w);   
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float getShadow(vec3 ro, vec3 rd, float tmin, float tmax, const float k)
{
	float res = 1.0;
    float t = tmin;
    for( int i = 0; i < 50; i++)
    {
		float h = sphereDist(ro + rd * t);
        res = min(res, k * h / t);
        t += clamp(h, 0.02, 0.20);
        if(res < 0.005 || t > tmax)
            break;
    }
    return clamp(res, 0.0, 1.0);
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 getNormal(vec3 pos)
{
    const float ep = 0.0001;
    vec2 e = vec2(1.0, -1.0) * 0.5773;
    return normalize( e.xyy*sphereDist(pos + e.xyy*ep) + 
					  e.yyx*sphereDist(pos + e.yyx*ep) + 
					  e.yxy*sphereDist(pos + e.yxy*ep) + 
					  e.xxx*sphereDist(pos + e.xxx*ep));
}

//smooth color gradient - https://www.shadertoy.com/view/4df3Rn
float mandelbrot(vec2 c)
{
    vec2 z = vec2(0.0);
    const float B = 256.0;
    float l = 0.0;
    for (int i = 0; i < 512; i++)
    {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
        if (dot(z, z) > (B * B))
            break;
        l += 1.0;
    }
    if (l > 511.0)
        return (0.0); 
    //float l = l - log(log(length(z))/log(B))/log(2.0);
    l = l - log2(log2(dot(z, z))) + 4.0;
    return (l);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 aspectRatio = vec2(iResolution.x / iResolution.y, 1.0);
    vec2 uv = aspectRatio * (fragCoord.xy / iResolution.xy - 0.5);
    vec2 mouse = 7.0 * (iMouse.xy / iResolution.xy - 0.5);  
    vec3 ro = vec3(0.0, 0.0, 0.0);
    vec3 rd = normalize(vec3(uv, 1.0));

	float rot = iTime * 0.2;
    mat3 rotX = mat3(
        vec3(cos(mouse.x - rot), 0.0, sin(mouse.x - rot)),
        vec3(0.0, 1.0, 0.0),
        vec3(-sin(mouse.x - rot), 0.0, cos(mouse.x - rot)));  
	mat3 rotY = mat3(
        vec3(1.0, 0.0, 0.0),
        vec3(0.0, cos(mouse.y), sin(mouse.y)),
        vec3(0.0, -sin(mouse.y), cos(mouse.y)));

    vec3 color = vec3(0.09 - uv.x, 0.38, uv.x + 0.4) * (0.9 - uv.x);

    float dSphere = sphereDist1(ro, rd);
    if (dSphere < 0.0)
    {
		fragColor = vec4(color, 1.0);
		return;
    }
	vec3 pos = ro + dSphere * rd;
	vec3 normal = getNormal(pos);
	vec3 v3 = rotX * rotY * normal;
    vec2 v2 = 1.5 * v3.xy / abs(v3.z);
    float dMb = mandelbrot(v2);

    vec3 material = 0.44 + 0.5 * cos(3.0 + dMb * 0.11 + vec3(0.0, 0.5, 1.0)); 
    vec3 lightDir = normalize(vec3(lightPos - pos));
    vec3 reflectDir = normalize(reflect(lightDir, normal));
    float shadow = getShadow(pos, lightDir, 0.001, 1.0, 8.0);

    float diffuse = clamp(dot(normal, lightDir), 0.0, 1.0) * shadow * 2.0;
    float ambient = 0.6 + 0.6 * normal.y;
	float specular = pow(clamp(dot(reflectDir, rd), 0.0, 1.0), 16.0) * 2.2 * shadow;

	color = lightCol * material * (diffuse + specular + ambient);
    color = sqrt(color);
    fragColor = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 20.0 * (fragCoord - iResolution.xy / 2.0) / min(iResolution.y, iResolution.x);

    float lar;

    for (int i=0; i <15; i++)
    {
     lar = length(vec2(uv.x, uv.y));

        uv.x = uv.x - cos(uv.y + sin(lar)) + cos(iTime / 9.0);
        uv.y = uv.y - cos(uv.x + cos(lar)) + sin(iTime / 12.0);
    }





    // Normal Mode
    fragColor = vec4(cos(lar * 2.0), cos(lar * 3.0), cos(lar * 1.0), 1.0);

    //Darck mode
    //fragColor = vec4(cos(lar * 3.0), cos(lar * 1.0), cos(lar * 1.0), 1.0);


}

#define PI 3.14159265358979323846
#define aa 0.02

vec2 rotate2D (vec2 st, float angle, float offset) 
{
    st -= offset;
    st = mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle)) * st;
    st += offset;
    return st;
}

vec2 inverseMatrix(vec2 st, float angle, float offset) 
{
	//st -= offset;
	//float s = (st.y + st.x*cos(angle)/sin(angle))/(sin(angle)+(cos(angle)*cos(angle)/sin(angle)));
	//float t = (s*cos(angle)-st.x)/sin(angle);
    //float s = st.y*sin(angle) + st.x*cos(angle);
 	//float t = st.y*cos(angle) - st.x*sin(angle);
	//st = vec2(s,t);
	//st += offset;

    // this line replaces the above 7 lines
    st = rotate2D(st, angle, offset);

	// this is a mask so that just the overlapping values are selected
    st *= step(0.0,st.x) * (1.0-step(1.0, st.x));
    st *= step(0.0,st.y) * (1.0-step(1.0, st.y));
    st *= smoothstep(0.0,aa,st.x) * (smoothstep(1.0,1.0-aa,st.x));
    st *= smoothstep(0.0,aa,st.y) * (smoothstep(1.0,1.0-aa,st.y));

	return st;
}

void mainImage ( out vec4 fragColor, in vec2 fragCoord ) 
{
    //vec2 st = fragCoord/iResolution.xy;
    //st.x *= iResolution.x/iResolution.y;
	vec2 st = fragCoord/iResolution.y;

	//create a grid of tiles that is 5 tall
    float numTiles = 5.0;
    st *= numTiles;    
    vec2 i_st = floor(st);
    vec2 f_st = fract(st);
    vec2 tile = f_st;

    // rotate each tile by the angle, which increases in each column
    float angle = PI/10.0*(i_st.x+1.0)*sin(iTime);
    float offset = 0.5;
    st = rotate2D(tile,angle,offset);

    // only show the square, we want black space in between each square
    st *= smoothstep(0.0,aa,st.x) * (smoothstep(1.0,1.0-aa,st.x));
    st *= smoothstep(0.0,aa,st.y) * (smoothstep(1.0,1.0-aa,st.y));

    // this section finds the values of the neighbor's overlapping corner
    angle = PI/10.0*(i_st.x)*sin(iTime);
    vec2 neighborLeft = f_st + vec2(1.0,0.0);
    neighborLeft = inverseMatrix(neighborLeft,angle,offset);
    angle = PI/10.0*(i_st.x+1.0)*sin(iTime);
    vec2 neighborBelow = f_st + vec2(0.0,1.0);
    neighborBelow = inverseMatrix(neighborBelow,angle,offset);
    angle = PI/10.0*(i_st.x+2.0)*sin(iTime);
    vec2 neighborRight = f_st + vec2(-1.0,0.0);
    neighborRight = inverseMatrix(neighborRight,angle,offset);
    angle = PI/10.0*(i_st.x+1.0)*sin(iTime);
    vec2 neighborAbove = f_st + vec2(0.0,-1.0);
    neighborAbove = inverseMatrix(neighborAbove,angle,offset);

    // this is my hacky method of masking in the overlapped section
    //st *= step(neighborLeft.x+neighborLeft.y, 0.01);
    //st *= step(neighborBelow.x+neighborBelow.y, 0.01);
    //st *= step(neighborRight.x+neighborRight.y, 0.01);
    //st *= step(neighborAbove.x+neighborAbove.y, 0.01);
    //st += neighborLeft;
    //st += neighborBelow;
    //st += neighborRight;
    //st += neighborAbove;
    st = max(st,neighborLeft);
    st = max(st,neighborBelow);
    st = max(st,neighborRight);
    st = max(st,neighborAbove);

    // output
    vec3 color = vec3(st,0.0);
    fragColor = vec4(color,1.0);
}

#define N 32.

vec2 rot(vec2 v, float angle)
{
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c)*v;
}

float hash( float n )
{
    return fract(sin(n)*43758.5453);
}

float noise( in vec2 x )
{
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f*f*(3.0-2.0*f);
    float n = p.x + p.y*57.0;
    return mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
               mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y);
}

vec4 rect(vec2 uv, vec2 pos, vec2 dim, vec3 col){

	if ((uv.x>=pos.x && uv.x<=pos.x+dim.x) && (uv.y>=pos.y && uv.y<=pos.y+dim.y)) {
		return vec4(col,1.0);
	}
	return vec4(0.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec2 p = gl_FragCoord.xy / iResolution.xy - 0.5;
  p.x *= iResolution.x / iResolution.y;

	vec4 color=vec4(0.);
	float dx=1./N;
	float dy=1./N;
	for(float t=0.;t<=1.;t+=(1./N))
	{
		float xi=N*t*dx;
		float yi=N*t*dy;
		float ddx=1.-xi*2.0;
		float ddy=1.-yi*2.0;
		//float an=noise(vec2(t*4.,iTime*4.));
		//vec2 r=rot(p,an);
		vec2 r=rot(p,1.4*sin(iTime+t*4.));
		color+=rect(r,vec2(-0.5+xi,-0.5+yi),vec2(ddx,ddy),vec3(t));
	}


	color=sqrt(color*0.2);

	fragColor = vec4( vec3( color ), 1.0 );

}

