#define MAX_STEPS 300
#define MAX_DEPTH 50.0
#define EPSILON 0.001

mat2 rot(float a)
{
  return mat2(cos(a), -sin(a), sin(a), cos(a));
}

// iq's anti-aliased checker pattern
float checkers(vec2 p)
{
    vec2 w = fwidth(p) + 0.001;
    vec2 i = 0.9*(abs(fract((p-0.5*w)*0.5)-0.5)-abs(fract((p+0.5*w)*0.5)-0.5))/w;
    return 1.2 - 0.5*i.x*i.y;
}

float opSU(float d1, float d2, float k) {
  float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0);
  return mix( d2, d1, h) - k*h*(1.0-h);
}

float line( vec3 p, vec3 a, vec3 b )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h );
}

float pln(vec3 p, vec3 n, float d)
{
  return dot(p, n) - d;
}

float box(vec3 p, vec3 b) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sph(vec3 p, float s)
{
  return length(p) - s;
}

float pyramid(vec3 p) {
   float s = 1.2;
   p.xz = abs(p.xz);
   float l1 = line(p,vec3(s, -s, s),vec3(0, -s, s));
   float l2 = line(p,vec3(s, -s, s), vec3(s, -s, 0));
   float l3 = line(p,vec3(0, s, 0), vec3(s, -s, s));
   return min(min(l1,l2),l3)-0.025;
}


float sdf(vec3 p)
{
  p.xz *= rot(iTime/2.0)*rot(0.3);

  float t = iTime/2.0;
  float c = cos(t)/3.;
  float s = sin(t)/3.;

  float p1 = pln(p, vec3(0.0, 1.0, 0.0), -2.0);
  float py1 = abs(pyramid(p));

  vec3 ps = p-vec3(0,-0.43,0);
  float r = 0.5;
  float us = 0.4;
  float s1 = sph(ps-vec3(c,  0., s ), r);
  float s2 = sph(ps-vec3(c,  s , 0.), r);
  float s3 = sph(ps+vec3(0., c , s ), r);
  float fft = texture(iChannel0, vec2(0.35, 0.)).r*.1;

  float ss = opSU(opSU(s1, s2, us), s3, us)-fft;
  return min(min(ss, py1), p1);
}

vec3 getNormal(vec3 p) {
   const float h = 0.00001;
   const vec2 k = vec2(1,-1);
   return normalize( k.xyy*sdf( p + k.xyy*h ) +
                     k.yyx*sdf( p + k.yyx*h ) +
                     k.yxy*sdf( p + k.yxy*h ) +
                     k.xxx*sdf( p + k.xxx*h) );
}

vec3 phongLightContrib(vec3 kd, vec3 ks, float alpha, vec3 p, vec3 eye, vec3 lightPos, vec3 lightInt)
{
  vec3 N = getNormal(p);
  vec3 L = normalize(lightPos - p);
  vec3 V = normalize(eye - p);
  vec3 R = normalize(reflect(-L, N));

  float dotLN = dot(L, N);
  float dotRV = dot(R, V);

  if (dotLN < 0.0) {return vec3(0.0);}
  if (dotRV < 0.0) {return lightInt * (kd * dotLN);}
  return lightInt * (kd * dotLN + ks * pow(dotRV, alpha));
}

vec3 phong(vec3 ka, vec3 kd, vec3 ks, float alpha, vec3 p, vec3 eye)
{
  const vec3 ambientLux = vec3(0.3);
  vec3 colour = ambientLux * ka;

  vec3 light1Pos = vec3(0.0, 0.4, -2.0);
  vec3 light1Int = vec3(0.7);

  vec3 light2Pos = vec3(0.0, 0.5, -1.0);
  vec3 light2Int = vec3(0.3);

  colour += phongLightContrib(kd, ks, alpha, p, eye, light1Pos, light1Int);
  //colour += phongLightContrib(kd, ks, alpha, p, eye, light2Pos, light2Int);
  return colour;
}

vec3 calcSceneColour(vec3 p, float t1, vec3 ro)
{
  vec3 lum = phong(
    vec3(  0.7,  0.0, 0.0), // ambient
    vec3(  1.0,  0.0, 0.0), // diffuse
    vec3(  1.0, 1.0, 1.0), // spectral
    3000.0,                 // shininess
    p,
    ro
  );
  return lum;
}

vec3 march(vec3 ro, vec3 rd)
{
  vec3  col   = vec3(0);
  float depth = 10.0/float(MAX_STEPS);
  float dist;
  vec3 pf;
  vec3 rf;
  vec3 pp;
  float tf;
  for (int i=0; i<MAX_STEPS; i++)
  {
      vec3 p = ro + rd * depth;
      p.y -= 1.3;
      p.z -= 0.5;
      dist = sdf(p.xyz);
      if (depth > MAX_DEPTH) {
          break;
      } else if (dist < EPSILON)
      {
        pf = p;
        tf = depth;
        rf = ro;
        if (pf.y > -1.4) {
           col = calcSceneColour(p, tf, rf);
        }
        break;
      }
      depth += dist*0.85;
  }
  if (pf.y < -1.4) {
          pf.xz *= rot(iTime/2.0) * rot(0.3);

    float r = texture(iChannel0, vec2(0.35, 0.)).r*.7;
    col = vec3(checkers(pf.xz)*mix(vec3(0.24,0.03,0.15), vec3(0.3,0.03,0.4), r));
    col*=30.0/(tf*tf*tf);
  } else {

  }
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);
  vec3 ro = vec3(0.0,1.0,-4.0);
  vec3 rd = vec3(uv, 1.0);
  vec3 c = march(ro, rd);
  fragColor = vec4(c, 1.0);
  fragColor = vec4(pow(c,vec3(0.4545)), 1.0);
}

#define PI 3.14159265

// decrease octaves for better performances
#define OCTAVES 10

// rotation matrix
mat2 rot(float a){
	return mat2(cos(a), -sin(a),
               sin(a), cos(a));
}


// perlin noise functions
// https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
float rand(vec2 c){
	return fract(sin(dot(c.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float noise(vec2 p, float freq ){
	float unit = 1./freq;
	vec2 ij = floor(p/unit);
	vec2 xy = mod(p,unit)/unit;
	xy = 3.*xy*xy-2.*xy*xy*xy;
	float a = rand((ij+vec2(0.,0.)));
	float b = rand((ij+vec2(1.,0.)));
	float c = rand((ij+vec2(0.,1.)));
	float d = rand((ij+vec2(1.,1.)));
	float x1 = mix(a, b, xy.x);
	float x2 = mix(c, d, xy.x);
	return mix(x1, x2, xy.y);
}

float pnoise(vec2 p, int res){
	float persistance = .4;
	float n = 0.;
	float normK = 0.;
	float f = 4.;
	float amp = 1.;
	for (int i = 0; i<res; i++){
		n+=amp*noise(p, f);
		f*=2.;
		normK+=amp;
		amp*=persistance;
	}
	float nf = n/normK;
	return nf*nf*nf*nf;
}


// height function for terrain generation
float f(vec2 p){
    return clamp(pnoise(p*0.5, OCTAVES)*0.7, 0.01, 100.); // clamp see level at 0.01
}

float f(float x, float y){ // overloaded method of previous function
	return f(vec2(x, y));
}

// ray marching for terrain detection
// from : https://www.iquilezles.org/www/articles/terrainmarching/terrainmarching.htm
bool castRay(in vec3 ro, in vec3 rd, out float resT){
    float dt0 = 0.005;
    float mint = 0.25;
    float maxt = 4.;

    float lh = 0.;
    float ly = 0.;

    vec3 p = ro;

    float dt = dt0;
    for(float t = mint; t < maxt; t += dt){
    	p = ro + rd * t;
        float h = f(p.xz);

        if(p.y < h){
            resT = t - dt + dt*(lh-ly)/(p.y-ly-h+lh);
            return true;
        }
        lh = h;
        ly = p.y;

        dt = dt0 + 0.005*t;
    }
    return false;
}

// get the normal vector for a given point on the terrain
vec3 getNormal( in vec3 p )
{
    float eps = 0.001;
    return normalize( vec3( f(p.x-eps,p.z) - f(p.x+eps,p.z),
                            2.0f*eps,
                            f(p.x,p.z-eps) - f(p.x,p.z+eps) ) );
}

// give a random 2d vector
vec2 rand2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

// smooth animated voronoi for water rendering
// adapted from : https://iquilezles.org/www/articles/smoothvoronoi/smoothvoronoi.htm
float vor(in vec2 uv){
    vec2 p = floor(uv);
    vec2 f = fract(uv);

    float res = 8.0;
    for( int j=-1; j<=1; j++ )
        for( int i=-1; i<=1; i++ )
        {
            vec2 b = vec2( i, j );
            vec2 pt = rand2( p + b );
            //animate the point
            pt = 0.5+0.5*sin(pt*6. + iTime*5.);
            vec2  r = vec2( b ) - f + pt;
            float d = dot( r, r );

            res += 1.0/pow( d, 8.0 );
        }
    return pow( 1.0/res, 1.0/16.0 );
}

float fw(float x, float y){
    return vor(vec2(x, y)*75.)*0.0005;// only a small variation in height
}

// get the normal vector on water based on voronoi height map
vec3 getNormalWater( in vec3 p )
{
    float eps = 0.001;
    return normalize( vec3( fw(p.x-eps,p.z) - fw(p.x+eps,p.z),
                            2.0f*eps,
                            fw(p.x,p.z-eps) - fw(p.x,p.z+eps) ) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    // pixel color
    vec3 col = vec3(0.);

    // camera movements
    float time = iTime * 0.01;
    float c2 = cos(time);
    c2 *= c2;
    float s2 = sqrt(2.);
    float cx = 6.*s2*sin(time)/(1.+c2);
    float cz = 6.*s2*sin(time)*cos(time)/(1.+c2);

    // ray
    vec3 ro = vec3(cx, 0., cz);
    float h = f(ro.xz);
    ro.y = 0.5 + h;
    vec3 rd = normalize(vec3(uv, 1.));

    // rotate the ray
    rd.yz *= rot(0.35);
    rd.xz *= rot(2.*sin(iTime*0.1));

    const vec3 sky = vec3(0.65, 0.6, 0.55); // sky color
    const vec3 sun = normalize(vec3(1., 1., 0.)); // sun direction

    float t = 10.;
    float t1; // keep track of the first distance, for the fog
    if(castRay(ro, rd, t)){
        t1 = t;
        vec3 p = ro + rd * t;

        bool calcColor = true;
        bool hitWater = false;

        vec3 nml; // normal vector

        if(p.y-0.001 <= 0.01){ // if the ray hits the water level
            hitWater = true;

            nml = getNormalWater(p);

            rd = reflect(rd, nml); // reflect the ray

            if(castRay(p, rd, t)){ // if the ray hits the terrain again
                p = p + rd * t;
                hitWater = false;
            } else {
                calcColor = false;
            }
        }

        if(calcColor){ // calculate terrain coloring if the ray ends on it
            vec3 tex1 = texture(iChannel0, p.xz*2.).rgb;
            vec3 tex2 = texture(iChannel1, p.xz).rgb;

            if(!hitWater) nml = getNormal(p);

            float ud = clamp(dot(vec3(0., 1., 0.), nml), 0., 1.);
            ud = pow(ud, 12.);

            vec3 grass = vec3(0.5, 1., 0.5);
            vec3 rock = vec3(0.6, 0.55, 0.5);
            vec3 sand = vec3(1.2, 1.2, 0.8);

            col = grass * (tex2 + tex1) / 1.5 * ud;
            col += rock * (1.-ud) * tex1.rrr;

            col = mix(col, vec3(1.), smoothstep(0.1, 0.15, p.y)*ud);

            float v = texture(iChannel0, p.xz*20.).r;
            col = mix(sand * v, col, smoothstep(0.01, 0.0155, p.y));

            float nd = clamp(dot(nml, sun), 0., 1.);
            col *= (nd*(2.-ud));
        } else {
            float nd = clamp(dot(nml, sun), 0., 1.);
            col = sky * nd;
        }

        if(hitWater) col = mix(col, vec3(0., 0.35, 0.7), 0.1); // add blue color to water

        col = clamp(col, 0., 1.);
        col = mix(col, sky, smoothstep(0.5, 4.5, t1)); // fog based on ray hit distance
    } else {
    	col = sky;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

