float fField(vec3 p) {
    float height = 1.;

    float ball = fSphere(p, 8.);
    p.y -= height;
    float cut = fSphere(vec3(p.x+28.*sin(iTime * .4), p.y-19., p.z), 19.);

    p.y += 3.2;
    ball = fOpUnionRound(ball, fSphere(p, 5.), 0.8);

    float bumper = fOpUnionRound(fSphere(vec3(p.x, p.y-7., p.z + 5.+ sin(iTime * .4)), 5.), fSphere(vec3(p.x-3.+ sin(iTime * .2), p.y-7., p.z - 4.), 5.), 0.8);
    bumper = fOpUnionRound(bumper, fSphere(vec3(p.x+3.+ sin(iTime * .3), p.y-6., p.z - 4.), 4.), .8);
    bumper = fOpUnionRound(bumper, fSphere(vec3(p.x, p.y-23. + sin(iTime * .2), p.z), 19.), 0.8);
    bumper = fOpUnionRound(bumper, fSphere(vec3(p.x+3., p.y-5. + sin(iTime * .6), p.z - 4.), 4.), 0.8);
    bumper = fOpUnionRound(bumper, fSphere(vec3(p.x+3. * sin(iTime * .6), p.y-5., p.z + 4.), 4.), 0.8);
    bumper = fOpUnionRound(bumper, fSphere(vec3(p.x-4., p.y-6., p.z + 2. + 2. * sin(iTime * .6)), 4.), 0.8);

    ball = max(ball, -bumper);

    ball = fOpUnionRound(ball, fSphere(vec3(p.x + 2. + 2. * sin(iTime * 3.), p.y - 3. + 7. * sin(iTime * 6.), p.z), 1.7), .5);
    ball = fOpUnionRound(ball, fSphere(vec3(p.x - 2. + 2. * sin(iTime * 3. + 3.), p.y - 3. + 7. * sin(iTime * 6. + 4.), p.z), 1.), .5);
    ball = fOpUnionRound(ball, fSphere(vec3(p.x, p.y - 3. + 7. * sin(iTime * 6. + 2.), p.z - 2. + 2. * sin(iTime * 3. + 3.)), 1.), .5);


    return ball;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (12.0+sin(iTime)*4.0) * (fragCoord - iResolution.xy / (2.0)) / min(iResolution.y, iResolution.x);

    float lar;

    for (float i=0.0; i < 6.0; i++)
    {
        lar = length(vec2(uv.x, uv.y));
		float uvx1= uv.x;
        uv.x = - uv.y + sin(uv.x)*(uv.x);
        uv.y = - uvx1 + sin(uv.y)*(uv.y);
    }
    // Output to screen
    fragColor = vec4(cos(lar * 2.0), cos(lar * 3.0), cos(lar * 1.0), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    vec2 board1Center = vec2(1.0 + 0.6 * sin(1.4*iTime), 0.5 + 0.5*cos(1.4*iTime));
    vec2 board2Center = vec2(1.2 + 0.5 * sin(2.2*iTime), 0.3 + 0.4*cos(2.2*iTime));

    float board1RingSize = 0.07;
    float board1PulseSpeed = 5.0f;   
    float board2RingSize = 0.07;   

    float intensity = 1.0 - mod(-board1PulseSpeed*iTime + distance(board1Center, uv)/board1RingSize, 1.0);       
    float intensityXor = mod(-board1PulseSpeed*iTime + distance(board1Center, uv)/board1RingSize, 1.0);

    vec2 board2DistortUv = vec2(uv.x + 0.2*sin(iTime+3.0*uv.x), uv.y + 0.2*sin(1.1*iTime+2.0*uv.y));
    float xorPhase = floor(mod(distance(board2Center, board2DistortUv)/board2RingSize, 2.0));

    fragColor = vec4(intensity*xorPhase + intensityXor*(1.0-xorPhase), 0.8*intensity*xorPhase + intensityXor*(1.0-xorPhase), intensity*xorPhase + intensityXor*(1.0-xorPhase), 1.0 );

}

