struct ray
{
	vec3 o;
	vec3 d;
	float l;
};

mat2 rot(float a)
{
	float s = sin(a);
	float c = cos(a);
	return mat2(c, -s, s, c);
}

vec4 opu(vec4 a, vec4 b)
{
	return (a.w < b.w) ? a : b;	
}

float nnn(float p, float a, float b, float c)
{
	return cos(-p*a + iTime * b)*c;
}

vec4 geo(vec3 p)
{
	vec3 color_head = mix(vec3(0.0,1.0,0.0), vec3(1.0,1.0,0.0), cos(iTime * 11.));

	float height = -0.1;

	vec4 head = vec4(color_head,length(p - vec3(0.75,height,1.5))-0.4) 
        + nnn(p.x,4.2,10.5,0.2);
	vec4 eye = vec4(vec3(1.0),length(p - vec3(0.75,height+0.15,1.3))-0.25) 
        + nnn(p.x, 4.2, 10.5, 0.2);
	vec4 eyeB = vec4(vec3(0.),length(p - vec3(0.6,height+0.18,1.28))-0.15) 
        + clamp(nnn(p.x, 4.2, 10.5, 0.2),-0.9,0.15);
	vec4 plane = vec4(0.2,0.6,0.4,(p.y + 0.55));
	vec3 bRot = p;
	bRot.yz *= rot(0.4);
	vec4 box = vec4(1.0,1.0,0.0,length(
        max(abs(bRot - vec3(-0.9,-0.6,1.3)) - vec3(1.15,0.02,0.02),0.0) + nnn(p.x, 2.1, 10.5, 0.2)) - 0.06);

	vec4 g = opu(head, plane);
	vec4 g2 = opu(g, box);
	vec4 g3 = opu(g2, eye);
	vec4 g4 = opu(g3, eyeB);
	return g4;
}

vec4 march(ray r)
{
	vec3 col = vec3(1.0);
	for(int i = 0; i < 64; i++)
	{
		vec3 p = r.o + r.d * r.l;
		vec4 g = geo(p);
		r.l += g.w;
		col = g.rgb;
		if(r.l > 32.)
			break;
	}
	return vec4(col, r.l);
}

vec3 normal(vec3 p)
{
	vec2 of = vec2(0.001, 0.0);
	float copy = geo(p).w;
	return normalize(copy - vec3(geo(p - of.xyy).w, geo(p - of.yxy).w, geo(p - of.yyx).w));
}

float shadow(vec3 ro, vec3 rd, float b)
{
	float t = 0.01;
	float res = 1.;
	for(int i = 0; i < 16; i++)
	{
		float copy = geo(ro + rd * t).w;
		res = min(res, b * copy/t);
		if(copy < 0.001)
			break;	
		t += copy;
		if(t < 0.01 || t > 3.)
			break;
	}
	return clamp(res,0.2,1.);
}

float lighting(vec3 p)
{
	vec3 lp = vec3(1.0,2.,-1.);
	vec3 lPos = normalize(lp - p);
	vec3 n = normal(p);

	float light = clamp(dot(lPos, n), 0.1,1.);
	return light * shadow(p, lp, 0.8);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -0.5 *iResolution.xy)/iResolution.y;

   	ray r;
	r.o = vec3(0.);
	uv.xy += sin(iTime * 25.)*0.015;
	r.d = vec3(uv, 1.0);
	vec4 m = march(r);
	vec3 mm = r.o + r.d * m.w;

    vec2 p = mm.xz;
    p.x-=iTime * 6.;
    if(m.rgb == vec3(0.2,0.6,0.4))
    {
     	if(fract(sin(p.x*0.5)*0.5)>0.45) m.rgb = mix(m.rgb,vec3(0.6,0.6,0.3)*2.,uv.x); 
    }

	vec3 col = m.rgb * lighting(mm);
    col *= exp(-0.05 * pow(m.w, 3.));
	col += (vec3(0.2,0.35,0.25) * 0.6) / 1.5;
    fragColor = vec4(col,1.0);
}

#define PI 3.1415926535897932384626433832795

float dot2( in vec2 v ) { return dot(v,v); }
float msign( in float x ) { return (x>0.0)?1.0:-1.0; }

// http://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
vec2 sdSqLine( in vec2 p, in vec2 a, in vec2 b )
{
	vec2 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return vec2( dot2(pa-ba*h), ba.x*pa.y-ba.y*pa.x );
}

float sdCrescent(vec2 p, float r0, float r1, float d, float sign0, float sign1)
{
    float a = (r0*r0 - r1*r1 + d*d) / (2.0 * d);

    if( a < r0)
    {
        p.y = abs(p.y);
        float b = sqrt(r0*r0-a*a);
        float k = p.y*a - p.x*b;
        float h = min(d*sign0*(d*(p.y-b)-k ),
                      d*sign1*k);
        if (h>0.0)
        {
            return length(p-vec2(a,b));
        }
    }

    return max(sign0*(length(p          )-r0),
               sign1*(length(p-vec2(d,0))-r1));
}

// http://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
vec2 sdSqArc( in vec2 p, in vec2 a, in vec2 b, in float h, float d2min )
{
    vec2  ba  = b-a;
    float l   = length(ba);
    float ra2 = h*h + l*l*0.25;

    // recenter
    p -= (a+b)/2.0 + vec2(-ba.y,ba.x)*h/l;

    float m = ba.y*p.x-ba.x*p.y;
    float n = dot(p,p);

    if( abs(h)*abs(ba.x*p.x+ba.y*p.y) < msign(h)*l*0.5*m )
    {
        d2min = min( d2min, n + ra2 - 2.0*sqrt(n*ra2) );
    }

    return vec2(d2min, -max(m,ra2-n) );
}


