vec3 generateBorder(in vec3 col, in float border)
{
    col = mix( col, vec3(0.0), 1.0-smoothstep(0.0,0.015,border) );
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    vec2 p2 = p;

    // distance computations
    float lineWidth = 0.08;

    float dMask = sdA(p);

    float t = mod(iTime * 0.5, 1.) * PI * 2.;

    p += vec2(-sin(t), cos(t)) * 0.08;
    float transform = sign(mod(p.y, lineWidth) / lineWidth - 0.5);
    float d = sdA(p);

    p2 -= vec2(-sin(t), cos(t)) * 0.08;
    float transform2 = sign(mod(p2.y, lineWidth) / lineWidth - 0.5);
    float d2 = sdA(p2);

    vec3 col = vec3(1.0) - sign(d)*vec3(1.0);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.15,abs(d)) );
    col *= transform;

    vec3 col2 = vec3(1.0) - sign(d2)*vec3(1.0);
	col2 = mix( col2, vec3(1.0), 1.0-smoothstep(0.0,0.15,abs(d2)) );
    col2 *= transform2;

    vec3 color = col + col2;

    float mask = 1. - sign(dMask);
	mask = mix(mask, 1., 1.0-smoothstep(0.0,0.05,abs(dMask)));

    //color = (1.-color)*mask + color*(1.-mask);
    color *= 1.-mask;

    // output
	fragColor = vec4(color, 1.0);
}

const vec3 white = vec3(1., 1., 1.);
const vec3 black = vec3(0., 0., 0.);
const vec3 gray  = vec3(.2, .2, .3);

// circleTest
//
// coord      - pixel to test
// center     - center of circle
// radius     - radius of circle
// width      - thickness of circle line
// pixelWidth - blendiness

vec3 circleTest(vec2 coord, vec2 center, float radius, float width, float pixelWidth) {
    float delta = distance(coord, center)-radius;

    // From shadertoy default shader
    vec3 inside = .5 + .5*cos(iTime+coord.xyx+vec3(0,2,4));

    float blend = smoothstep(0., pixelWidth, abs(delta) - width);

    if (delta  < 0.)
        return mix(white, inside, blend); // rgb -> white (inside  edge)
    else if (delta  > 0.)
        return mix(white, black, blend);  // white -> black (outside edge)
    else
        return white;					  // can't decide

}

// Credit: https://thebookofshaders.com/edit.php#10/ikeda-simple-grid.frag
//
float grid(vec2 st, float res){
    vec2 grid = fract(st*res);
    return 1.-(step(res,grid.x) * step(res,grid.y));
}


void mainImage(out vec4 out_color, vec2 fragCoord) {

    float aspect     = iResolution.y/iResolution.x,
          pixelWidth = 1./iResolution.x,
          width      = 2./iResolution.x,
          maxPetals  = 12.,
          br         = .1;


    vec2 dMouse = iMouse.xy == vec2(0) ? iResolution.xy/2. : iMouse.xy,
         cen    = vec2(1., aspect) / 2.,
    	 q      = fragCoord.xy/iResolution.x,
    	 nq     = q - cen,
    	 m      = dMouse.xy/iResolution.x,
         nm     = m - cen;

    float a   = atan(nq.y,nq.x);
    float p   = ceil(maxPetals * m.x);
	float vr  = 8. * br * aspect * nm.y; // -1./petals keeps bottom row as vr=0
    float s   = sin(p * a);
    float r   = br  + vr * s;

    vec3 c = circleTest(
        q, 
        cen,
        r,
        width, 
        pixelWidth
    );

    c = c == vec3(0.) ? gray * grid(fragCoord, maxPetals/iResolution.x ) : c;

    out_color = vec4(c, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;    
    uv.x+=uv.y*0.2;
    float t = iTime * 0.2;
    t*= uv.y > fract(uv.x*2.0)? 1.0: -1.0;
    uv.x += t;
    float y = uv.y;
    float e = uv.x*40.;
    float d = tan(e)*0.1+0.4+sin(uv.x*5.5)*0.1;
    float k = smoothstep(abs(y+0.5)*0.5,y*0.3,d);
    fragColor = vec4(k);
}

float rand(vec2 p) {
    return fract(sin(dot(p, vec2(12.344532, 4321.3432))) * 321321.6);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.xx;


    vec3 col = vec3(0.);

    for(int j=-1; j<=1; j++) {
        vec2 q = p;
        q = mod(q*6., 1.)-0.5;
        q.y -= float(j);

        vec2 id = floor(p*6.);

        q /= 0.5;
        q.y -= mod(-iTime*(0.5+rand(id.xx)*3.), 2.);
        q.y = q.y > 0. ? q.y/(2.0*exp(-abs(q.x))) : q.y;
        float d = smoothstep(0.1, 0.0, length(q)-0.3);

        col += d*vec3(0.6, 0.6, 1.0*(0.5+q.x) + (0.5-q.y));
    }

    fragColor = vec4(col,1.0);
 }

#define PI 3.14159265

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    float x0 = uv.x * 2. * PI;
    float p0 = uv.y * 2. * PI;

    float k = sin(iTime * 0.2) + 1.0;

    float p = p0;
    float x = x0;

    for (int i = 0; i < 50; ++i) {
    	p = mod(p + k * sin(x), 2. * PI);
    	x = mod(x + p, 2. * PI);
    }

    float q = abs(p - p0);

    vec3 col = vec3(
        sin(q), sin(q + PI / 3.), sin(q + 2. * (PI / 3.))
    );

    fragColor = vec4(col, 1.);
}

vec4 quaternion(vec3 axis,float angle){
	float halfang=angle/2.;
    return vec4(axis*sin(halfang),cos(halfang));
}

vec4 quaternionMultiply(vec4 q1,vec4 q2){
	return vec4(q1.xyz*q2.w+q1.w*q2.xyz+cross(q1.xyz,q2.xyz),q1.w*q2.w-dot(q1.xyz,q2.xyz));
}

vec3 rotation(vec4 q,vec3 pos){
	return pos+2.*cross(q.xyz,cross(q.xyz,pos)+q.w*pos);
}

float hash(float v){
