//------------------------------

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}


//Box function
float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf 
}


float differenceSDF(float distA, float distB) 
{
    return max(distA, -distB);
}

///-----------------------------




vec2 isphere( in vec4 sph, in vec3 ro, in vec3 rd )
{
    vec3 oc = ro - sph.xyz;

	float b = dot(oc,rd);
	float c = dot(oc,oc) - sph.w*sph.w;
    float h = b*b - c;

    if( h<0.0 ) return vec2(-1.0);

    h = sqrt( h );

    return -b + vec2(-h,h);
}

#define ZERO (min(iFrame,0))


float map( in vec3 p, out vec4 resColor )
{
    vec3 w = p;
    float m = dot(w,w);

    vec4 trap = vec4(abs(w),m);
	float dz = 1.0;


	for( int i=0; i<4; i++ )
    {
        dz = 8.0*pow(sqrt(m),7.0)*dz + 1.0;
		//dz = 8.0*pow(m,3.5)*dz + 1.0;

        float r = length(w);
        float b = 8.0*acos( w.y/r);
        float a = 8.0*atan( w.x, w.z );
        w = p + pow(r,8.0) * vec3( sin(b)*sin(a), cos(b), sin(b)*cos(a) );


        trap = min( trap, vec4(abs(w),m) );

        m = dot(w,w);
		if( m > 256.0 )
            break;
    }

    resColor = vec4(m,trap.yzw);

   float fra =0.25*log(m)*sqrt(m)/dz;


    float sdb1 =sdBox(p- vec3(0.0), vec3(0.3,1.0,1.0) );

    float res=999.9 ;

    float dif1= differenceSDF(sdb1,fra) ;

    res=min(res,dif1);
    return res;
}

float intersect( in vec3 ro, in vec3 rd, out vec4 rescol, in float px )
{
    float res = -1.0;

    // bounding sphere
    vec2 dis = isphere( vec4(0.0,0.0,0.0,1.25), ro, rd );
    if( dis.y<0.0 )
        return -1.0;
    dis.x = max( dis.x, 0.0 );
    dis.y = min( dis.y, 10.0 );

    // raymarch fractal distance field
	vec4 trap;

	float t = dis.x;
	for( int i=0; i<128; i++  )
    { 
        vec3 pos = ro + rd*t;
        float th = 0.25*px*t;
		float h = map( pos, trap );
		if( t>dis.y || h<th ) break;
        t += h;
    }


    if( t<dis.y )
    {
        rescol = trap;
        res = t;
    }

    return res;
}

float softshadow( in vec3 ro, in vec3 rd, in float k )
{
    float res = 1.0;
    float t = 0.0;
    for( int i=0; i<64; i++ )
    {
        vec4 kk;
        float h = map(ro + rd*t, kk);
        res = min( res, k*h/t );
        if( res<0.001 ) break;
        t += clamp( h, 0.01, 0.2 );
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 calcNormal( in vec3 pos, in float t, in float px )
{
    vec4 tmp;
    vec2 e = vec2(1.0,-1.0)*0.5773*0.25*px;
    return normalize( e.xyy*map( pos + e.xyy,tmp ) + 
					  e.yyx*map( pos + e.yyx,tmp ) + 
					  e.yxy*map( pos + e.yxy,tmp ) + 
					  e.xxx*map( pos + e.xxx,tmp ) );
}

const vec3 light1 = vec3(  0.577, 0.577, -0.577 );
const vec3 light2 = vec3( -0.707, 0.500,  0.707 );


vec3 render( in vec2 p, in mat4 cam )
{
	// ray setup
    const float fle = 1.5;

    vec2  sp = (2.0*p-iResolution.xy) / iResolution.y;
    float px = 2.0/(iResolution.y*fle);

    vec3  ro = vec3( cam[0].w, cam[1].w, cam[2].w );
	vec3  rd = normalize( (cam*vec4(sp,fle,0.0)).xyz );

    // intersect fractal
	vec4 tra;
    float t = intersect( ro, rd, tra, px );

	vec3 col;

    // color sky
    if( t<0.0 )
    {
     	col  = vec3(0.8,.9,1.1)*(0.6+0.4*rd.y);
		col += 5.0*vec3(0.8,0.7,0.5)*pow( clamp(dot(rd,light1),0.0,1.0), 32.0 );
	}
    // color fractal
	else
	{
        // color
        col = vec3(0.01);
		col = mix( col, vec3(0.10,0.20,0.30), clamp(tra.y,0.0,1.0) );
	 	col = mix( col, vec3(0.2,0.50,0.30), clamp(tra.z*tra.z,0.0,1.0) );
        col = mix( col, vec3(0.30,0.10,0.2), clamp(pow(tra.w,6.0),0.0,1.0) );
        col *= 0.5;
		//col = vec3(0.1);

        // lighting terms
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos, t, px );
        vec3 hal = normalize( light1-rd);
        vec3 ref = reflect( rd, nor );
        float occ = clamp(0.015*log(tra.x),0.0,1.0);
        float fac = clamp(1.0+dot(rd,nor),0.0,1.0);

        // sun
        float sha1 = softshadow( pos+0.001*nor, light1, 32.0 );
        float dif1 = clamp( dot( light1, nor ), 0.0, 1.0 )*sha1;
        float spe1 = pow( clamp(dot(nor,hal),0.0,1.0), 32.0 )*dif1*(0.04+0.96*pow(clamp(1.0-dot(hal,light1),0.0,1.0),5.0));
        // bounce
        float dif2 = clamp( 0.5 + 0.5*dot( light2, nor ), 0.0, 1.0 )*occ;
        // sky
        float dif3 = (1.0+0.3*nor.y)*(0.2+0.8*occ);

		vec3 lin = vec3(0.0); 
		     lin += 7.0*vec3(1.50,1.10,0.70)*dif1;
		     lin += 4.0*vec3(0.25,0.20,0.15)*dif2;
        	 lin += 1.5*vec3(0.10,0.20,0.30)*dif3;
             lin += 2.5*vec3(0.35,0.30,0.25)*(0.05+0.95*occ); // ambient
        	 lin += 4.0*fac*occ;                          // fake SSS
		col *= lin;
		col = pow( col, vec3(0.7,0.9,1.0) );                  // fake SSS
        col += spe1*15.0;
        //col += 8.0*vec3(0.8,0.9,1.0)*(0.2+0.8*occ)*(0.03+0.97*pow(fac,5.0))*smoothstep(0.0,0.1,ref.y )*softshadow( pos+0.01*nor, ref, 2.0 );
        //col = vec3(occ*occ);
    }

    // gamma
	col = sqrt( col );

    // vignette
    col *= 1.0 - 0.05*length(sp);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime*.5;

    // camera
	float di = 1.4+0.1*cos(.29*time);
	vec3  ro = di * vec3( cos(.33*time), 0.8*sin(.37*time), sin(.31*time) );
	vec3  ta = vec3(0.0,0.1,0.0);
	float cr = 0.5*cos(0.1*time);

    // camera matrix
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
    vec3 cw = normalize(ta-ro);
	vec3 cu = normalize(cross(cw,cp));
	vec3 cv =          (cross(cu,cw));
    mat4 cam = mat4( cu, ro.x, cv, ro.y, cw, ro.z, 0.0, 0.0, 0.0, 1.0 );

	vec3 col = render(  fragCoord, cam );

	fragColor = vec4( col, 1.0 );
}

#define GammaValue 0.5
#define VignetteStrength 1.1
#define FocalLength 2.0
#define GroundHeight 4.0

mat3 rot_y(float angle)
{
    float s = sin(angle);
    float c = cos(angle);

    return mat3(c, 0.0 , -s,
              0.0, 1.0, 0.0,
               s, 0.0, c);
}

//The distance field fot the diamond
float diamond3( vec3 pos )
{
    float r = 2.0;
    return max(
        max(
            max(
                max(
                    max(abs(pos.x), abs(pos.y))
                    , abs(pos.z))
                , abs(pos.x + pos.y))
            , abs(pos.z + pos.y))
        , abs(pos.x + pos.z)) / 2.0 - r;
}

//Distance between two skew lines
float distBetweenLines(vec3 origin, vec3 dir, vec3 origin2, vec3 dir2)
{
    vec3 normal = normalize(cross(dir, dir2));
    return abs(dot(normal, origin - origin2));
}

//Distance to a line from origin along a ray
float distToLine(vec3 origin, vec3 dir, vec3 origin2, vec3 dir2)
{
    vec3 normal = normalize(cross(dir, dir2));
    vec3 n2 = cross(dir2, normal);
    return dot(origin2 - origin, n2) / dot(dir, n2);
}

//Laser brightness as a function of distance from surface
float laserBrightness(float dist)
{
    return smoothstep(0.2, 0.1,dist);
}

//The distance field function
float distModel(vec3 pos, float time)
{
    return min(diamond3(rot_y(time) * pos), -pos.y + GroundHeight);
}

//Computes the normal of the distance field 
vec3 normModel(vec3 pos, float time)
{
    float delta = 0.001;
    return normalize(vec3(
         distModel(pos + vec3(delta, 0.0, 0.0), time) - distModel(pos - vec3(delta, 0.0, 0.0), time),
         distModel(pos + vec3(0.0, delta, 0.0), time) - distModel(pos - vec3(0.0, delta, 0.0), time),
         distModel(pos + vec3(0.0, 0.0, delta), time) - distModel(pos - vec3(0.0, 0.0, delta), time)
    ) / (2.0 * delta));
}

float castRay(vec3 pos, vec3 dir, float time, float mult)
{
    float totalDist = 0.0;
    float distToSurface = distModel(pos, time) * mult;

    while(totalDist < 100.0)
    {
        totalDist += distToSurface + 0.01;
        vec3 currentPos = pos + dir * totalDist;
        distToSurface = distModel(currentPos, time) * mult;

        if(distToSurface < 0.00)
        {
        	totalDist += distToSurface;
            return totalDist;
        }
    }

    return 100.0;
}

vec3 rtMain(in vec2 fragCoord, in float time)
{
    vec2 position = fragCoord * -2.0 + 1.0;
    vec3 rayDir = normalize(vec3( position, FocalLength));
    vec3 cameraPos = vec3( 0, -2.0, -30.0 );
    vec3 lightPos = vec3(-3.784, -2.0, 3.26);
    vec3 laserPos = vec3(-1.0, -1.0, 2.0);
    vec3 laserLookAt = vec3(0.0, 0.0, 2.0);
    vec3 laserDir = normalize(laserLookAt - laserPos);
    vec3 laserColor = vec3(1.0, 0.01, 0.01);
    //The attenuation of light within the diamond
    float lightDecay = 0.4;
    float refractiveIndex = 1.0/1.43;
    //The attenuation of laser when ray is traversing through the diamond
    float laserAttenuation = 0.7;

    float laserHitDist = castRay(laserPos, laserDir, time, 1.0) ;
    vec3 laserHitPos = laserPos + laserDir * (laserHitDist + 0.05);
    vec3 normalAtLaserHit = normModel(laserHitPos, time);
    vec3 refractedLaserDir = refract(laserDir, normalAtLaserHit, refractiveIndex);


    float internalCastLen = castRay(laserHitPos, refractedLaserDir, time, -1.0);
    vec3 laserExitPos = laserHitPos + refractedLaserDir * internalCastLen;
    float laserInsideDist = length(laserExitPos - laserHitPos);
    vec3 normalAtLaserExit = -normModel(laserExitPos, time);
    vec3 laserExitDir = refract(refractedLaserDir, normalAtLaserExit, 1.0/refractiveIndex);    

    float distToSurface = castRay(cameraPos, rayDir, time, 1.0);
    vec3 cameraRayEnter = cameraPos + rayDir * (distToSurface + 0.1);
    float distInside = castRay(cameraRayEnter, rayDir, time, -1.0);
    vec3 cameraRayExit = cameraRayEnter + rayDir * distInside;



    vec3 frontNormal = normModel(cameraRayEnter, time);
    vec3 backNormal = normModel(cameraRayExit, time);
    vec3 lightDirFront = normalize(lightPos - cameraRayEnter);
    vec3 lightDirBack = normalize(lightPos - cameraRayExit);
    float shadeFront = max(0.0, dot(lightDirFront, frontNormal));
    float shadeBack = max(0.0, -dot(lightDirBack, backNormal));

    vec3 diamondColor = vec3(shadeBack) * exp(-lightDecay * distInside) + vec3(shadeFront);


    //Background
    diamondColor *= smoothstep(100.0, 80.0, distToSurface);

    float laserDist = distToLine(cameraPos, rayDir, laserPos, laserDir);    
    float laserIntensity = 0.0; 

    vec3 laserPoint = cameraPos + laserDist * rayDir;
    float distanceToLaser = dot(laserDir, laserPoint - laserPos);

    if(distanceToLaser < laserHitDist)
    {
        //Ray hasn't yet hit the diamond
        laserIntensity = laserBrightness(distBetweenLines(cameraPos, rayDir, laserPos, laserDir));
    }
    else
    {
        laserDist = distToLine(cameraPos, rayDir, laserHitPos, refractedLaserDir);
        laserPoint = cameraPos + laserDist * rayDir;
    	float distanceToLaserEnter = dot(refractedLaserDir, laserPoint - laserHitPos);

        if(distanceToLaserEnter < laserInsideDist)
        {
            //Laser is inside the diamond
            laserAttenuation *= exp(-lightDecay * (distanceToLaser - laserHitDist));
            laserIntensity = laserBrightness(distBetweenLines(cameraPos, rayDir, laserHitPos, refractedLaserDir));
        }
        else
        {
            //Laser has exited the diamond
            laserAttenuation *= exp(-lightDecay * laserInsideDist);
            laserIntensity = laserBrightness(distBetweenLines(cameraPos, rayDir, laserExitPos, laserExitDir));
            laserDist = distToLine(cameraPos, rayDir, laserExitPos, laserExitDir);
            laserPoint = cameraPos + laserDist * rayDir;
        }
    }

    if(laserPoint.y > GroundHeight)
    {
        //Laser has entered the ground
        laserAttenuation = 0.0;
    }

    //The attenuation of laser when ray is returning through the diamond
    float returnLaserAttenuation = 1.0;
    if(laserDist < distToSurface)
    {
        //Laser seen in front of the diamond
    }
    else if(laserDist < (distToSurface + distInside))
    {
       //Laser seen inside the diamond
       returnLaserAttenuation *= exp(-lightDecay * (laserDist - distToSurface));
    }
    else
    {
       //Laser seen behind the diamond
       returnLaserAttenuation *=  exp(-lightDecay * distInside);
    }

    //Add a laser dot to where the laser enters the diamond
    float laserOnSurface =  (laserBrightness(length(laserHitPos - laserPoint)))
        * abs(dot(frontNormal, -laserDir));
    laserOnSurface += (laserBrightness(length(laserExitPos - laserPoint)))
        * exp(-lightDecay * laserInsideDist)
        * 2.0 //Hack to make it more visible
        * abs(dot(backNormal, -refractedLaserDir));

    //If laser refracts back inside, there is no hitpoint on the ground plane
    if(dot(refractedLaserDir, laserExitDir) > 0.0)
    {
       	float distAfterExit = castRay(laserExitPos, laserExitDir, time, 1.0);
    	vec3 laserGroundPos = laserExitPos + laserExitDir * distAfterExit;
        laserOnSurface += (laserBrightness(length(laserGroundPos - laserPoint)))
            * exp(-lightDecay * laserInsideDist)
            * 2.0 //Hack to make it more visible
            * abs(dot(backNormal, -refractedLaserDir));        
    }

    diamondColor += laserOnSurface * returnLaserAttenuation * laserColor;

    return diamondColor + laserIntensity * laserColor * laserAttenuation * returnLaserAttenuation;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.y/iResolution.x;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy - vec2(0.5f)) * vec2(1.0, aspect) + vec2(0.5);
    // Time varying pixel color
    vec3 color = rtMain(uv, iTime * 0.3);

    //Gamma
    color.rgb = pow(color.rgb, vec3(GammaValue));

    //Saturate channels
    color.rgb = min(color.rgb, vec3(1.0));

    //Vignette
    color.rgb *= pow(1.0 - length(uv - vec2(0.5, 0.5)) * VignetteStrength, 0.5); 

    // Output to screen
    fragColor = vec4(color,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    float a = atan(uv.y, uv.x);
    float smth = length(uv) * sin(a * 5.) - sin(a * 5. + iTime);

    float r = 0.2;
    vec3 col = vec3(smoothstep(r, r + 0.02, smth));
    // col = vec3(smth);

    fragColor = vec4(col, 1.0);
}

