    for (int i = 0; i < motionBlurSteps + min(iFrame,0); ++i) {
        float subsample = bayer8(ivec2(fragCoord));
        float time = iTime + ((float(i)+subsample)/float(motionBlurSteps)-.5) * timeStep;
        col += smoothstep(-edge,edge,-get(uv,time));
    }
    col/=float(motionBlurSteps);

    col = clamp(col, 0., 1.);
