void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float PI = 3.14159;

    vec3 color = vec3(0.448,0.800,0.785);
    vec3 background = vec3(0.418,0.421,0.440);

    float width = iResolution.x;
    float height = iResolution.y;
    float ellipseScale = 2.0;

    float scale = 1.3;
    float borderWidthRatio = 0.06;
    float glowWidthRatio = 1.58;
    float animationSpeed = 0.42;

    float x = (fragCoord.x / iResolution.x);
    float y = (fragCoord.y / iResolution.y);

    x = (x * 2.0) - 1.0;
    y = (y * 2.0) - 1.0;

	float theta = atan(y, x);
    float tanTheta = y/x;

    float a = width / scale;
    float b = height / scale;

    //intersections with the ellipse and the function y = x * tan(theta) https://math.stackexchange.com/questions/22064/calculating-a-point-that-lies-on-an-ellipse-given-an-angle
    float x1 = a*b / sqrt(b*b + a*a * tanTheta * tanTheta);
    float y1 = a*b*tanTheta / sqrt(b*b + a*a * tanTheta * tanTheta);

    vec2 point = vec2(x, y) * max(width, height); // work in a square space
    vec2 pointOnEllipse = vec2(x1, y1);

    float distanceToEllipse = abs(length(pointOnEllipse) - length(point)) / max(width, height);
    float r = (distanceToEllipse / borderWidthRatio);

    float progress = mod(iTime * animationSpeed, 1.0);
    float current = (theta + PI); //[0 - 360]
    current = mod(current + PI / 2.0, 2.0 * PI);  //offset to put the beguining at the top
    current = current / (2.0 * PI); //ratio for animation


    vec3 currentColor = current <= progress  ? color.xyz : background.xyz;
    float glowRatio = current <= progress ? 1.0 / glowWidthRatio : 3.0;

    if(r < 1.0){
        fragColor = vec4(currentColor, 1.0);
    } else {
        float alpha = 1.0 - (r - 1.0) * glowRatio;
        fragColor = vec4(vec3(currentColor * alpha), alpha);
    }

}

