void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.y *=2.;
    uv.x *= iResolution.x/iResolution.y;

    vec3 uvR = make2DRotation(vec3(uv, 1.), PI/(iTime*iTime)) * vec3(uv, 1.);

    vec3 col = vec3(1.,1.,1.);
    vec3 bgcol = vec3(0.,0.,0.);

    if(uvR.y < sin(uvR.x*PI*iTime))
        col = bgcol;

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.yy;

    vec3 col = vec3(0.0);
    float t = iTime*0.2 + p.x*0.2+sin(p.y)*0.2+p.x*sin(iTime*0.2)+100.0;

    float pi = acos(-1.0);
    int cnt = 0;
    for(float r=t; r<pi*2.0 + t; r+=pi/3.0) {
        float c = cos(r), s = sin(r);

        if(length(p + 1.5*vec2(c, s)*sin(t+sin(r)))-0.6 <= 0.0) {
            cnt += 1;
        }

        if(length(p + 0.8*vec2(c, s)*sin(t+cos(r)))-0.2 <= 0.0) {
            cnt += 1;
        }


        if(length(p + 1.2*vec2(c, s)*sin(t+clamp(tan(r), -10., 10.)))-0.05 <= 0.0) {
            cnt += 1;
        }
    }

    switch(cnt%3) {
        case 0:
        col = vec3(0.0, 0.1, 0.5*(fract(sin(length(p)*20.+t*200.))*0.2+0.8));
        break;
        case 1:
        col = vec3(0.5, 0.5, 1.0)
            *(smoothstep(0.005, 0.006, fract(p.x+t))*0.2+0.6)
            *(smoothstep(0.005, 0.006, fract(p.y+t))*0.2+0.6);
        break;
        case 2:
        col = vec3(0.0, 0.0, 0.3)*(fract(t) - 0.2);
        break;
        default:
        col = vec3(0.0);
        break;
    }

    col *= 1.-length(p)*0.3;
    col = pow(col, vec3(0.5));
    fragColor = vec4(col,1.0);
}

