//
// Srinivasa Ramanujan, 22 December 1887 â€“ 26 April 1920.
//
// https://en.wikipedia.org/wiki/Srinivasa_Ramanujan
//
// Here we have a 12x12x12 cube, total volume 1728, that can be dissected and,
// with an extra piece, reassembled into a 9x9x9 cube and a 10x10x10 cube,
// Key 'x' shows the 10x10x10 cube.
//
// Some of Ramanujan's more profound work was in the area of partition theory,
// for example, Ramanujan's Congruences concerning p(n), the number of
// partitions of integer n (ie. the number of ways of expressing positive n as a
// sum of positive integers):
//
// https://en.wikipedia.org/wiki/Ramanujan%27s_congruences:
//
// p(5k+4) = 0 mod 5
// p(7k+5) = 0 mod 7
// p(11k+6) = 0 mod 11
//
// The first two are nicely illustrated with p(19) = 490 and all 490
// partitions appear in the background, grouped appropriately. (Key 'b'
// shows just the background).
//
// Matthew Arcus, mla, 26th April 2020
//
////////////////////////////////////////////////////////////////////////////////

int AA = 2; // Set to 1 if too slow.

float eyedist = 40.0;     // Eye is at (0,0,eyedist);
vec3 light = normalize(vec3(0,1,3)); // Direction of light
bool dorotate = true;

float PI = 3.1415927; 

// Raymarching configuration.
int maxsteps = 100;
float precis = 1e-3; 

int cubesize = 6; // Half width of cube
float dicesize = 0.4; // 
float de(vec3 p) {
  vec3 p0 = abs(p);
  p = fract(p);
  p -= 0.5;
  p = abs(p);
  float d = -1e8;
  d = max(d,p.x-dicesize);
  d = max(d,p.y-dicesize);
  d = max(d,p.z-dicesize);
  // Clip to cubesize
  d = max(d,p0.x-float(cubesize));
  d = max(d,p0.y-float(cubesize));
  d = max(d,p0.z-float(cubesize));
  return d;
}

vec3 getnormal(vec3 p, vec3 rd) {
  // Repeat DE calculation, but return normal to closest plane.
  p = fract(p);
  p -= 0.5;
  p = abs(p);
  float d1, d = -1e8;
  vec3 n = vec3(0);
  d1 = p.x-dicesize;
  if (d1 > d) { d = d1; n = vec3(1,0,0); }
  d1 = p.y-dicesize;
  if (d1 > d) { d = d1; n = vec3(0,1,0); }
  d1 = p.z-dicesize;
  if (d1 > d) { d = d1; n = vec3(0,0,1); }
  // Make sure normal points the right way.
  return dot(n,rd) < 0.0 ? n : -n;
}

// Rotate vector p by angle t.
vec2 rotate(vec2 p, float t) {
  return cos(t)*p + sin(t)*vec2(-p.y,p.x);
}

// Rotate according to mouse position
vec3 transformframe(vec3 p) {
  if (iMouse.x > 0.0) {
    // Full range of rotation across the screen.
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  // autorotation
  if (dorotate) {
    p.yz = rotate(p.yz,-iTime*0.125);
    p.zx = rotate(p.zx,iTime*0.1);
  }
  return p;
}

vec3 colours[] =
  vec3[](vec3(0,0,1),
         vec3(1,0,0),
         vec3(1,1,0),
         vec3(0,1,0),
         vec3(1));

// p is integer coordinates of 'dice'.
vec3 getdicecolor(ivec3 p) {
  p += cubesize;
  int t = int(p.x >= 9) + int(p.y >= 9) + int(p.z >= 9);
  return colours[t+int(cubesize == 5)];
}

float march(vec3 q, vec3 r) {
  float maxdist = 2.0*eyedist;
  float t = 0.0;
  for (int i = 0; i < maxsteps; i++) {
    vec3 p = q+t*r;
    float d = de(p);
    if (abs(d) < precis) return t; // Close enough to the surface.
    t += d;
    if (t > maxdist) break;
  }
  return -1.0;
}

bool raycolor(vec3 q, vec3 r, out vec3 color) {
  float t = march(q,r);
  if (t < 0.0) return false;
  // Get the surface point that has been hit,
  vec3 p = q+t*r;
  // and the normal at that point.
  vec3 normal = getnormal(p,r);

  // Apply lighting. This is a basic "Lambertian" model.
  color = 0.2+0.8*getdicecolor(ivec3(floor(p)));
  float ambient = 0.3;
  float diffuse = 0.7*clamp(dot(normal,light),0.0,1.0);
  color *= ambient+ diffuse;
  return true;
}

// Partitions. p(n,m) = number of partitions of n with parts no larger than m
// p(0,_) = 1
// p(n,0) = 0
// p(n,m) = p(n,m-1) + p(n-m,m)

const int ptable[20*20] =
