int pcount(int n, int m) {
  if (n == 0) return 1;
  if (m == 0) return 0;
  return ptable[20*(n-1)+m-1];
}

// Recurrence:
// parts(0,_) = [[]]
// parts(_,0) = []
// parts(n,m) = parts(n,m-1) + parts(n-m,m)
// Use p(n,m) to decide which branch of the recursion to follow.
int unrank(int n, int k, int t) {
  int m = n, i = 0;
  while (true) {
    int k1 = pcount(n,m-1);
    if (k < k1) m = m-1;
    else {
      t -= m;
      if (t < 0) return i;
      i++; n -= m; k -= k1;
    }
  }
}

vec3 bgcolor(vec2 fragCoord) {
  // 490 = 2*5*7*7 items, each 19 squares, arranged in 14 rows of 7
  // blocks of 5 subrows of 19 square. With a border.
  ivec2 pdim = ivec2(iResolution.xy);
  ivec2 pcoord = ivec2(fragCoord);
  pcoord.y = pdim.y-1-pcoord.y;
  vec3 col = vec3(0.2);
  // Width is 7 blocks of 19, plus 8 separators
  // Height is 14 blocks of 5, plus 15 separators
  int W = 7, W1 = 19;
  int H = 14, H1 = 5;
  int M = 1;
  ivec2 bdim = ivec2(W*(W1+M)+M, H*(H1+M)+M); //7*19+8;
  ivec2 bcoord = pcoord*bdim/pdim;
  int nwidth = bdim.x; //14*5+15;
  int nheight = bdim.y; //14*5+15;
  int i = bcoord.x, j = bcoord.y;
  int i1 = (i+W1)%(W1+M); // Index within a horizontal block
  int j1 = (j+H1)%(H1+M);   // Index within a vertical block
  if (i1 < W1 && j1 < H1) {
    int p = unrank(19,(i-M)/(W1+M)*(H*H1)+(j-M)/(H1+M)*H1+j1,i1);
    if (p%2 != 0) col = vec3(0.8);
    else col = vec3(0.5);
  }
  ivec2 boffset = (bcoord*pdim+bdim-1)/bdim; // Index of 1st pixel in block
  if (bcoord.x > 0 && j1 < H1 && pcoord.x == boffset.x) col = vec3(0);
  if (bcoord.y > 0 && i1 < W1 && pcoord.y == boffset.y) col = vec3(0);
  return col;
}

const int CHAR_B = 66;
const int CHAR_R = 82;
const int CHAR_X = 88;

bool key(int code) {
  return texelFetch(iChannel3, ivec2(code,2),0).x != 0.0;
}

void mainImage(out vec4 fragColor, vec2 fragCoord){
  if (key(CHAR_X)) cubesize = 5;
  dorotate = !key(CHAR_R);
  vec3 eye = vec3(0,0,eyedist);
  eye = transformframe(eye);
  light = transformframe(light);
  vec3 bgcol = bgcolor(fragCoord);
  vec3 col = vec3(0);
  if (key(CHAR_B)) {
    col = bgcol;
  } else {
    for (int i = 0; i < AA; i++) {
      for (int j = 0; j < AA; j++) {
        vec2 z = (2.0*(fragCoord+vec2(i,j)/float(AA))-iResolution.xy)/iResolution.y;
        vec3 ray = vec3(z,-4);
        ray = transformframe(ray);
        ray = normalize(ray);
        vec3 raycol;
        if (!raycolor(eye,ray,raycol)) raycol = bgcol;
        col += raycol;
      }
    }
    col /= float(AA*AA);
  }
  col = pow(col,vec3(0.4545)); // Gamma
  fragColor = vec4(col,1);
}

void mainImage(out vec4 O, vec2 C) {
	O-=O;
	for(float e,g=0.,i=0.;++i<50.;){
		vec3 p=g*vec3(C/iResolution.y-.7,1);
		p.z+=iTime*4.;
		p=mod(p,8.)-4.;
		p.xz=length(p.xz)*vec2(sin(iTime)*.5,2.);
		p.z=fract(p.z)-.5;
        for(int j=0;j++<3;)p.yz=abs(p.yz)-.1;
		g+=e=dot(abs(p),.5+p-p)-1.;
        e<.01?O.xyz+=abs(cos(p*9.+iTime))/i:p;
    }
}

// The larger the DISTORTION, the smaller the glow
const float DISTORTION = 0.2;
// The higher GLOW is, the smaller the glow of the subsurface scattering
const float GLOW = 6.0;
// The higher the BSSRDF_SCALE, the brighter the scattered light
const float BSSRDF_SCALE = 3.0;
// Boost the shadowed areas in the subsurface glow with this
const float AMBIENT = 0.0;
// Toggle this to affect how easily the subsurface glow propagates through an object

float subsurface(vec3 lightDir, vec3 normal, vec3 viewVec, float thickness) {
    vec3 scatteredLightDir = lightDir + normal * DISTORTION;
    float lightReachingEye = pow(clamp(dot(viewVec, -scatteredLightDir), 0.0, 1.0), GLOW) * BSSRDF_SCALE;
    float attenuation = 1.0;
	float totalLight = attenuation * (lightReachingEye + AMBIENT) * thickness;
    return totalLight;
}
mat3 rotateY(float rad)
{
    return mat3(vec3(cos(rad), 0, -sin(rad)),
                vec3(0, 1, 0),
                vec3(sin(rad), 0, cos(rad))
               );
}
mat3 rotateZ(float rad)
{
    return mat3(vec3(cos(rad), -sin(rad), 0),
                vec3(sin(rad), cos(rad), 0),
                vec3(0, 0, 1)
               );
}
mat3 rotateX(float rad)
{
    return mat3(vec3(1, 0, 0),
        		vec3(0, cos(rad), -sin(rad)),
                vec3(0, sin(rad), cos(rad))
               );
}
mat3 scale(float a, float b, float c)
{
    return mat3(vec3(a, 0, 0), vec3(0, b, 0), vec3(0, 0, c));
}
mat3 scale(float a)
{
    return scale(a, a, a);
}
float sdfBox(vec3 p, vec3 b)
{
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)), 0.) + length(max(d, 0.));
}
float sdfSphere(vec3 p)
{
    return length(p) - 0.5;
}
float sdfPlane(vec3 p)
{
    return dot(p, vec3(0, 0, 1));
}
vec2 closer(vec2 a, vec2 b)
{
    return a.x < b.x ? a: b;
}
float deg2rad(float r)
{
	return r / 180. * 3.14159;
}
vec2 map(vec3 pos) // find closest object
{
    vec3 sphereWorld = vec3(0.3, 0.3, -0.4);
    vec3 posSphereModel = pos - sphereWorld; // position in sphere model space
    vec2 sphereInfo = vec2(sdfSphere(posSphereModel), 1.);

    vec3 boxWorld = vec3(-0.35, -0.6, 0.3);
    vec3 posBoxModel = inverse(rotateY(deg2rad(35.))) * (pos - boxWorld);
    vec3 boxSize = vec3(0.2, 0.4, 0.2);
    vec2 boxInfo = vec2(sdfBox(posBoxModel, boxSize), 2.);

    vec3 boxWorld2 = vec3(0.35, -0.8, 0.3);
    vec3 posBoxModel2 = inverse(rotateY(deg2rad(60.))) * (pos - boxWorld2);
    vec3 boxSize2 = vec3(0.22, 0.22, 0.22);
    vec2 boxInfo2 = vec2(sdfBox(posBoxModel2, boxSize2), 8.);

    vec3 planeWorld1 = vec3(-1, 0, 0);
    vec3 posPlaneModel1 = inverse(rotateY(3.14 / 2.)) * (pos - planeWorld1);
    vec2 planeInfo1 = vec2(sdfPlane(posPlaneModel1), 3.);

    vec3 planeWorld2 = vec3(1, 0, 0);
    vec3 posPlaneModel2 = inverse(rotateY(3. * 3.14 / 2.)) * (pos - planeWorld2);
    vec2 planeInfo2 = vec2(sdfPlane(posPlaneModel2), 4.);

    vec3 planeWorld3 = vec3(0, 0, 0);
    vec3 posPlaneModel3 = (pos - planeWorld3);
    vec2 planeInfo3 = vec2(sdfPlane(posPlaneModel3), 5.);

    vec3 planeWorld4 = vec3(0, 1, 0);
    vec3 posPlaneModel4 = inverse(rotateX(3. * 3.14 / 2.)) * (pos - planeWorld4);
    vec2 planeInfo4 = vec2(sdfPlane(posPlaneModel4), 6.);

    vec3 planeWorld5 = vec3(0, -1, 0);
    vec3 posPlaneModel5 = inverse(rotateX(3.14 / 2.)) * (pos - planeWorld5);
    vec2 planeInfo5 = vec2(sdfPlane(posPlaneModel5), 7.);

    vec2 closest;
    closest = closer(boxInfo, planeInfo1);
    closest = closer(closest, planeInfo2);
    closest = closer(closest, planeInfo3);
    closest = closer(closest, planeInfo4);
    closest = closer(closest, planeInfo5);
    closest = closer(closest, boxInfo2);
    closest = closer(closest, sphereInfo);
    return closest;
}
float fiveTapAO(vec3 p, vec3 n, float k) {
    float aoSum = 0.0;
    for(float i = 0.0; i < 5.0; ++i) {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * 0.15 - map(p + n * i * 0.15).x);
    }
    return 1.0 - k * aoSum;
}
vec2 calcIntersection(in vec3 ro, in vec3 rd){
    float currentMinSdf = 0.002;
    float t = 0.0; // distance from ro (ray origin)
	float resDistance = -1.;
    float id = -1.;
    for (int i = 0; i < 100; ++i){
        if (currentMinSdf < 0.001 || t > 10.) break; // hit or exceed max distance
	   	vec2 hitInfo = map(ro + rd * t); // hit object distance and id
        currentMinSdf = hitInfo.x; // distance
        t += currentMinSdf; // update current distance from ro
        id = hitInfo.y;
    }
    if (t <= 10.) resDistance = t; // if in distance
    else id = -1.; // no hit
    return vec2(resDistance, id);
}
vec3 calcNormal(in vec3 pos){

	vec3 dx = vec3(0.001, 0.0, 0.0);    
	vec3 dy = vec3(0.0, 0.001, 0.0);    
	vec3 dz = vec3(0.0, 0.0, 0.001);
	vec3 nor = vec3(
	    map(pos + dx).x - map(pos - dx).x,
	    map(pos + dy).x - map(pos - dy).x,
	    map(pos + dz).x - map(pos - dz).x
    );
	return normalize(nor);
}
vec3 render(vec2 res, in vec3 ro, in vec3 rd)
{
    float ambient = 0.05;
    vec3 color = vec3(0);
    float lightX = 0.5 * sin(iTime * 4.);
    vec3 lightPos = vec3(lightX, 0.9, 0.8);
    if (res.y > 0.) // id
    {
    	vec3 pos = ro + rd * res.x;
    	vec3 norm = calcNormal(pos);
    	vec3 lightDir = normalize(lightPos - pos);
        float match = max(0., dot(lightDir, norm) / pow(length(lightPos - pos), 0.3));
        float ao = fiveTapAO(pos, norm, 2.);
        vec3 view = ro - pos;
        if (res.y == 1.) color = subsurface(-lightDir, norm, view, ao) * vec3(1., 1., 0.) * match + ambient;
        else if (res.y == 2.) color = subsurface(-lightDir, norm, view, ao) * vec3(1., 0.8, 0.4) * match + ambient;
		else if (res.y == 3.) color = ao * vec3(1, 0, 0) * match + ambient;
        else if (res.y == 4.) color = ao * vec3(0, 1, 0) * match + ambient;
		else if (res.y == 5.) color = ao * vec3(1, 1, 1) * match + ambient;   
        else if (res.y == 6.) color = ao * vec3(0.5, 0.5, 0.5) * match + ambient;
		else if (res.y == 7.) color = ao * vec3(1, 1, 0.8) * match + ambient;  
		else if (res.y == 8.) color = subsurface(-lightDir, norm, view, ao) * vec3(0.4, 0.4, 1) * match + ambient;   
    }
    return color;
}
mat3 lookAt(in vec3 eye, in vec3 ref)
{
    vec3 front = normalize(ref - eye);
    vec3 right = normalize(cross(front, vec3(0, 1, 0)));
    vec3 up = normalize(cross(right, front));
    return mat3(right, up, front);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2. * fragCoord.xy - iResolution.xy) / iResolution.y;
    vec3 eye = vec3(0, 0, 2), ref = vec3(0, 0, 0);
    vec3 direction = normalize(lookAt(eye, ref) * vec3(p.xy, 1));
    vec2 res = calcIntersection(eye, direction);
    fragColor = vec4(render(res, eye, direction), 1);
}

#define PI 3.1415926535897932384626433832795

float dot2( in vec2 v ) { return dot(v,v); }
float msign( in float x ) { return (x>0.0)?1.0:-1.0; }

// http://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
vec2 sdSqLine( in vec2 p, in vec2 a, in vec2 b )
{
	vec2 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return vec2( dot2(pa-ba*h), ba.x*pa.y-ba.y*pa.x );
}

float sdCrescent(vec2 p, float r0, float r1, float d, float sign0, float sign1)
{
    float a = (r0*r0 - r1*r1 + d*d) / (2.0 * d);

    if( a < r0)
    {
        p.y = abs(p.y);
        float b = sqrt(r0*r0-a*a);
        float k = p.y*a - p.x*b;
        float h = min(d*sign0*(d*(p.y-b)-k ),
                      d*sign1*k);
        if (h>0.0)
        {
            return length(p-vec2(a,b));
        }
    }

    return max(sign0*(length(p          )-r0),
               sign1*(length(p-vec2(d,0))-r1));
}

// http://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
vec2 sdSqArc( in vec2 p, in vec2 a, in vec2 b, in float h, float d2min )
{
    vec2  ba  = b-a;
    float l   = length(ba);
    float ra2 = h*h + l*l*0.25;

    // recenter
    p -= (a+b)/2.0 + vec2(-ba.y,ba.x)*h/l;

    float m = ba.y*p.x-ba.x*p.y;
    float n = dot(p,p);

    if( abs(h)*abs(ba.x*p.x+ba.y*p.y) < msign(h)*l*0.5*m )
    {
        d2min = min( d2min, n + ra2 - 2.0*sqrt(n*ra2) );
    }

    return vec2(d2min, -max(m,ra2-n) );
}


