// friol 2o2o
// music KV by Bloc https://soundcloud.com/kvmusicprod/bloc
// (watch this with music, please. be sure music is in sync)
// sdf functions by iq
// blob taken from mercury and modified
// da rest is by P.Mondrian
// 06.05.2020: reduced compilation time as for suggestions by iq
//

const int iterationAmount=256;
const float patternLength=14.5;
const float numPatterns=4.0;


float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }

vec3 rotx(in vec3 p, float a) 
{
	return vec3(p.x,
                cos(a) * p.y + sin(a) * p.z,
                cos(a) * p.z - sin(a) * p.y);
}

vec3 roty(in vec3 p, float a) {
	return vec3(cos(a) * p.x + sin(a) * p.z,
                p.y,
                cos(a) * p.z - sin(a) * p.x);
}

vec3 rotz(in vec3 p, float a) {
	return vec3(cos(a) * p.x + sin(a) * p.y,
                cos(a) * p.y - sin(a) * p.x,
                p.z);
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float sdSphere(vec3 p, float r)
{
    return length(p) - r;
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float sdPlane( vec3 p, vec4 n )
{
  return dot(p,n.xyz) + n.w;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdPyramid( vec3 p, float h)
{
  float m2 = h*h + 0.25;

  p.xz = abs(p.xz);
  p.xz = (p.z>p.x) ? p.zx : p.xz;
  p.xz -= 0.5;

  vec3 q = vec3( p.z, h*p.y - 0.5*p.x, h*p.x + 0.5*p.y);

  float s = max(-q.x,0.0);
  float t = clamp( (q.y-0.5*p.z)/(m2+0.25), 0.0, 1.0 );

  float a = m2*(q.x+s)*(q.x+s) + q.y*q.y;
  float b = m2*(q.x+0.5*t)*(q.x+0.5*t) + (q.y-m2*t)*(q.y-m2*t);

  float d2 = min(q.y,-q.x*m2-q.y*0.5) > 0.0 ? 0.0 : min(a,b);

  return sqrt( (d2+q.z*q.z)/m2 ) * sign(max(q.z,-p.y));
}

float sdOctahedron( vec3 p, float s)
{
  p = abs(p);
  float m = p.x+p.y+p.z-s;
  vec3 q;
       if( 3.0*p.x < m ) q = p.xyz;
  else if( 3.0*p.y < m ) q = p.yzx;
  else if( 3.0*p.z < m ) q = p.zxy;
  else return m*0.57735027;

  float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
  return length(vec3(q.x,q.y-s+k,q.z-k)); 
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float fBlob(vec3 p) 
{
    float fft = texture( iChannel0, vec2(0.0,0.0) ).x;
    p*=(1.5+(fft*0.4));
    p=roty(p,iTime);
    p=rotx(p,iTime);

    float PHI=(sqrt(5.0)*0.5 + 0.5);
	p = abs(p);
	if (p.x < max(p.y, p.z)) p = p.yzx;
	if (p.x < min(p.y, p.z)) p = p.yzx;
	float b = max(max(min(
		dot(p, normalize(vec3(.3, 1., 1.))),
		dot(p.xz, normalize(vec2(PHI+1., 1.)))),
		dot(p.yx, normalize(vec2(1, PHI)))),
		dot(p.xz, normalize(vec2(1, PHI))));
	float l = length(p)/(1.0+.01*abs(sin(iTime)));
	return l - 1.5 - 0.2 * (1.5 / 2.0)* .4*sin(min(sqrt(1.01 - b / l)*(3.141592 / 0.25), 3.141592));
}

vec2 SDF(vec3 r)
{
    vec3 rOrig=r;
    float mat=0.0;

    float plane0=sdPlane(r					,vec4(0.0,1.0,0.0,1.0));
    float plane1=sdPlane(r-vec3(0.0,0.1,0.0),vec4(0.0,-1.0,0.0,1.0));

    float domainRep=10.0;
    vec3 c=vec3(domainRep);
    r = mod(r+0.5*c,c)-0.5*c;

    float b0=sdBox(r,vec3(1.0,1.1,1.0));
    float b1=sdBox(r,vec3(2.0,.9,.8));
    float b2=sdBox(r-vec3(0.0,0.,0.0),vec3(0.8,0.9,2.0));

    float b3=sdBox(r-vec3(0.0,-0.9,-0.9),vec3(10.0,0.1,0.1));
    float b4=sdBox(r-vec3(0.9,-0.9,0.0),vec3(0.1,0.1,10.0));
    float b5=sdBox(r-vec3(-0.9,-0.9,0.0),vec3(0.1,0.1,10.0));
    float b6=sdBox(r-vec3(0.0,-0.9,0.9),vec3(10.0,0.1,0.1));
    float t1=opSubtraction(b1,b0);
    float t0=opSubtraction(b2,t1);
    t0=min(t0,b3);
    t0=min(t0,b4);
    t0=min(t0,b5);
    t0=min(t0,b6);

    float s0=sdSphere(r-vec3(.6*sin(iTime),0.2*cos(iTime),.5*sin(iTime)),0.3);
    float s1=sdSphere(r-vec3(.5*cos(iTime),-0.2*sin(iTime),0.0),0.2);
    float s2=sdSphere(r-vec3(.7*cos(iTime),-0.5*cos(iTime),-.2*sin(iTime)),0.2);

    //vec2 uv = ((normal.xy + 1.0) * 0.5)*4.0;
	//float h = texture(iChannel0, uv).x/32.0;  

    float h0=0.,h1=0.,h2=0.;
    float unionCoeff=0.2;
    float spheres=opSmoothUnion(opSmoothUnion(s0-h0,s1-h1,unionCoeff),s2-h2,unionCoeff);

    float pyr0=sdOctahedron((((r)-vec3(2.0,-2.0,0.0))),1.5);
    float pyr1=sdOctahedron((((r)-vec3(-2.0,-2.0,0.0))),1.5);

    float roamingSphere;
    float sphTime=iTime*(patternLength/10.615);

    float modTime=mod(iTime,patternLength*numPatterns);    
    if (modTime<patternLength)
    {
    	roamingSphere=sdSphere(r-vec3(0.0,-10.6,1.5+abs(5.0*sin(sphTime))),0.4);
    }
    else
    {
    	roamingSphere=sdSphere(r-vec3(0.0,-.6,1.5+abs(5.0*sin(sphTime))),0.4);
    }

    vec3 rr=rotz(r,3.141592/2.0);
    float roamingSphere2=sdTorus(rr-vec3(-.5,0.0,-1.5-abs(5.0*cos(sphTime))),vec2(0.35,0.2));

    float mirror=sdBox(r-vec3(0.0,0.0,5.0),vec3(.8,1.1,.1));


    float t=min(min(min(min(min(min(min(min(t0,plane0),plane1),spheres),pyr0),pyr1),roamingSphere),mirror),roamingSphere2);
    if (t==plane0) mat=1.0;
    if (t==plane1) mat=1.0;
    if (t==spheres) mat=2.0;
    if (t==pyr0) mat=3.0;
    if (t==pyr1) mat=4.0;
	if (t==roamingSphere) mat=6.0;                                 
	if (t==roamingSphere2) mat=7.0;                                 
    if (t==mirror) mat=5.0;

    if (modTime>=patternLength*3.0)        
    {
        float centeredBlob=fBlob(rOrig-vec3(5.0,0.0,1.0+iTime));
        if (centeredBlob<t)
        {
            return vec2(centeredBlob,8.0);
        }
    }

    return vec2(t,mat);   
}

vec3 calcNormal(vec3 pos)
{
    float c = SDF(pos)[0];
    vec2 eps_zero = vec2(0.01, 0.0);
    return normalize(vec3( SDF(pos + eps_zero.xyy)[0], SDF(pos + eps_zero.yxy)[0], SDF(pos + eps_zero.yyx)[0] ) - c);
}

vec2 castRay(vec3 rayOrigin, vec3 rayDir)
{
    float t = 0.0;

    for (int i = 0; i < iterationAmount; i++)
    {
        vec2 res = SDF(rayOrigin + rayDir * t);
        if (res[0] < (0.0001*t))
        {
            return vec2(t,res[1]);
        }
        t += res[0];
    }

    return vec2(-1.0,-1.0);
}

vec3 bounceRender(vec3 rayOrigin, vec3 rayDir, vec2 uv)
{
    vec3 col;
    vec3 L=normalize(vec3(1.0,0.2,-2.0));

    vec2 rayHit = castRay(rayOrigin, rayDir);
    float t=rayHit[0];

    if (t!=-1.0)
    {
    	vec3 pHit=rayOrigin + rayDir * t;
        vec3 N=calcNormal(pHit);
        float NoL = max(dot(N, L), 0.0);
        float mat=rayHit[1];
        if (mat==0.0)
        {
            col=vec3(NoL);
        }
        else if (mat==1.0) // plane
        {
            float lDist=distance(pHit,L+rayOrigin);
        	vec2 a = vec2(1.)*smoothstep(-0.05, 0.05, mod(pHit.x, 1.))*smoothstep(-0.05, 0.05, mod(pHit.z, 1.));
        	col = vec3(1.0-a.x,1.0-a.y,1.0-a.x);

            //col+=vec3(pow(clamp(1.0-lDist/6.0,0.0,1.0),1.0));
        }

        else if (mat==2.0) // spheres
        {
            NoL+=pow(NoL,32.0);
            col+=vec3(.2,.3,.5)*vec3(NoL);
        }
        else if (mat==3.0)
        {
            NoL+=pow(NoL,32.0);
            col+=vec3(1.0,0.0,0.0)*vec3(NoL);
        }
        else if (mat==4.0)
        {
            NoL+=pow(NoL,32.0);
            col+=vec3(1.0,1.0,0.0)*vec3(NoL);
        }

        col/=distance(pHit-rayOrigin,vec3(0.))*.3;
    }

    col=pow(col,vec3(0.58));
    return col;
}

vec4 render(vec3 rayOrigin, vec3 rayDir, vec2 uv)
{
    vec3 col=vec3(0.);
    vec3 L=normalize(vec3(1.0,0.2,-2.0));

    vec2 rayHit = castRay(rayOrigin, rayDir);
    float t=rayHit[0];

    if (t!=-1.0)
    {
    	vec3 pHit=rayOrigin + rayDir * t;
        float mat=rayHit[1];
        vec3 N=calcNormal(rayOrigin + rayDir * t);
        float NoL = max(dot(N, L), 0.0);

        if (mat==0.0)
        {
            col=vec3(NoL);
        }
        else if (mat==1.0) // plane
        {
            float lDist=distance(pHit,L+rayOrigin);
        	vec2 a = vec2(1.)*smoothstep(-0.05, 0.05, mod(pHit.x, 1.))*smoothstep(-0.05, 0.05, mod(pHit.z, 1.));
        	col = vec3(1.0-a.x,1.0-a.y,1.0-a.x);

            col+=vec3(pow(clamp(1.0-lDist/6.0,0.0,1.0),2.2));
        }
        else if ((mat==2.0)||(mat==6.0)||(mat==7.0)) // meatballs/roaming spheres
        {
            float modTime=mod(iTime,30.0);    
    		if ((modTime<10.0)&&(mat==6.0))
            {
                return vec4(0.,0.,0.,0.);
            }

            NoL=pow(NoL,32.0);
            NoL=clamp(NoL,0.0,1.0);
            vec3 colReflect=bounceRender(pHit,N,uv);
            if (mat==2.0) col=vec3(1.0,1.0,1.0)*vec3(NoL);
            else if (mat==7.0) col=vec3(1.,1.,1.)*vec3(NoL);
            else col=vec3(.2,.2,.85)*vec3(NoL);
            //col=vec3(0.5,0.5,0.5)*vec3(NoL);
            col=mix(col,colReflect,0.2);
        }
        else if (mat==3.0) // pyr1
        {
            NoL+=pow(NoL,32.0);
            col+=vec3(1.0,0.0,0.0)*vec3(NoL);
        }
        else if (mat==4.0) // pyr2
        {
            NoL+=pow(NoL,32.0);
            col+=vec3(1.0,1.0,0.0)*vec3(NoL);
        }
        else if (mat==5.0) // magic mirror
        {
            vec3 N=vec3(0.0,0.0,-1.0);
            vec3 colReflect=bounceRender(pHit,N,uv);
            col=colReflect*0.5;
        }
        else if (mat==8.0) // mysterious blob
        {
            vec3 centerVec=vec3(5.0,0.0,1.0+iTime);
            vec3 edgeVec=pHit-centerVec;

            edgeVec=normalize(edgeVec);
            float edge=1.0-dot(edgeVec,vec3(0.,0.,-1.0));
            edge=pow(edge,3.0);

            vec3 color1 = vec3(0.0,0.0,0.0);
            vec3 color2 = vec3(0.726,0.726,0.726);
            col = mix(color1,color2,edge);
           	col+=edge*pow(distance(pHit,centerVec)*.4,2.0)*vec3(0.38,0.38,0.64);
            //col=mix(col,color3,min(0.,edge-0.6)*2.0);
        }

    	float modTime=mod(iTime,patternLength*numPatterns);    
        if (modTime>=patternLength*3.0)        
        {
            if (mat!=8.0)
            {
                vec3 nrd=normalize(rayDir);
                float fft = texture( iChannel0, vec2(0.0,0.0) ).x;
                float myTime=iTime/8.0;
                vec2 b=abs(fract(vec2(1.)*nrd.z+myTime)-.5)*abs(fract(vec2(1.)*nrd.z+myTime)-.5);
                col+=vec3(
                    (.01*pow(b.x+.8,20.)+b.y/dot(nrd.xy,nrd.xy)*exp(.24-1./4.))*fft,
                    (.01*pow(b.x+.2,20.)+b.y/dot(nrd.xy,nrd.xy)*exp(.1-1./4.))*fft,
                    (.01*pow(b.x+.1,20.)+b.y/dot(nrd.xy,nrd.xy)*exp(.32-1./2.))*fft)/3.0;
            }
        }

        col/=distance(pHit-rayOrigin,vec3(0.))*.6;
    }

    col=pow(col,vec3(0.58));
    return vec4(col,1.0);
}

vec3 getCameraRayDir(vec2 uv, vec3 camPos, vec3 camTarget)
{
    vec3 camForward = normalize(camTarget - camPos);
    vec3 upz=vec3(0.,1.,0.);
    vec3 camRight = normalize(cross(upz, camForward));
    vec3 camUp = normalize(cross(camForward, camRight));

    float fPersp = 2.0;
    vec3 vDir = normalize(uv.x * camRight + uv.y * camUp + camForward * fPersp);

    return vDir;
}

vec2 normalizeScreenCoords(vec2 screenCoord)
{
    vec2 result = 2.0 * (screenCoord/iResolution.xy - 0.5);
    result.x *= iResolution.x/iResolution.y;
    return result;
}

