void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float myTime=iTime/8.0;
	vec2 uv = normalizeScreenCoords(fragCoord);

    vec3 camPos,camTarget;

    float modTime=mod(iTime,patternLength*numPatterns);

    if (modTime<patternLength) // magic mirror
    {
        modTime/=8.0;
        camPos = vec3(0.0,0.,-3.0+7.5-modTime);
        camTarget = vec3(.0, 0.,0.0+7.5-modTime);
    }
    else if ((modTime>=patternLength)&&(modTime<(patternLength*2.0))) // roaming camera
    {
        if (iTime>(patternLength*4.0))
        {
            camPos = vec3(cos(myTime)*4.0,0.,-3.0+iTime);
            camTarget = vec3(.0, 0.,0.0+iTime);
        }
        else
        {
            camPos = vec3(sin(myTime)*4.0,0.,-3.0+iTime);
            camTarget = vec3(.0, 0.,0.0+iTime);
        }
    }
    else if ((modTime>=patternLength*2.0)&&(modTime<(patternLength*3.0))) // fisheye
    {
    	uv*=(asin(length(uv)*0.75));
        camPos = vec3(sin(myTime)*5.0,0.6*cos(myTime),-3.0+iTime);
        camTarget = vec3(.0, 0.,0.0+iTime);
    }
    else // glowsphere
    {
        camPos = vec3(5.0,0.0,-3.0+iTime);
        camTarget = vec3(5.0, 0.,0.0+iTime);
    }




    vec3 rayDir = getCameraRayDir(uv, camPos, camTarget);   

    float fft = texture( iChannel0, vec2(0.0,0.0) ).x;

    vec4 finalCol = vec4(render(camPos, rayDir,uv).xyz*(1.2+fft*2.0),1.0);

    vec4 distCol=finalCol;
    float pixx=mod(abs(uv.x*64.0),1.0);
    if (pixx<0.33) distCol.x/=2.0;
    if ((pixx>=0.33)&&(pixx<0.66)) distCol.y/=2.0;
    if (pixx>0.66) distCol.z/=2.0;

    if ((modTime>=patternLength*2.0)&&(modTime<(patternLength*3.0)))
    {
        float mult=4.0;
        distCol+=sin(uv.x*mult)*cos(uv.y*mult);
    }

   	finalCol=mix(distCol,finalCol,0.5);

    if (iTime>(patternLength*4.0))
    {
        vec3 value = finalCol.rgb;
        vec3 oldcolor = value + (value * texture(iChannel1, (mod(fragCoord, 8.0) / 8.0)).rrr);
        finalCol = vec4(floor(oldcolor),1.0);    
    }

    fragColor=finalCol;
}

#define R(a)a=vec2(a.x+a.y,a.x-a.y)*.7
#define G(a,n)R(a);a=abs(a)-n;R(a)
void mainImage(out vec4 O, vec2 C) {
	for(float g,e,i;i++<45.;){
   		vec3 p=g*vec3((C*2.-iResolution.xy)/iResolution.y,1);
        p.z+=iTime;
        p=fract(p)-.5;
        G(p.xz,.3);
        G(p.zy,.1);
        G(p.yz,.15);
        g+=e=.6*length(p.xy)-.01;
        e<.01?O+=abs(cos(p.z*99.))/i:O;
    }
}

