mat3 flMat, obMat;
vec4 obRnd;
vec3 trkAx, trkAy, trkFx, trkFy, sunDir, flPos, cId, qHit, obDisp, crMid;
vec2 scrMid;
float bGrid, tCur, dstFar, spd, obOcc, nSpk, obSzFac, rScrn;
int idObj;
const int idCage = 1, idScrn = 2, idVbal = 3, idVspk = 4, idVcap = 5;
const float pi = 3.14159;

#define VAR_ZERO min (iFrame, 0)

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

vec3 TrackPath (float t)
{
  return vec3 (dot (trkAx, cos (trkFx * t)), dot (trkAy, cos (trkFy * t)), t);
}

vec3 TrackVel (float t)
{
  return vec3 (- dot (trkFx * trkAx, sin (trkFx * t)),
     - dot (trkFy * trkAy, sin (trkFy * t)), 1.);
}

vec3 TrackAcc (float t)
{
  return vec3 (- dot (trkFx * trkFx * trkAx, cos (trkFx * t)),
     - dot (trkFy * trkFy * trkAy, cos (trkFy * t)), 0.);
}

void ObjState ()
{
  obRnd = Hashv4v3 (cId + 17.11);
  obDisp = 0.5 * bGrid * (obRnd.xyz - 0.5);
  obMat = VToRMat (normalize (obRnd.xyz - 0.5), (0.2 + 0.8 * obRnd.w) * tCur);
  nSpk = floor (100. + 40. * obRnd.y);
  obSzFac = 0.4 + 0.2 * obRnd.y;
}

float VbalDf (vec3 p, float dMin)
{
  vec3 q, qn, uf;
  float d, rad, len, h;
  dMin /= 0.6 * obSzFac;
  q = p / obSzFac;
  rad = 1.;
  qn = normalize (q);
  uf = SphFib (qn, nSpk);
  d = PrSphDf (q, rad * (1. - 0.05 * smoothstep (0.1, 0.2, length (qn - uf))));
  DMIN (idVbal);
  q = RotToDir (uf, vec3 (0., 0., 1.), q - uf);
  h = Hashfv3 (41. * uf);
  len = 0.3 * (1. - 0.2 * h);
  q.z -= len;
  d = PrCylDf (q, 0.04 * (1. - 0.1 * q.z / len), len);
  DMIN (idVspk);
  q.z -= len + 0.05;
  q.xy = Rot2D (q.xy, pi * h / 3.);
  q.xy = Rot2D (q.xy, 2. * pi * floor (3. * atan (q.y, - q.x) / (2. * pi) + 0.5) / 3.);
  q.x += 0.08;
  d = PrSphDf (q * vec3 (1., 1., 1.3), 0.1);
  DMIN (idVcap);
  return 0.6 * obSzFac * dMin;
}

float ObjCDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  q = p - obDisp;
  d = PrSphDf (q, 1.7 * obSzFac);
  if (d < 0.05) dMin = VbalDf (obMat * q, dMin);
  else dMin = min (dMin, d);
  return dMin;
}

float ObjCRay (vec3 ro, vec3 rd)
{
  vec3 cIdP, p, s, rdi;
  float dHit, d, eps;
  eps = 0.0005;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  rdi = 1. / rd;
  cIdP = vec3 (-999.);
  dHit = eps;
  for (int j = VAR_ZERO; j < 120; j ++) {
    p = ro + dHit * rd;
    cId = floor (p / bGrid);
    if (cId != cIdP) {
      ObjState ();
      cIdP = cId;
    }
    s = (bGrid * (cId + step (0., rd)) - p) * rdi;
    d = abs (Minv3 (s)) + eps;
    if (obRnd.x < obOcc && length (cId.xy - floor (TrackPath (bGrid * cId.z).xy / bGrid)) >= 1.)
       d = min (d, ObjCDf (p - (bGrid * (cId + 0.5))));
    dHit += d;
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjCNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0005, -0.0005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjCDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, len, rad, s, b;
  dMin = dstFar;
  len = 1.;
  rad = 0.8;
  p = flMat * (p - flPos);
  q = p;
  q.yz -= vec2 (-0.1 * rad, 0.3 * len);
  b = length (q.xy);
  s = (q.z > len) ? 16. * (atan (q.z - len, b) / (2. * pi) + 0.5) : 2. * q.z / len;
  s = min (abs (fract (s) - 0.5), abs (fract (12. * atan (q.y, - q.x) / (2. * pi)) - 0.5));
  d = 0.7 * max (max (PrCapsAnDf (q, rad, 0.01 * (1.5 - clamp (20. * s, 0.5, 1.75)), len),
     -1.25 * len - 0.03 - q.z), 0.2 * rad - b);
  DMINQ (idCage);
  q = p;
  q.yz -= vec2 (-0.17, 0.5);
  scrMid = q.xy;
  q.xy = Rot2D (q.xy, pi / 4.);
  q.xy -= 1.02 * rScrn * sign (q.xy);
  d = PrCylDf (q, rScrn, 0.003);
  DMINQ (idScrn);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 80; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0005, -0.0005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 StarPat (vec3 rd, float scl)
{
  vec3 tm, qn, u;
  vec2 q;
  float f;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = Maxv3 (tm) * rd;
  q = atan (vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.)) / pi;
  f = 0.57 * (Fbm2 (11. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 131.13 * scl * q) +
      Fbm2 (13. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 171.13 * scl * q.yx));
  return 5. * vec3 (1., 1., 0.8) * pow (f, 16.);
}

vec3 ErCol (vec3 rd)
{
  vec3 erDir, col, vn;
  float erRad, bs, ts;
  erDir = normalize (vec3 (0.02, -0.04, 1.));
  erRad = 0.04;
  col = vec3 (0.);
  bs = dot (rd, erDir);
  ts = bs * bs - 1. + erRad * erRad;
  if (ts > 0.) {
    ts = bs - sqrt (ts);
    if (ts > 0.) {
      vn = normalize ((ts * rd - erDir) / erRad);
      col = mix (vec3 (0.3, 0.4, 0.8), vec3 (1., 1., 0.95),
         smoothstep (0.2, 0.8, Fbm2 (6. * vn.xy + 7.1))) * (0.5 + 0.5 * max (- dot (vn, rd), 0.)) *
         (0.2 + 0.8 * max (dot (vn, sunDir), 0.));
    }
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  vec2 vf;
  float dstObj, nDotS, t;
  bool isScrn, showBg;
  rScrn = 0.03;
  isScrn = false;
  showBg = true;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == idCage) {
      col = mix (vec3 (0.45, 0.5, 0.5), vec3 (0.5, 0.5, 0.6), smoothstep (0.4, 0.6,
         Fbm3 (64. * qHit)));
      vn = VaryNf (128. * qHit, vn, 0.2);
    } else if (idObj == idScrn) {
      if (length (qHit.xy) < 0.95 * rScrn) {
        qHit.xy = Rot2D (qHit.xy, - pi / 4.);
        if (min (abs (qHit.x), abs (qHit.y)) > 0.03 * rScrn) {
          ro = flPos;
          rd = normalize (vec3 (qHit.xy, - rScrn)).xzy;
          if (abs (scrMid.y) > abs (scrMid.x)) rd.yz = Rot2D (rd.yz, -0.25 * pi * sign (scrMid.y));
          else rd.xy = Rot2D (rd.xy, 0.25 * pi * sign (scrMid.x));
          ro += 0.5 * rd;
          isScrn = true;
        } else col = vec3 (0.7, 0.7, 0.8) * (0.7 + 0.3 * sin (4096. * length (qHit.xy)));
      } else {
        col = vec3 (0.5, 0.5, 0.6) * (0.7 + 0.3 * sin (256. * atan (qHit.y, qHit.x)));
      }
    }
    if (! isScrn) {
      nDotS = max (dot (vn, sunDir), 0.);
      nDotS *= nDotS;
      col = col * (0.3 + 0.3 * max (- dot (vn, sunDir), 0.) + 0.7 * nDotS * nDotS);
      showBg = false;
    }
  }
  if (dstObj >= dstFar || isScrn) {
    dstObj = ObjCRay (ro, rd);
    if (dstObj < dstFar) {
      ro += dstObj * rd;
      cId = floor (ro / bGrid);
      ObjState ();
      ro -= bGrid * (cId + 0.5);
      vn = ObjCNf (ro);
      vf = vec2 (0.);
      if (idObj == idVbal) {
        col4 = vec4 (0.5, 0.4, 0.2, 0.1);
        vf = vec2 (8., 8.);
      } else if (idObj == idVspk) {
        col4 = vec4 (0.5, 0.6, 0.5, 0.4);
        vf = vec2 (64., 0.1);
      } else if (idObj == idVcap) {
        col4 = vec4 (1., 0.2, 0.2, 0.3);
        vf = vec2 (64., 2.);
      }
      if (! isScrn) {
        t = 0.3 * tCur + obRnd.z;
        if (idObj == idVbal) col4.rgb = mix (col4.rgb, vec3 (0.2, 1., 0.2), 
           SmoothBump (0.45, 0.55, 0.005, mod (t, 1.)));
        else if (idObj == idVspk || idObj == idVcap) col4.rgb = mix (col4.rgb, vec3 (1., 1., 0.7),
           SmoothBump (0.49, 0.51, 0.005, mod (0.5 * length (ro - obDisp) - t, 1.)));
        if (vf.x > 0.) vn = VaryNf (vf.x * (obMat * (ro - obDisp)), obMat * vn, vf.y) * obMat;
      }
      col = col4.rgb * (0.2 + 0.2 * max (- dot (vn, sunDir), 0.) + 0.8 * max (dot (vn, sunDir), 0.)) +
          col4.a * pow (max (dot (reflect (rd, vn), sunDir), 0.), 16.);
      if (! isScrn) {
        col = mix (col, ErCol (rd), smoothstep (0.6, 1., dstObj / dstFar));
      } else {
        col = mix (col, vec3 (0.), smoothstep (0.6, 1., dstObj / dstFar));
        col = sqrt (Maxv3 (col)) * vec3 (0.5, 0.5, 1.);
      }
      showBg = false;
    } else {
      col = vec3 (0.);
      if (! isScrn) col += ErCol (rd);
    }
  }
  if (showBg) {
    col = ErCol (rd);
    if (length (col) < 0.03) col += StarPat (rd, 8.);
  }
  return clamp (col, 0., 1.);
}

void FlyerPM (float s, float rl, float vu)
{
  vec3 vel, ort, ca, sa;
  float el, az;
  flPos = TrackPath (s);
  vel = normalize (TrackVel (s));
  el = -0.2 * asin (vel.y);
  az = 0.2 * (atan (vel.z, vel.x) - 0.5 * pi);
  rl = clamp (rl - 0.5 * TrackAcc (s).x, -0.3 * pi, 0.3 * pi);
  ort = (vu >= 0.) ? vec3 (el, az, rl) : vec3 (- el, az + pi, - rl);
  ca = cos (ort);
  sa = sin (ort);
  flMat = mat3 (ca.z, - sa.z, 0., sa.z, ca.z, 0., 0., 0., 1.) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x) *
          mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y);
}

#define AA  0

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  tCur = mod (tCur, 1800.) + 10.;
  az = 0.;
  el = -0.03 * pi;
  if (mPtr.z > 0.) {
    az += 1.5 * pi * mPtr.x;
    el += 1.5 * pi * mPtr.y;
    az = clamp (az, -0.5 * pi, 0.5 * pi);
    el = clamp (el, -0.4 * pi, 0.4 * pi);
  }
  vuMat = StdVuMat (el, az);
  spd = -2.;
  obOcc = 0.4;
  bGrid = 4.;
  trkAx = 0.5 * vec3 (0.7, 0.8, 1.2);
  trkAy = 0.5 * vec3 (1., 1.1, 0.9);
  trkFx = vec3 (0.43, 0.33, 0.23);
  trkFy = vec3 (0.41, 0.31, 0.12);
  FlyerPM (spd * tCur, 0.1 * pi * (Fbm1 (0.01 * tCur) - 0.5), 1.);
  ro = flPos;
  zmFac = 2.6;
  dstFar = 60.;
  sunDir = normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    rd = vuMat * (rd * flMat);
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (pow (col, vec3 (0.9)), 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsAnDf (vec3 p, float r, float w, float h)
{
  p.z = abs (p.z);
  return max (length (p - vec3 (0., 0., min (p.z, h + w))) - r,
     - length (p - vec3 (0., 0., min (p.z, h - w))) + r) - w;
}

vec3 SphFib (vec3 v, float n)
{   // Keinert et al's inverse spherical Fibonacci mapping
  vec4 b;
  vec3 vf, vfMin;
  vec2 ff, c;
  float fk, ddMin, dd, a, z, ni;
  const float phi = 1.618034;
  ni = 1. / n;
  fk = pow (phi, max (2., floor (log (n * pi * sqrt (5.) * dot (v.xy, v.xy)) /
     log (phi + 1.)))) / sqrt (5.);
  ff = vec2 (floor (fk + 0.5), floor (fk * phi + 0.5));
  b = vec4 (ff * ni, pi * (fract ((ff + 1.) * phi) - (phi - 1.)));
  c = floor ((0.5 * mat2 (b.y, - b.x, b.w, - b.z) / (b.y * b.z - b.x * b.w)) *
     vec2 (atan (v.y, v.x), v.z - (1. - ni)));
  ddMin = 4.1;
  for (int j = 0; j < 4; j ++) {
    a = dot (ff, vec2 (j - 2 * (j / 2), j / 2) + c);
    z = 1. - (2. * a + 1.) * ni;
    vf = vec3 (sin (2. * pi * fract (phi * a) + vec2 (0.5 * pi, 0.)) * sqrt (1. - z * z), z);
    dd = dot (vf - v, vf - v);
    if (dd < ddMin) {
      ddMin = dd;
      vfMin = vf;
    }
  }
  return vfMin;
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 VToRMat (vec3 v, float a)
{
  mat3 m;
  vec2 cs;
  float a1, a2;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  m[0][0] = (1. - cs.x) * v.x * v.x + cs.x;
  m[1][1] = (1. - cs.x) * v.y * v.y + cs.x;
  m[2][2] = (1. - cs.x) * v.z * v.z + cs.x;
  a1 = (1. - cs.x) * v.x * v.y;
  a2 = - cs.y * v.z;
  m[0][1] = a1 + a2;
  m[1][0] = a1 - a2;
  a1 = (1. - cs.x) * v.z * v.x;
  a2 = - cs.y * v.y;
  m[2][0] = a1 + a2;
  m[0][2] = a1 - a2;
  a1 = (1. - cs.x) * v.y * v.z;
  a2 = - cs.y * v.x;
  m[1][2] = a1 + a2;
  m[2][1] = a1 - a2;
  return m;
}

vec3 RotToDir (vec3 v1, vec3 v2, vec3 p)
{
  vec3 n;
  float c;
  n = normalize (cross (v1, v2));
  c = dot (v1, v2);
  return c * p + sqrt (1. - c * c) * cross (n, p) + (1. - c) * dot (n, p) * n;
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashfv3 (vec3 p)
{
  return fract (sin (dot (p, vec3 (37., 39., 41.))) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  return fract (sin (dot (p, cHashVA3) + vec4 (0., cHashVA3.xyz)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm3 (vec3 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv3 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

