const float PI = acos(-1.);
#define SS smoothstep

#define usin(x) (0.5+0.5*sin(x))
#define ucos(x) (0.5+0.5*cos(x))

// smooth min function from iq
// https://www.iquilezles.org/www/articles/smin/smin.htm
float smin( float a, float b, float k ) { 
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*k*(1.0/4.0); 
}
float smax( float a, float b, float k ) {
    float h = max( k-abs(a-b), 0.0 )/k;
	return max( a, b ) + h*h*k*(1./4.);
}

float line( in vec2 uv, in float y, in float w ) { return SS(y-w, y, uv.y) - SS(y, y+w, uv.y); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // calc equation value 
    float x = uv.x + iTime*0.1;
    float v1 = usin(fract(x*1.3)*PI*2.);
    float v2 = ucos(fract(x*2.3)*PI*2.);

    // calc smooth min/max
    float k = 0.2*usin(iTime);
    float vsmin = smin(v1, v2, k);
    float vsmax = smax(v1, v2, k);

    vec3 col = vec3(0.);
    // draw equation value
    col += line(uv, v1, 0.01) * vec3(1., 0., 0.);// red line = v1
    col += line(uv, v2, 0.01) * vec3(0., 0., 1.);// blue line = v2
	// draw smooth min/max value
    col = mix(col, vec3(1.,1.,0.), line(uv, vsmin, 0.01));// yellow line = smin
    col = mix(col, vec3(0.,1.,1.), line(uv, vsmax, 0.01));// cyan line = smax

    fragColor = vec4(col,1.0);
}

float rand(vec2 p){
    return fract(sin(p.x*102.33+p.y*3623.42)*27827.);
}

float smoothrand(vec2 p){
    vec2 lv = fract(p);
    vec2 id = floor(p);

    lv = lv*lv*(3.-2.*lv);

    float b = mix(rand(id),rand(id+vec2(1.,0.)),lv.x);
	float t = mix(rand(id+vec2(0.,1.)),rand(id+vec2(1.,1.)),lv.x);

    return mix(b,t,lv.y);
}

float noise(vec2 p,uint depth){
    float c=0.;
    float amp=1.;
    float a=0.;
    for(uint i = 0U;i<=depth;i++){
        c+=smoothrand(p+vec2(float(i)))*amp;

        p*=2.*vec2(( (i%2U == 0U)?(-1.):(1.) ),( (i%2U == 0U)?(1.):(-1.) ));
        a+=amp;
        amp/=2.;
    }
    return c / a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

