vec3 twistY(const in vec3 p, const in float angle) {
    return p * mat3(cos(angle), 0, sin(angle),
              0,          .5,          0,
             -sin(angle), 0, cos(angle));
}

float norm(const in vec3 v, float n) {
    return pow(pow(v.x,n) + pow(v.y,n) + pow(v.z,n), 1.0/n);
}
float dstSphereN(const in vec3 p, const in float r, const in float no) {
    return norm(p, no)-r;
}

float dstBoxN(const in vec3 p, const in vec3 r, const in float no) {
    return norm(max(abs(p), r)-r, no);
}

float dist(vec3 p) {
    float res = 1e38;

    const vec3 repeat_box = vec3(40.0, 40.0, 40.0);

    float rounding = 10.0-pow(abs(cos(iTime)),1.0)*10.0;
    float N = 4.0;
    float case_dist = max(-dstSphereN(twistY(p,iTime*0.1), 6.0, N), dstBoxN(twistY(p,iTime*0.1), vec3(5.0,5.0,5.0), N));

    // twist p
    rounding = 7.0 - pow(sin(iTime*5.0), 8.0)*.2;
    res = min(res, dstBox(twistY(p,p.y*0.5-iTime), vec3(5.0 - rounding,6.0 - rounding,2.0 - rounding)) - rounding);

    res = min(res, case_dist);

    return res;
}

