    return min(pow(cos(p.x),8.0)+pow(cos(p.y),8.0)+pow(cos(p.z),8.0), 3.0)/3.0;
}
float polkadot_color(vec3 p) {
    p *= 3.1415;
    //p += vec3(10.0, 3.0, 12.0) * iTime;
    return min(pow(cos(p.x),2.0)*pow(cos(p.y),2.0)*pow(cos(p.z),4.0), 3.0)/3.0;
}

// vec4 iMouse  image/buffer    xy = current pixel coords (if LMB is down). zw = click pixel

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 scr = (fragCoord - iResolution.xy/2.0)/iResolution.x;

    float camera_angle = iTime * 0.0, camera_dist = 20.0, F = .5;
    vec3 camera_origin = vec3(cos(camera_angle)*camera_dist, camera_dist*0.5, sin(camera_angle)*camera_dist);
    vec3 camera_right = vec3(-sin(camera_angle), 0, cos(camera_angle));
    vec3 camera_up = normalize(cross(camera_right, camera_origin));
    vec3 ray_direction = normalize(normalize(-camera_origin)*F + camera_right * scr.x + camera_up * scr.y);
    vec3 ray_position = camera_origin;

    float d;
    float num_hops = 0.0;
    float closest = 1e38;
    do {
        d = dist(ray_position);
        closest = min(closest, d);
       ray_position += ray_direction * d;
        num_hops += 1.0;
    } while(d > 0.1 && length(ray_position) < 1000.0);

    vec3 p = ray_position;
    float angle = p.y*0.5-iTime;
    p *= mat3(cos(angle), 0, sin(angle),
              0,          .5,          0,
             -sin(angle), 0, cos(angle));
    vec2 N = worley(p/2.0, 1.0, false);
    vec3 col = (pow(abs(N.y-N.x),-0.4)) * normalize(ray_position)/2.0 + vec3(0.5,0.5,0.5);;

    if(length(ray_position) > 999.0) col = vec3(0.2, 0.2, 0.2);

    if(closest != d) col = vec3(pow(1.0-col.x, closest*2.0), col.y, col.z);

    fragColor = vec4(col,1.0);


}

float zoom = float(.5f);

int jPix(){
    vec2 complexLocation = vec2(sin(iTime), cos(iTime));

    int i = 0;
    vec2 uv = gl_FragCoord.xy;

    float zx = (float(uv.x) - float(iResolution.x) / float(2)) / (float(iResolution.x) / float(2)) / zoom;
    float zy = (float(uv.y) - float(iResolution.y) / float(2)) / (float(iResolution.y) / float(2)) / (zoom * (iResolution.x / iResolution.y));

    while (zx * zx + zy * zy < float(4) && i < 256)
    {
        float xTemp = zx * zx - zy * zy;
        zy = float(2) * zx * zy + complexLocation.y;
        zx = xTemp + complexLocation.x;

        i++;
    }

    return i;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{    
    int i = jPix();

    if (i > 255)
        fragColor = vec4(1, 1, 1, 1.0);
    else
    {
        float value = sqrt(float(i) / float(256));
        fragColor = vec4(float(.2) * value, float(.2) * value, value, 1);
    }
}

