uint hash( uint x ) {  //http://amindforeverprogramming.blogspot.com/2013/07/random-floats-in-glsl-330.html

    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}
float random( float f ) {  //http://amindforeverprogramming.blogspot.com/2013/07/random-floats-in-glsl-330.html

    const uint mantissaMask = 0x007FFFFFu;
    const uint one          = 0x3F800000u;

    uint h = hash( floatBitsToUint( f ) );
    h &= mantissaMask;
    h |= one;

    float  r2 = uintBitsToFloat( h );
    return r2 - 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r = 0.0;
    float g = 0.0;
    float b = 0.0;
    vec2 xy0 = vec2(0.,0.);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p=(fragCoord.xy-iResolution.xy)/iResolution.y;

    vec2 xy = vec2(0.,0.);
    //xy = xy + 12.*vec2(8.*sin(12.*xy.x),8.*cos(12.*xy.y));
    float rr = .35*(nsin(.15*iTime))+3.55; // + .5*nsin(iTime*.5); //2.57 +nsin(iTime); //+cos(iTime);
    float spin = sin(p.x+.3*iTime) + cos(p.y+.3*iTime);
    xy = uv + .08*vec2(.8*tan(20.*(p.y+spin)),.8*tan(20.*(p.x+spin)));;
    for (int i = 0; i<8; i++)
    {
        xy.x = xy.x + .01*sin(xy.x*3.+iTime +float(i)*1.57);
        xy.y = xy.y + .01*cos(xy.y*3.+iTime +float(i)*1.57);
        xy = rr*xy*(vec2(1.,1.)- .2*xy -.25*vec2(xy.y,xy.x));
        if(i==0)
        {
          xy0 = xy;  
        }
        if (i>0)
            {
                if(abs(length(xy) - length(xy0)) < .005 +.02*ncos(iTime*.15)) // try and tease out the cycles and color them
                {
                    r = 1.*random(float(mod(float(i),7.0)));
                    g = 1.*random(1.8+float(mod(float(i),7.0)));
                    b = 1.*random((1.30 - float(mod(float(i),7.0))));
                }
            }
        if(i>6)
        {
            r=0.;
            g=0.;
            b=0.;
        }
    }
    if (length(xy) > .25+1.0*sin(iTime*.15))
    {
        r = 1.0 - mod(length(xy),2.);
        g = 0.2*pow(dot(normalize(xy),normalize(xy0)),4.0);
        b = min(1.0,mod(2.,length(xy)));
    }
    if (dot(xy,xy0) > .50 +.05*nsin(iTime*.3))
    {
        r = pow(ncos(iTime*2.5),.5);
        g = 2.0/dot(xy,xy0);
        b = 1.0 - g;
    }
    if (dot(xy,xy0) < .5*nsin(iTime*.3))
    {
        r = 1.0 - pow(ncos(iTime*.3),.5);
        g = 1.0 - 1.05/dot(xy,xy0);
        b = 1.0 - pow(length(xy),4.);
    }        
        //m = (rand_point.x-prev_point.x) / ((rand_point.y-prev_point.y);
        //line_y = m*p.x +  



    vec3 col = vec3(r, g, b);
    float dn = col.x + col.y + col.z;
    col /= dn;
    // Output to screen
    //fragColor = vec4(col,1.0);
    fragColor = vec4( pow(col,vec3(1./2.2)),1.0);
}

const float width = 0.05; // Width ratio of rectangle
const float height = 0.05; // Height ratio of rectangle

vec2 func1(vec2 uv, float time) {
	float hypot = length(uv);
    float uvang = atan(uv.y, uv.x);
    float newang = uvang + time;
	return hypot * vec2(cos(newang), sin(newang));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){

    fragColor = vec4(0.25, 0.25, 0.25, 1.0);

    vec2 R = iResolution.xy;
    vec2 uv = (fragCoord.xy -.5 * R) / R.y;
    uv.x += cos(iTime) * 0.1;

    vec2 rot = func1(uv, iTime);

    rot = abs( rot ) - vec2( width, height );
    if(rot.x <= width && rot.x >= -width && rot.y <= height && rot.y >= -height){
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);
	}
}

