#define PI 3.141
#define MAX_HISTORY 200
#define MAX_SIMUL_ROTATIONS 50


vec2 translate(
	vec2 uv,
    vec2 t
) {
    return uv - t;
}

mat2 rotate2d(float theta) {
    return mat2(cos(theta), -sin(theta),
                sin(theta), cos(theta));
}

float random (vec2 uv, float bigSeed) {
    return fract(sin(
        dot(uv,vec2(12.9898,78.233)))
                 *bigSeed);
}

float rectSdf(
    vec2 uv,
    vec2 halfSize
) {
	vec2 edgeDist = abs(uv) - halfSize;
    float insideDist = min(0., max(edgeDist.x, edgeDist.y));
    float outsideDist = length(max(edgeDist, 0.));
    return insideDist + outsideDist;
}

float circSdf(
	vec2 uv,
	float r
) {
    return length(uv) - r;
}

float annularSdf(
	float d, 
    float w
) {
    return abs(d) - w;
}

float unionSdf(float d1, float d2) {
    return min(d1, d2);
}

float annulus(vec2 uv, float r, float w, float blur) {
    float circ = circSdf(uv, r);
    return smoothstep(0.-blur, 0.+blur, annularSdf(circ, w));
}

float baseTile(
    vec2 uv,
    float circW,
    float blur
) {
    float circTr = annulus(translate(uv, vec2(1.)), 1., circW, blur);
    float circBl = annulus(translate(uv, vec2(-1.)), 1., circW, blur);
    float shape = 1. - unionSdf(circTr, circBl);
    return  shape;
}

float randomGridSquare(
	vec2 scaledUv,
    float iTime,
    float scaleFactor,
    float seed
) {
    float xRandom = scaleFactor * random(vec2(floor(iTime)), seed);
    float yRandom = scaleFactor * random(vec2(xRandom), seed);
    float xIdx = floor(mod(xRandom, scaleFactor));
    float yIdx = floor(mod(yRandom, scaleFactor));
	// Create circular mask for smother rotation
    vec2 circC = translate(vec2(xIdx, yIdx), vec2(-0.5));
    float circMaskSdf = circSdf(translate(scaledUv, circC), 0.500);
    float randomSquare = 1. - step(0., circMaskSdf);
    return randomSquare;
}

float randomGridSquares(
    int nSquares,
	vec2 scaledUv, 
    float iTime,
    float scaleFactor,
    float seed
) {
    float allSquares = 0.;
    for (int i = 0; i < MAX_SIMUL_ROTATIONS; i++) {
        if (i >= nSquares) { break; }
        allSquares += randomGridSquare(scaledUv, iTime, scaleFactor, seed);
        seed *= 2.;
    }
    return min(1., allSquares);
}

vec2 aggregateRotations(
    vec2 fractUv,
    vec2 scaledUv,
    int nSquares,
    float iTime,
    float scaleFactor,
    float seed
) {
    vec2 rotatedUv = fractUv;
    // Keep track of all tile rotations for up to MAX_HISTORY
	for (int historySecond = 0; historySecond < MAX_HISTORY; historySecond++) {
        if (historySecond >= int(floor(mod(iTime, float(MAX_HISTORY))))) { break; }
        float rotateSquare = randomGridSquares(
            nSquares,
            scaledUv, 
            float(historySecond), 
            scaleFactor, 
            seed
        );
        // Rotate 90 degrees 
        float theta = (PI / 2.) * rotateSquare;
        rotatedUv *= rotate2d(theta);
    }
    return rotatedUv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    float scaleFactor = 14.;
    vec2 scaledUv = uv * scaleFactor;
    vec2 tileIdx = floor(scaledUv);
    vec2 fractUv = fract(scaledUv);
    fractUv = 2. * fractUv - 1.;

    float randSeed = 212034.331;

    // Random initial orientations
    float rand = 2.*random(tileIdx, randSeed);
	float isEven = floor(mod(rand, 2.));
	fractUv.x *= 2.*isEven - 1.;

    float iTime = iTime;
    iTime = mod(iTime, float(MAX_HISTORY));
    // Move backwards in time once we reach MAX_HISTORY
    float timeForward = 1. - step(float(MAX_HISTORY / 2), iTime);
    iTime = (iTime * timeForward) + (float(MAX_HISTORY) - iTime) * (1. - timeForward);

    int nSquares = 10;
    vec2 rotatedUv = aggregateRotations(
        fractUv, 
        scaledUv, 
        nSquares, 
        iTime, 
        scaleFactor, 
        randSeed
    );
    //  Current rotation
	float rotateSquare = randomGridSquares(nSquares, scaledUv, iTime, scaleFactor, randSeed);
    float theta = (PI / 2.) * fract(iTime) * rotateSquare;
	rotatedUv *= rotate2d(theta);

    float shape = baseTile(
        vec2(rotatedUv.x, rotatedUv.y),
    	.08,
    	.03
    );

    vec3 color = vec3(0.013,0.185,0.380);
    color = mix(color, vec3(1.000,0.978,0.858), shape);
    fragColor = vec4(color,1.0);
}

