    new.y = (uv.y - wop*sin(iTime))*D/d;

    if(new.x < 0.) new.x -= smt*fract(smt1*iTime);
        else if (new.x > 0.) new.x += smt*fract(smt1*iTime);

    if(new.y < 0.) new.y -= smt*fract(smt1*iTime);
        else if (new.y > 0.) new.y += smt*fract(smt1*iTime);


    for(float i = -30.; i <= 30.; i++)
    {
