                		* smoothstep(floor(new.y)-.03, floor(new.y) + .03, new.y);

        	else if (floor(new) == vec2(j-1., j))
                col += vec3(.38 - 1.2*pow(.13*(abs(i) + abs(j)), 3.), .7 -1.15*pow(.12*(abs(i) + abs(j)), 3.), .85 - 1.1*pow(.13*(abs(i) + abs(j)), 3.))
