float sdfBox(vec3 p, vec3 b)
{
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)), 0.) + length(max(d, 0.));
}
float sdfSphere(vec3 p)
{
    return length(p) - 0.5;
}
float sdfPlane(vec3 p)
{
    return dot(p, vec3(0, 0, 1));
}
vec2 closer(vec2 a, vec2 b)
{
    return a.x < b.x ? a: b;
}
float deg2rad(float r)
{
	return r / 180. * 3.14159;
}
vec2 map(vec3 pos) // find closest object
{
    vec3 sphereWorld = vec3(0.3, 0.3, -0.4);
    vec3 posSphereModel = pos - sphereWorld; // position in sphere model space
    vec2 sphereInfo = vec2(sdfSphere(posSphereModel), 1.);

    vec3 boxWorld = vec3(-0.35, -0.6, 0.3);
    vec3 posBoxModel = inverse(rotateY(deg2rad(35.))) * (pos - boxWorld);
    vec3 boxSize = vec3(0.2, 0.4, 0.2);
    vec2 boxInfo = vec2(sdfBox(posBoxModel, boxSize), 2.);

    vec3 boxWorld2 = vec3(0.35, -0.8, 0.3);
    vec3 posBoxModel2 = inverse(rotateY(deg2rad(60.))) * (pos - boxWorld2);
    vec3 boxSize2 = vec3(0.22, 0.22, 0.22);
    vec2 boxInfo2 = vec2(sdfBox(posBoxModel2, boxSize2), 8.);

    vec3 planeWorld1 = vec3(-1, 0, 0);
    vec3 posPlaneModel1 = inverse(rotateY(3.14 / 2.)) * (pos - planeWorld1);
    vec2 planeInfo1 = vec2(sdfPlane(posPlaneModel1), 3.);

    vec3 planeWorld2 = vec3(1, 0, 0);
    vec3 posPlaneModel2 = inverse(rotateY(3. * 3.14 / 2.)) * (pos - planeWorld2);
    vec2 planeInfo2 = vec2(sdfPlane(posPlaneModel2), 4.);

    vec3 planeWorld3 = vec3(0, 0, 0);
    vec3 posPlaneModel3 = (pos - planeWorld3);
    vec2 planeInfo3 = vec2(sdfPlane(posPlaneModel3), 5.);

    vec3 planeWorld4 = vec3(0, 1, 0);
    vec3 posPlaneModel4 = inverse(rotateX(3. * 3.14 / 2.)) * (pos - planeWorld4);
    vec2 planeInfo4 = vec2(sdfPlane(posPlaneModel4), 6.);

    vec3 planeWorld5 = vec3(0, -1, 0);
    vec3 posPlaneModel5 = inverse(rotateX(3.14 / 2.)) * (pos - planeWorld5);
    vec2 planeInfo5 = vec2(sdfPlane(posPlaneModel5), 7.);

    vec2 closest;
    closest = closer(boxInfo, planeInfo1);
    closest = closer(closest, planeInfo2);
    closest = closer(closest, planeInfo3);
    closest = closer(closest, planeInfo4);
    closest = closer(closest, planeInfo5);
    closest = closer(closest, boxInfo2);
    return closest;
}
vec2 calcIntersection(in vec3 ro, in vec3 rd){
    float currentMinSdf = 0.002;
    float t = 0.0; // distance from ro (ray origin)
	float resDistance = -1.;
    float id = -1.;
    for (int i = 0; i < 100; ++i){
        if (currentMinSdf < 0.001 || t > 10.) break; // hit or exceed max distance
	   	vec2 hitInfo = map(ro + rd * t); // hit object distance and id
        currentMinSdf = hitInfo.x; // distance
        t += currentMinSdf; // update current distance from ro
        id = hitInfo.y;
    }
    if (t <= 10.) resDistance = t; // if in distance
    else id = -1.; // no hit
    return vec2(resDistance, id);
}
vec3 calcNormal(in vec3 pos){

	vec3 dx = vec3(0.001, 0.0, 0.0);    
	vec3 dy = vec3(0.0, 0.001, 0.0);    
	vec3 dz = vec3(0.0, 0.0, 0.001);
	vec3 nor = vec3(
	    map(pos + dx).x - map(pos - dx).x,
	    map(pos + dy).x - map(pos - dy).x,
	    map(pos + dz).x - map(pos - dz).x
    );
	return normalize(nor);
}
vec3 render(vec2 res, in vec3 ro, in vec3 rd)
{
    float ambient = 0.05;
    vec3 color = vec3(0);
    float lightX = 0.5 * sin(iTime * 4.);
    vec3 lightPos = vec3(lightX, 0.9, 0.8);
    if (res.y > 0.) // id
    {
    	vec3 pos = ro + rd * res.x;
    	vec3 norm = calcNormal(pos);
    	vec3 lightDir = normalize(lightPos - pos);
        float match = max(0., dot(lightDir, norm) / pow(length(lightPos - pos), 0.3));
        if (res.y == 1.) color = vec3(1., 0., 0.) * match + ambient;
        else if (res.y == 2.) color = vec3(0.4, 0.4, 0.4) * match + ambient;
		else if (res.y == 3.) color = vec3(1, 0, 0) * match + ambient;
        else if (res.y == 4.) color = vec3(0, 1, 0) * match + ambient;
		else if (res.y == 5.) color = vec3(1, 1, 1) * match + ambient;   
        else if (res.y == 6.) color = vec3(0.5, 0.5, 0.5) * match + ambient;
		else if (res.y == 7.) color = vec3(1, 1, 1) * match + ambient;  
		else if (res.y == 8.) color = vec3(1, 1, 1) * match + ambient;   
    }
    return color;
}
mat3 lookAt(in vec3 eye, in vec3 ref)
{
    vec3 front = normalize(ref - eye);
    vec3 right = normalize(cross(front, vec3(0, 1, 0)));
    vec3 up = normalize(cross(right, front));
    return mat3(right, up, front);
}
