float s = 0.4142135623730951; // silver ratio

void octant1(inout vec2 z, inout float ds, inout vec3[3] colors) {
    if (z.x < 0.0) {
        z.x = -z.x;
        colors[2]=1.0-colors[2];
    }
    if (z.y < 0.0) {
        z.y = -z.y;
        colors[1]=1.0-colors[1];
    }
    float r2 = dot(z,z);
    if (r2 > 1.0) {
        z /= r2; ds /= r2;
        colors[0]=1.0-colors[0];
    }
}

vec3 color(vec2 z, float ds, bool fl) {
    vec3[3] colors;
    colors[0]=vec3(1.0,0.5,0.0);
    colors[1]=vec3(0.0,1.0,0.5);
    colors[2]=vec3(0.5,0.0,1.0);

    if (fl) {
        colors[0]=1.0-colors[0];
        colors[1]=1.0-colors[1];
        colors[2]=1.0-colors[2];
    }

    float r2;
    int n = 60;
    int i;
    for(i=0;i<n;i++) {
        octant1(z, ds, colors);
        z -= vec2(s,s);
        r2 = dot(z,z);
        if (r2 < s * s) {
            z *= s * s / r2; ds *= s * s / r2;
            fl = !fl;
            z += vec2(s,s);
        } else {
            z += vec2(s,s);
        	break;
        }
    }
    octant1(z, ds, colors);
    r2 = dot(z,z);
    float v = (r2 - 2.0 * (z.x + z.y) * s + s * s) / (2.0 * ds * s * s);
    v = min(v,1.0);
    v = 0.75 + 0.25 * float(n-i) / float(n) * v;
    if (fl) v = 1.5 - v;
    float zz = 0.5 * (1.0 - r2);
    if (zz > z.x && zz > z.y) {
        return colors[0] * v * min(1.0, min((zz - z.x) / ds, (zz - z.y) / ds));
    }
    if (z.y > z.x) {
        return colors[1] * v * min(1.0, min((z.y - zz) / ds, (z.y - z.x) / ds));
    }
    return colors[2] * v * min(1.0, min((z.x - zz) / ds, (z.x-z.y) / ds));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = 2.0 * (fragCoord - iResolution.xy * 0.5)/iResolution.y;
    float ds = 4.0 / iResolution.y;

    float period = 4.2549485065150545;
    float t = iTime * 0.5;
    bool r = false;

    while (t > period * 0.5) {
        t -= period;
        float c = 0.766311365; float s = -0.64246936;
        uv = vec2(uv.x * c - uv.y * s, uv.x * s + uv.y * c);
        r = !r;
    }

    uv *= exp(-t); ds *= exp(-t);

    uv += vec2(0.25262046414724887,-1.0187347727326157);
	uv /= dot(uv,uv); ds *= dot(uv,uv);
    uv += vec2(0.22732631827540598,0.4228686518338363);

    fragColor = vec4(color(uv,ds,r),1.0);
    fragColor = pow(fragColor, vec4(1./2.2));
}

// Rotation
#define R(p,a) p=cos(a)*p+sin(a)*vec2(-p.y,p.x);
#define kPI 3.141592

// maximum iteration count
#define kMAXITERS 100
#define kEPSILON 0.001

float dist(in vec3 p){
    // Rotate
    R(p.xz, iTime*.3);
    // Scale Z to flatten
    p.z *= 1.7;
    // Shift on Y to form heart
    //p.y -= smoothstep(0., 1., abs(p.x)) / 2.;
    //p.y -=pow(abs(p.x)/2.,1.7)*1.3;
    p.y -=pow(abs(p.x)/2.,-p.y*.3+1.5)*1.3;
    // Scale X to widen
    p.x *=.8;
 	return length(p) - 1.5;   
}

// Gets the normal
vec3 normal(in vec3 p) {
    vec2 eps = vec2(0.0001, 0);
    float baseDist = dist(p);
 	return normalize(vec3(
        dist(p + eps.xyy) - baseDist,
        dist(p + eps.yxy) - baseDist,
        dist(p + eps.yyx) - baseDist
        ));
}

// Calulcate a fresnel term for reflections
float fresnelTerm(in vec3 d, in vec3 n, in float power) {
	float fresnel = min(1., dot(d, n) + 1.0);
	fresnel = pow(fresnel, power);
    return fresnel;
}

vec4 iC(vec3 p, vec3 d,vec3 ba, vec3 bb){
	vec3 a=(ba-p)/d, // near
	b=(bb-p)/d, // far
	f=max(a,b), // furthest
	n=min(a,b); // nearest
	float x=min(f.x,min(f.y,f.z)), // furthest plane
	dist=max(n.x,max(n.y,n.z)), // nearest plane
	o=dist<0.?x:dist; // nearest in front
	//if(dist>=x||o<0.) return vec4(0,0,0,-100.); // d>=x = invalid, o>t = behind other geometry, o<0 behind

	vec3 normal = normalize(step(kEPSILON,abs(a-dist))-step(kEPSILON,abs(b-dist)))*sign(dist);
	//h.m=m;
    return vec4(normal, dist);
}

vec3 bkg(in vec3 p, in vec3 d, bool heart, vec2 uv) {
    vec4 box = iC(p, d, vec3(-100.), vec3(100.));
    p += d * box.w;
    if (heart) {
    	//float spec = (p.y < -99. && p.x>10. && abs(p.z+30.) < 30.) ? 1. : 0.;
    	float spec = p.y > -99. ? 0. : 
        (1.-smoothstep(40., 50., abs(p.x-50.))) * (1.-smoothstep(30., 40., abs(p.z+30.)));
        //    && p.x>10. && abs(p.z+30.) < 30.) ? 1. : 0.;
        return vec3(1,.2,.2) * (dot(d, normalize(vec3(3,2,-1)))*.3+.7) + spec;
    }

    vec3 col = vec3(d.y *.5 + .5);

    return col;
  //  return abs(box.zxy); 
}

// The main marching loop
vec3 marchRay(in vec3 p, in vec3 d, vec2 uv) {
    float td = 0.;

    for (int i=0; i<kMAXITERS; i++) {
        // Get distance to nearest surface
        float result = dist(p);
        td += result;
        if ((p.z) > 3.0) { break; }

        p += d * result * 0.6;

        if (result < kEPSILON * td) {
            // colision
            // normal
            // Get the normal, then clamp the intersection to the surface
    		vec3 n = normal(p);
            d = reflect(d, n);
            return bkg(p, d, true, uv);
        }
    }
    // Ray did not hit, background
    vec3 col = bkg(p, d, false, uv)*.6 + vec3(.2, .3, 1.);

	col = col*pow(clamp(0., 1., 1.2-length(uv)),.75);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy)*2.-1.;
    vec2 uv2 = uv;
    uv.y *= iResolution.y/iResolution.x;
    vec3 cp = vec3(0,.5,-4.5);
    vec3 rd = normalize(vec3(uv, 1)); 

    // Time varying pixel color
    vec3 col = marchRay(cp, rd, uv2);
	//col = col*pow(clamp(0., 1., 1.-length(uv2)),.75);
    // Output to screen
    fragColor = vec4(col,1.0);
}

