}

vec3 shm(vec2 po, vec2 cent, float ang, float t)
{
    float c = ang;
    float l = length(po-cent);
    float f = c*sin((t)*sqrt(9.8/l)-PI/2.); //from T = 2PI*sqrt(L/g) but here as a function of angle and time
    float x = l*sin(f)+cent.x; //calc x and y based on angle
    float y = -l*cos(f)+cent.y;

    return vec3(x,y,f);
}

