}

#define MAX_MARCH_STEP 120
#define MAX_SHADE_STEP 60
#define CLIP_DIST 1000.0
#define EPSILON 0.001

float maxcomp(vec3 p) { return max(max(p.x, p.y), p.z); }

float intersect(float shape1, float shape2){
    return max(shape1, shape2);
}

float subtract(float base, float subtraction){
    return intersect(base, -subtraction);
}

float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdBox(vec3 p, vec3 b, float r) {
    vec3 d = abs(p) - (b - r);
    return length(max(d, 0.0)) + min(maxcomp(d), 0.0) - r;
}

float sdPlane(vec3 p, vec3 n) {
    return dot(n, p);
}

float eatenSphere(vec3 pos) {
    return intersect(subtract(sdSphere(pos - vec3(0.0, 0.0, 5.0), 1.0),
                    sdSphere(pos - vec3(-0.3, 0.0, 4.38), 0.4)), sdBox(pos - vec3(-1.3, 0.0, 5.0), vec3(1.0), 0.65));
}

float otherBox(vec3 pos) {
	return sdBox(pos - vec3(1.0, 0.0, 6.0), vec3(0.7), 0.2);
}

float sceneSDF(vec3 pos) {
    float temp;
    float dist = CLIP_DIST;

    dist = (temp = sdPlane(pos - vec3(0.0, -2.0, 0.0), vec3(0.0, 1.0, 0.0))) < dist ? temp : dist;
    dist = (temp = eatenSphere(pos)) < dist ? temp : dist;
    dist = (temp = otherBox(pos)) < dist ? temp : dist;

    return dist;
}

vec3 calcNormal(vec3 pos) {
    const vec2 eps = vec2(0.001, 0.0);

    vec3 nor = vec3(
        sceneSDF(pos + eps.xyy) - sceneSDF(pos - eps.xyy),
        sceneSDF(pos + eps.yxy) - sceneSDF(pos - eps.yxy),
        sceneSDF(pos + eps.yyx) - sceneSDF(pos - eps.yyx));
    return normalize(nor);
}

float raymarch(vec3 ro, vec3 rd, float mint, float maxt) {
    float depth = mint;
    for (int i = 0; i < MAX_MARCH_STEP; ++i) {
        vec3 pos = ro + rd*depth;
        float dist = sceneSDF(pos);
        if (dist < EPSILON) return depth;
        if (dist < 0.00015*depth) return depth;

        depth += dist;
        if (depth >= maxt) break;
    }

    return maxt;
}

float softshadow( vec3 ro, vec3 rd, float mint, float maxt, float w ) {
    float s = 1.0;
    float t = mint;
    for(int i = 0; i < MAX_SHADE_STEP; ++i) {
        float h = sceneSDF(ro + rd*t);
        s = min( s, 0.5+0.5*h/(w*t) );
        if( s<0.0 ) break;
        t += h;
    }
    s = max(s,0.0);
    return s*s*(3.0-2.0*s); // smoothstep
}

vec3 sceneColor(vec3 pos) {
    if (eatenSphere(pos) < EPSILON) return vec3(1.0, 1.0, 0.0);
	if (otherBox(pos) < EPSILON) return vec3(0.0, 0.0, 1.0);

    // Fallback
    return vec3(1.0);
}

vec3 shadeScene(vec3 pos) {
    const vec3 lightPos = vec3(-4.0, 3.0, -2.0);

    vec3 col = vec3(1.0);

    vec3 normal = calcNormal(pos);
    vec3 lightVec = pos - lightPos;
    vec3 lightDir = normalize(lightVec);

    float lightFalloff = mix(1.0, 0.0, length(lightVec) / 32.0);
    col *= lightFalloff;
    col *= dot(-lightDir, normal);

    col *= softshadow(pos, -lightDir, 0.01, 10.0, 0.2);

    // Apply per-object coloring
    col *= sceneColor(pos);

    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    vec2 aspect = vec2(1.0, iResolution.y / iResolution.x);

    float FOV = 90.0 + 20.*sin(iTime);
    float RFD = 1.0 / tan(radians(FOV) / 2.0);
    vec3 ro = vec3(0.0);
    vec3 rd = normalize(vec3((uv * 2.0 - 1.0) * aspect, RFD));

    float depth = raymarch(ro, rd, 0.1, CLIP_DIST);
    vec3 col = vec3(0.0);
    if (depth < CLIP_DIST) {
        col = shadeScene(ro + rd*depth);
    }

    col = pow (col, vec3 (1.0 / 2.2)); // Gamma correction
    fragColor = vec4(col, 1.0);
}

