vec2 map( in vec3 pos )
{
    pos.z += .7;
    float time = 2.0 * iTime;
 	float sphere_size = 0.35;
    vec2 res = vec2(11111111.0,0.0);

    float balls = 11111111.;

    for( int i=0; i<4; i++ )
    {
    	//res = opU( res, vec2( sdTorus(pos, vec2(0.90,0.01), float(i) * PI_16TH ), 25.0 ) );  
        //res = opU(res, vec2( sdSphere(    pos, 0.13 , float(i) * PI_16TH, time + PI_16TH * float(i)), time + float(i)));
        balls = min(balls, sdSphere( pos + vec3(float(i - 2) * sphere_size, 0., 0.), sphere_size / 2. , 0., 0.));
    }
    float pendulum = sin(time * 0.6) * .7;
    //balls = opU(balls, vec2( sdSphere( pos + vec3(2. * sphere_size + sin(pendulum), 0., 1. - cos(pendulum)), sphere_size / 2. , 0., 0.), time));
    balls = min(balls, sdSphere( pos + vec3(2. * sphere_size + sin(pendulum) * .9, (-1. + cos(pendulum)) * .5, 0.), sphere_size / 2. , 0., 0.));
    vec3 cp = pos - vec3(0., 0., 0.875);

    float mount = sdBox( cp, vec3(1., 1., .51) );
    mount = max(mount, -sdBox( cp - vec3(0., .1, 0.), vec3(2., 1., .42) ));
    mount = max(mount, -sdBox( cp - vec3(0., -.1, 0.), vec3(.9, 1., 2.) ));

    vec3 pt = cp;

    pt.yz -= vec2(.65, .3);
    pR(pt.yz, .53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.yz -= vec2(.65, -.3);
    pR(pt.yz, -.53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x += sphere_size;
    pt.yz -= vec2(.65, .3);
    pR(pt.yz, .53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x += sphere_size;
    pt.yz -= vec2(.65, -.3);
    pR(pt.yz, -.53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x -= sphere_size;
    pt.yz -= vec2(.65, .3);
    pR(pt.yz, .53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x -= sphere_size;
    pt.yz -= vec2(.65, -.3);
    pR(pt.yz, -.53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x -= sphere_size * 2.;
    pt.yz -= vec2(.65, .3);
    pR(pt.yz, .53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x -= sphere_size * 2.;
    pt.yz -= vec2(.65, -.3);
    pR(pt.yz, -.53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x += sphere_size * 2.;
    pt.x += sin(pendulum) * 0.3;
    pt.yz -= vec2(.65, .3);
    pR(pt.xy, -sin(pendulum));
    pR(pt.yz, .53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .45, .02) ));
    pt = cp;

    pt.x += sphere_size * 2.;
    pt.x += sin(pendulum) * 0.3;
    pt.yz -= vec2(.65, -.3);
    pR(pt.xy, -sin(pendulum));
    pR(pt.yz, -.53);
    mount = min(mount, sdBox( pt - vec3(0., -.1, 0.), vec3(.02, .49, .02) ));
    pt = cp;


    return vec2(min(mount, balls), time);
}

vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 1.0;
    float tmax = 20.0;

	float precis = 0.002;
    float t = tmin;
    float m = -1.0;
    for( int i=0; i<50; i++ )
    {
	    vec2 res = map( ro+rd*t );
        if( res.x<precis || t>tmax ) break;
        t += res.x;
	    m = res.y;
    }

    if( t>tmax ) m=-1.0;
    return vec2( t, m );
}


float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );

}

vec3 calcNormal( in vec3 pos )
{
	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.7, 0.9, 1.0) +rd.y*0.8;
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y;
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );
        vec3 ref = reflect( rd, nor );

        // material        
		col = 0.45 + 0.3*sin( vec3(0.05,0.08,0.10)*(m-1.0) );

        if( m<1.5 )
        {

            float f = mod( floor(5.0*pos.z) + floor(5.0*pos.x), 2.0);
            col = 0.4 + 0.1*f*vec3(1.0);
        }

        // lighitng        
        float occ = calcAO( pos, nor );
		vec3  lig = normalize( vec3(-0.6, 0.7, -0.5) );
		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
        float dom = smoothstep( -0.1, 0.1, ref.y );
        float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );
		float spe = pow(clamp( dot( ref, lig ), 0.0, 1.0 ),16.0);

        dif *= softshadow( pos, lig, 0.02, 2.5 );
        dom *= softshadow( pos, ref, 0.02, 2.5 );

		vec3 lin = vec3(0.0);
        lin += 1.20*dif*vec3(1.00,0.85,0.55);
		lin += 1.20*spe*vec3(1.00,0.85,0.55)*dif;
        lin += 0.20*amb*vec3(0.50,0.70,1.00)*occ;
        lin += 0.30*dom*vec3(0.50,0.70,1.00)*occ;
        lin += 0.30*bac*vec3(0.25,0.25,0.25)*occ;
        lin += 0.40*fre*vec3(1.00,1.00,1.00)*occ;
		col = col*lin;

    	col = mix( col, vec3(0.8,0.9,1.0), 1.0-exp( -0.002*t*t ) );

    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy/iResolution.xy;
    vec2 p = -1.0+2.0*q;
	p.x *= iResolution.x/iResolution.y;
    vec2 mo = iMouse.xy/iResolution.xy;

	float time = 0.0 + iTime/3.0;
    float cra = HALF_PI - clamp(HALF_PI * (cos(time)), 0.0, HALF_PI); // camera rotation angle

    float y = (0.5 + 0.5 * cos(time))*3.0;

	// camera	
	vec3 ro = vec3( 0.0, sqrt(9.0-y*y), y);
	vec3 ta = vec3( 0.0, 0.0, 0.0 );

	// camera-to-world transformation
    mat3 ca = setCamera( ro, ta, 0.0 );

    // ray direction
	vec3 rd = ca * normalize( vec3(p.xy,2.0) );

    // render	
    vec3 col = render( ro, rd );

	col = pow( col, vec3(0.4545) );

    fragColor=vec4( col, 1.0 );
}

#define PI          3.141592654
#define TAU         (2.0*PI)
#define LAYERS      6
#define FBM         3
#define LIGHTNING   3
#define DISTORT     1.4
#define TIME        iTime
#define TTIME       (TAU*TIME)

float wave(float theta, vec2 p) {
  return (cos(dot(p,vec2(cos(theta),sin(theta)))));
}

float noise(vec2 p, float time) {
  float sum = 0.;
  float a = 1.0;
  for(int i = 0; i < LAYERS; ++i)  {
    float theta = float(i)*PI/float(LAYERS);
    sum += wave(theta, p)*a;
    a*=DISTORT;
  }

  return abs(tanh(sum+1.0+0.75*cos(time)));
}

float fbm(vec2 p, float time) {
  float sum = 0.;
  float a = 1.0;
  float f = 1.0;
  for(int i = 0; i < FBM; ++i)  {
    sum += a*noise(p*f, time);
    a *= 2.0/3.0;
    f *= 2.31;
  }

  return 0.45*(sum);
}

vec3 lightning(vec2 pos, float offset) {
  vec3 col = vec3(0.0);
  vec2 f = vec2(0);

  const float w=0.15;

  for (int i = 0; i < LIGHTNING; i++) {
    float time = TIME + 0.5*float(i);   
    float d1 = abs(offset * w / (0.0 + offset - fbm((pos + f) * 3.0, time)));
    float d2 = abs(offset * w / (0.0 + offset - fbm((pos + f) * 2.0, 0.9 * time + 10.0)));
    col += vec3(clamp(d1, 0.0, 1.0) * vec3(0.1, 0.5, 0.8));
    col += vec3(clamp(d2, 0.0, 1.0) * vec3(0.7, 0.5, 0.3));
  }

  return (col);
}

vec3 postProcess(vec3 col, vec2 q) {
  col=pow(clamp(col,0.0,1.0),vec3(0.75)); 
  col=col*0.6+0.4*col*col*(3.0-2.0*col);  // contrast
  col=mix(col, vec3(dot(col, vec3(0.33))), -0.4);  // satuation
  col*=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);  // vigneting
  return col;
}

void rot(inout vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(p.x*c + p.y*s, -p.x*s + p.y*c);
}

vec3 normal(vec2 p, float time) {
  vec2 v;
  vec2 w;
  vec2 e = vec2(0.00001, 0);

  vec3 n;
  n.x = fbm(p + e.xy, time) - fbm(p - e.xy, time);
  n.y = 2.0*e.x;
  n.z = fbm(p + e.yx, time) - fbm(p - e.yx, time);

  return normalize(n);
}

vec3 dragonEye(vec2 p) {
  vec2 pp = 10.0*p;

  rot(p, -0.75); 
  p *= vec2(1.1/tanh(1.0 + length(p)), 1.0);
  float l = length(p);

  float dd = 0.2 + 0.65*(-0.5 + 1.75*(0.5 + 0.5*cos(3.0*l-TTIME/12.0)))*tanh(1.0/((pow(l, 4.0) + 2.0)));
  dd *= smoothstep(9.0, 12.0, TIME-l*2.0);
  vec3 col = vec3(0.0);
  float f = fbm(pp, TIME*0.1);
  vec3 ld = normalize(vec3(p.x, 0.5, p.y));
  vec3 n = normal(pp, TIME*0.1);
  float diff = max(dot(ld, n), 0.0);
  col += vec3(0.5, 1.0, 0.8)*pow(diff, 20.0)/(0.5+dot(p, p));
  col += lightning(pp, dd);
  col *= pow(vec3(f), vec3(1.5, 5.0, 5.0));
//  col += -0.1+0.3*vec3(0.7, 0.2, 0.4)*vec3(tanh((pow(0.6/f, 10))));
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord.xy/iResolution.xy;    
  vec2 p = -1.0 + 2.0*q;

  p.x *= iResolution.x/iResolution.y;    

  vec3 col = dragonEye(p);

  col = postProcess(col, q);

  col *= smoothstep(0.0, 4.0, TIME);

  fragColor = vec4(col, 1.0);  
}

float circ(
	vec2 uv,
    float r1,
    float r2,
    float blend
) {
    return (smoothstep(r1-blend, r1+blend, length(uv))
           - smoothstep(r2-blend, r2+blend, length(uv)));
}

float square(
	vec2 uv,
    float r,
    float blend
) {
    vec2 dist = abs(uv);
    float d = max(dist.x, dist.y);
    return 1. - smoothstep(r-blend, r+blend, d);
}

mat2 rotate2d(float theta) {
    return mat2(cos(theta), -sin(theta),
                sin(theta), cos(theta));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    float scale = 10.2;
    uv *= scale;
    vec2 tileIdx = floor(uv);
    vec2 tileUv = fract(uv);
    tileUv = tileUv*2. - 1.;

    // Background
    vec3 color = vec3(0.147,0.208,0.320);
    float cBlur = circ(tileUv, 0.2, 3.192, 2.236);
    vec3 cBlurColor = vec3(0.228,0.770,0.825);
    color = mix(color, cBlurColor, cBlur);

    // Gradient for shape colors using non-tile coordinates
    vec2 gradientUv = uv - vec2(iTime*3.);
    float colorGrad = sin(gradientUv.x + gradientUv.y) * 0.2;

    // Shapes
    vec2 shapeCoords = rotate2d(3.14/4.)*tileUv;
    // Square
    float s = square(shapeCoords, .45, 0.01);
    vec3 squareColor = vec3(0.427, 0.166 + colorGrad, .5 - colorGrad);
    // Circle
    float c = circ(tileUv, 0.504, 0.668, 0.01);
    vec3 circColor = vec3(0.845,0.756 - colorGrad,.3 + colorGrad);
	// Alternate shapes by row/column
    float circPercent = mod(tileIdx.x + tileIdx.y, 2.);
    float squarePercent = 1. - mod(tileIdx.x + tileIdx.y, 2.);  
    float shapeMask = c*circPercent + s*squarePercent;
    vec3 shapeColor = mix(circColor, squareColor, squarePercent);

    color = mix(color, shapeColor, shapeMask);

    fragColor = vec4(color,1.0);
}

float focalPlane; // along the z-axis

#define TAU 6.28318530718
#define PI TAU/2.

float hash13(in vec3 p) {
    /*
	p  = fract(p * .103177);
    p += dot(p, p.yzx + 1.91979);
    return fract((p.x + p.y) * p.z);
	*/
    // This hash is slightly cheaper.
    return fract(sin(dot(p, vec3(12.9898,78.233,7.279237))) * 43758.5453);
}

// How many cells around the central_cell we check for ellipses.
#define SAMPLE_RADIUS 2
const float num_layers = 50.;
const float depth_of_field = 30.; // the distance between in-focus and having a blurRadius of 1.

/*
// Debugging things.
const vec3 default_debug = vec3(-100, -20, -10);
const vec3 orange = vec3(1., .5, 0.);
const vec3 cornflowerblue = vec3(100./255., 149./255., 237./255.);
vec3 debug = default_debug;
*/

float monochrome(vec2 uv) {

    const float cell_wd = 6.0;
    vec2 central_cell_center = floor((uv+.5*cell_wd) / cell_wd) * cell_wd;

	float hashTime = iTime + 100.;  // Don't want thing to be synchonous when the shader starts    

    float acc = 0.;
    // Painters algorithm.
    for (float z = 1.0; z>=-0.001; z -= 1./num_layers) {
        vec2 shifted_central_cell_center = central_cell_center + cell_wd * vec2(
            hash13(vec3(10.2, 1.13, z)),
            hash13(vec3(20.3, 2.34, z))
        );

        int j;
        for (int i = -SAMPLE_RADIUS; i<=SAMPLE_RADIUS; ++i) {
            for (j = -SAMPLE_RADIUS; j<=SAMPLE_RADIUS; ++j) {
                vec2 cell_center = shifted_central_cell_center + cell_wd*vec2(ivec2(i,j));
              	//cell_center = round(cell_center);
                //cell_center = round(cell_center);

                float hash_z = 10. * z;
                vec2 cell_hash_pos = cell_center + vec2(1030., 0);
                float blurRadius = depth_of_field*abs((z+.5*hash13(vec3(cell_hash_pos, ++hash_z))) - focalPlane);

                float yaw = .1*hashTime + TAU * hash13(vec3(cell_hash_pos, ++hash_z));
                float angular_speed = 3. + hash13(vec3(cell_hash_pos, ++hash_z));
                vec2 angular_vel = angular_speed * vec2(cos(yaw), sin(yaw));
                float pitch = length(angular_vel) * hashTime * 0.6;
                vec2 a = normalize(angular_vel);  // axis of rotation - major axis of projected ellipse.
                //vec2 b = vec2(-a.y, a.x) * cos(pitch);  // minor axis
                vec2 ab = vec2(1., abs(cos(pitch)));
                vec2 extended_ab = ab + vec2(blurRadius);

                mat2 rot = mat2(a.x, -a.y,
                                a.y, a.x);

                vec3 normal = vec3(0.0, sin(pitch), cos(pitch));
                mat3 rot3 = mat3(a.x, -a.y, 0.,
                                 a.y, a.x, 0.,
                                 0., 0., 1.);
                normal = rot3 * normal;
                const vec3 LIGHT_DIR = normalize(vec3(1.5, -1.0, 2.0));
                float light_per_area = 
                    .2 + // ambient
                    100.0 * smoothstep(.98, .99, abs(dot(normal, LIGHT_DIR)));  // cheap (wrong-ish) specular
                float total_light = light_per_area * ab.x * ab.y;  // total amount of light emitted by this object towards all pixels.
                // Have a random offset from the cell_center.
                vec2 ellipse_center = cell_center + (.5*cell_wd - ab.x) * vec2(
                    sin(hashTime * (.01 + .06*hash13(vec3(cell_hash_pos, ++hash_z)))),
                    cos(hashTime * (.01 + .06*hash13(vec3(cell_hash_pos, ++hash_z))))
                );
                vec2 ellipse_uv = uv - ellipse_center;   

                //float sd = sdEllipse(transpose(rot)*inner_uv, extended_ab);
                //float sd = sdEllipsoidApproximated(transpose(rot)*inner_uv, extended_ab);
                float sd = length(rot*ellipse_uv / extended_ab) - 1.;  // Signed distance approximation only accurate for values near zero.
                float proportional_area = extended_ab.x * extended_ab.y;
                float edge_blurryness = 0.1/length(extended_ab);
                float is_in_ellipse = smoothstep(edge_blurryness, -edge_blurryness, sd);


                float opacity = is_in_ellipse * ab.x / extended_ab.x;
                //acc = mix(acc, total_light / proportional_area, opacity);
                //if (opacity > .9) debug=orange;
                acc *= 1.-(opacity*opacity*opacity*opacity*opacity);  // haax to allow dark in-focus objects in front of blurry backgrounds.
                acc += opacity * total_light / proportional_area;
            }
        }
    }
	return acc;
}

vec2 project(vec2 pixel) {
    vec2 uv = pixel/iResolution.xy - vec2(0.5);
    uv.x *= iResolution.x / iResolution.y;
    return uv *50.;
}


