vec2 map(in vec2 uv, in float t) {
    vec2 z = uv;    

    for (float i = 0.; i < t; i += 1.) {
        if(length(z) > 100.) {
            return z;   
        }
        vec2 newZ = square(z) + uv;
        z = (
            smoothstep(0., 1., t - i) * newZ
            + (1. - smoothstep(0., 1., t - i)) * z
        );
    }
    return z;
}


vec3 gradient(in vec2 z) {	
