    r /= 3.;
    r = pow(r, 0.6);
    vec3 rainbow = 0.5 + 0.5 * cos((5.5 * r + vec3(0.2, 0.45, 0.8)*6.));
    rainbow *= (1. - smoothstep(1., 1.03, r));
    rainbow *= (smoothstep(0.0, 0.03, abs(r-0.4)));
    return rainbow;
}


