}

float sic(vec3 ro, vec3 rd, vec3 so, float r)
{
	const int nos = numberOfIteration;
    float d = 0.0;
    vec3 cd = so - ro;
	float stepSize = (sqrt((pow(length(so - ro), 2.0)) + (r * r)) / float(nos));
    for(int i = 0; i < nos; i++)
    {
		d = d + stepSize;
        if(pointInsideEllipsoid(ro + rd * d, so) == true)
        {
