#define M_PI 3.1415926535897932384626433832795

// Official HSV to RGB conversion 
// source: https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	return c.z * mix( vec3(1.0), rgb, c.y);
}

//https://gist.github.com/ayamflow/c06bc0c8a64f985dd431bd0ac5b557cd
vec2 rotateUV(vec2 uv, float rotation)
{
    float mid = 0.0;
    return vec2(
        cos(rotation) * (uv.x - mid) + sin(rotation) * (uv.y - mid) + mid,
        cos(rotation) * (uv.y - mid) - sin(rotation) * (uv.x - mid) + mid
    );
}

float DrawCircle(vec2 uv, float pixelSize, float time, float timeOffset, float paramA, float paramB)
{
    time+=timeOffset;
    vec2 center = vec2(cos(time + timeOffset) + 0.5, sin(time + timeOffset)) - 0.5;
    float dist = distance(uv, center) * paramA;
   	float circle = 1.0 - (step(paramB, dist));
    float circleLight = 1.0 - clamp(dist, 0.0, 1.0);
    circle+=circleLight;

    circle = clamp(circle, 0.0, 1.0);
    return circle;
}


vec4 DrawMoon(vec2 uv, float pixelSize, float worldTime)
{
    float circle = DrawCircle(uv, pixelSize, worldTime, M_PI * 0.5, 4.0, 0.4);
    return vec4(circle);
}

vec4 DrawRainbow(vec2 uv, float pixelSize, float worldTime)
{
    pixelSize *= 10.0;

    uv *= 0.8;
    uv.y += 0.8;

    worldTime *= 2.0;
    float vertAlpha = step(0.4, uv.y);
    uv = rotateUV(uv, 3.0 -worldTime);



    float dist = distance(uv, vec2(0.0));
    vec3 rgb = hsv2rgb(vec3( pow(dist, 5.0), 1.0, 1.0));
    float alpha = 1.0 
                - smoothstep(dist - pixelSize, dist + pixelSize, 0.9) 
                - smoothstep(1.1 - pixelSize, 1.1 + pixelSize, dist);


    float angle = (atan(uv.y, uv.x))/(M_PI*2.0) + .5;
    angle = 0.0 + smoothstep(0.5 - pixelSize, 0.5 + pixelSize, angle)
                + (1.0 - smoothstep(0.1 - pixelSize, 0.1 + pixelSize, angle));

    return vec4(rgb, alpha * angle * 0.6 * vertAlpha);
}

