#define res iResolution.xy
#define t (1.2*iTime)
#define PI 3.14159265358979

#define cp(x) clamp(x,0.,1.)
#define ss(x) (x)*(x)*(x)*((6.*(x)-15.)*(x)+10.)

float smin(float a, float b, float k){
    float d = a-b;
    return abs(d)>k?min(a,b):.5*(-0.5/k*d*d+a+b-.5*k);
}


// Virus Modeling

#define R 0.74
#define r 0.25
#define n 8.
#define df 0.058
#define ff 14.4
#define rc 1.06
float u(vec2 p){ return length(vec2(rc*(p.x+df*cos(ff*p.y)-(R+r)),p.y))-r; }

#define sm 0.156
float V(vec2 p, float td, inout vec3 col){
    float a = mod(atan(p.y,p.x)-td, 2.*PI/n)-PI/n;
    vec2 q = length(p)*vec2(cos(a),sin(a));
    float d = smin(u(q),length(p)-R,sm);
    if (d<0.) col-=sqrt(dot(p,p)+.1)*vec3(0,1,1);
    return d;
}


// Virus Crowd

#define P1 vec2(-0.82,-0.35)
#define v1 -0.5
#define s1 0.85
#define m1 0.095
#define f1 3.
#define P2 vec2(0.81,0.54)
#define to 2.56
#define v2 0.625
#define s2 0.58
#define m2 0.044
#define f2 4.

float Vs(vec2 p, inout vec3 col){
    vec3 c1; float d1 = V((p-P1)/s1+vec2(0,m1*sin(f1*t)),v1*t,c1);
    vec3 c2; float d2 = V((p-P2)/s2+vec2(0,m2*sin(f2*(t+to))),v2*(t+to),c2);
    col += c1 + c2;
    return min(d1,d2);
}


// Liquid

#define wf 6.77
#define wm 0.14
#define wv 5.
#define whf 0.15
float whfs(float x) { return x<0.5?0.:x<1.5?ss(x-0.5):x<2.?1.:1.-ss(x-2.); }

float W(float x, float y, inout vec3 col){
    float wh = 4.*whfs(mod(whf*t,3.))-2.5;
    float d = y-(wm*exp(sin(wf*x+wv*t))+wh);
    if (d<0.) col-=(1.-exp(y-wh-3.*wm))*vec3(1,.8,0);
    return d;
}


// Main

float scene(vec2 p, inout vec3 col){
    float clip = max(abs(p.x)-3.,abs(p.y)-2.);
    float sd = smin(Vs(p,col),W(p.x,p.y,col),0.1);
    if (clip>0.) col=vec3(1.);
    return max(clip,sd);
}

float house(vec2 p){
    vec2 b = abs(p);
    float u = max(b.x-3.,b.y-2.);
    float v = min(p.y+2.,min(3.-b.x,(7.-2.*p.y-b.x)/sqrt(5.)));
    return min(abs(u),abs(v));
}

void mainImage(out vec4 col, in vec2 coord) {
    col.xyz = vec3(1.0);
    float S = min(res.x,res.y);
    vec2 p = 7.0*(coord-0.5*res)/S-vec2(0,-.5);
    float ds = scene(p,col.xyz);
    float d = abs(ds)/length(vec2(dFdx(ds),dFdy(ds))) - .001*S;
    col.xyz *= cp(d)*cp(house(p)*.14*S-.001*S);
}

vec4 DrawSun(vec2 uv, float speed)
{
    float dist = distance(uv, vec2(0.0));
    float circle = step(dist, 0.5);   

    float lines =sin(uv.y * 50.0 + iTime * speed) * ((1.0 - uv.y) * 0.5);
    lines *= 1.0;
    lines = step(lines, 0.5);

    circle *= lines;

    vec3 yellow = vec3(0.921, 0.843, 0);
    vec3 pink = vec3(0.941, 0, 0.737);

    float gradientMask = ((1.0 - uv.y) * 0.5);
    gradientMask += 0.1;
    gradientMask *= gradientMask;
   	vec3 gradient = mix(yellow, pink, gradientMask);

    vec3 sun = circle * gradient;

    dist = 1.0 - dist;
    dist = clamp(dist, 0.0, 0.5);

    dist *= (sin(iTime * 2.0) + 10.0) * 0.11;


    gradient *= dist;
    sun = mix(sun, gradient, dist);
    return vec4(sun, 1.0);
}


vec4 DrawGrid(vec2 uv)
{   
    float isVisible = step(uv.y, -0.1);
    float gradientMask = ((0.85 - uv.y) * 1.0);

    uv.y = 2.0 / uv.y;
    uv.x *= uv.y;

    vec2 grid = fract(uv - vec2(0.0, iTime * 5.0)) * 0.05 * (1.0 - uv.y) / fwidth(uv);
    float line = min(grid.x, grid.y);
    line = clamp(line, 0.0, 1.0);
    line = 1.0 - line;

    vec3 blue = vec3(0, 0.047, 0.239);
    vec3 pink = vec3(0.941, 0, 0.737);

    gradientMask *= gradientMask;
    gradientMask = clamp(gradientMask, 0.0, 1.0);
    gradientMask -= isVisible;
   	vec3 gradient = gradientMask * pink;

    vec3 bg = (isVisible - (isVisible * line * 1.8)) * blue;

    return vec4((clamp(isVisible * line, 0.0, 1.0) * pink) + gradient + bg, gradientMask + isVisible);
    //return vec4(vec3(bg), 1.0);
}

