#define MAX_STEPS 64
#define MAX_DIST 20.
#define EPS 0.001
const float PI = acos(-1.0);

mat3 rotX(float angle)
{
    return mat3(1,	0,			0,
               0,	cos(angle), -sin(angle),
               0,	sin(angle), cos(angle));
}

mat3 rotY(float angle)
{
	return mat3(cos(angle), 0, sin(angle),
               0,			1, 0,
               -sin(angle), 0, cos(angle));
}

float sdCube(vec2 pos, float r)
{
	vec2 q = abs(pos) - r;
	return length(max(q,0.0)) + min(max(q.x,q.y),0.0);
}

float sdCube(vec3 pos, float r)
{
	vec3 q = abs(pos) - r;
	return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float map(vec3 pos)
{
    pos = fract(pos)-0.5;

    float d1 = sdCube(pos, 0.1);
    float d2 = sdCube(vec2(pos.x, pos.z), 0.02);
    float d3 = sdCube(vec2(pos.x, pos.y), 0.02);
    float d4 = sdCube(vec2(pos.z, pos.y), 0.02);

    float res = MAX_DIST;
    res = min(res, d1);
    res = min(res, d2);
    res = min(res, d3);
    res = min(res, d4);
    return res;
}

vec3 normals(vec3 pos)
{
    vec2 e = vec2(0, 0.0001);
    return normalize(vec3(
    	map(pos+e.yxx)-map(pos-e.yxx),
        map(pos+e.xyx)-map(pos-e.xyx),
        map(pos+e.xxy)-map(pos-e.xxy)
    ));
}

const vec4 miss = vec4(MAX_DIST, vec3(0));
vec4 raymarch(vec3 ro, vec3 rd)
{
    float t = 0.0;
    for(int i = 0; i<MAX_STEPS; i++)
    {
        vec3 pos = ro + rd*t;
        float d = map(pos);

        if(abs(d)<=EPS) return vec4(length(pos-ro), normals(pos));
        if(d >= MAX_DIST) return miss;

        t+=d;
    }


    return miss;
}

const vec3 BACK_GROUND = vec3(0.2);
vec3 GetScene(vec3 ro, vec3 rd, vec3 lightDir)
{

    vec4 a = raymarch(ro, rd);
    float d = a.x;
    vec3 nor = a.yzw;

    vec3 col = BACK_GROUND;

    if(d < MAX_DIST)
    {
        float li = dot(lightDir, nor);

        vec3 pos = ro+rd*d;
        pos *= rotX(0.9);

        float lines = sin(pos.y * 600.) * 0.5+0.5;
        lines = smoothstep(li+0.25, li-0.25, lines);
        lines = lines*0.7+0.3;

        col = vec3(1, 0.94, 0.8) * lines;
        col = mix(col, BACK_GROUND, smoothstep(1., 8., d)); // fog
    }

    return col;

}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

