#define MAX_STEPS 64
#define MAX_DIST 100.
#define SURF_DIST .01
#define MAXITE 32.0

mat3 rotx(float a) { mat3 rot; rot[0] = vec3(1.0, 0.0, 0.0); rot[1] = vec3(0.0, cos(a), -sin(a)); rot[2] = vec3(0.0, sin(a), cos(a)); return rot; }
mat3 roty(float a) { mat3 rot; rot[0] = vec3(cos(a), 0.0, sin(a)); rot[1] = vec3(0.0, 1.0, 0.0); rot[2] = vec3(-sin(a), 0.0, cos(a)); return rot; }

float rnd(vec2 n){
  return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float formula(vec2 p) {
    vec3 col;
    float t = iTime;
    for(float j = 0.0; j < 3.0; j++){
        for(float i = 1.0; i < 15.0; i++){
            p.x += 0.1 / (i + j) * sin(i * 10.0 * p.y + t + cos((iTime / (12. * i)) * i + j));
            p.y += 0.1 / (i + j)* cos(i * 10.0 * p.x + t + sin((iTime / (12. * i)) * i + j));
        }
        col[int(j)] = abs(p.x + p.y);
    }
    return (col.x+col.y+col.z)/3.;
}

// MandelBrot not used
float mandel(vec2 p)
{    
    float zre=sin(iTime)/4.0;
    float zim=cos(iTime)/3.0;
    float cre=p.x;
    float cim=p.y;
    float col=0.;

    for(float i=0.;i<MAXITE;i++)
    {
        float dam=zre*zre-zim*zim+cre;
        zim=2.0*zre*zim+cim;
        zre=dam;
        col++;
        if( (zre*zre+zim*zim)>4.0 )
            break;
    }

    return (col/MAXITE);
}


float GetDist(vec3 p) {
    //float v=0.1*mandel(p.xz/6.0);
    float v=0.;
    if(p.x>-6. && p.x<6. && p.z>-6. && p.z<6.)
    	v=0.5*formula(p.xz/12.0);
	vec4 s1 = vec4(p.x,v,p.z,0.8);

    //float sphereDist1 =  length(p-s1.xyz)-s1.w;
	float cube = length(max(abs(p-s1.xyz)-vec3(0.01,0.01,0.01), 0.));
    float planeDist = p.y;

    float d = min(cube, planeDist); //sphereDist1, planeDist);
    return d;    
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;

        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p) {
    float dif=0.;
    if(p.x>-6. && p.x<6. && p.z>-6. && p.z<6.)
    {

        vec3 lightPos = vec3(0, 6, 2);
        vec3 l = normalize(lightPos-p);
        vec3 n = GetNormal(p);

        dif = clamp(dot(n, l), 0., 1.);
        float d = RayMarch(p+n*SURF_DIST*2., l);
        if(d<length(lightPos-p)) dif *= .1;
    }

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);

    vec3 ro = vec3(-2., 6, -7.5);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    ro = ro*(rotx(-0.4));
    ro.x=0.0+4.0*cos(iTime/2.0);
    ro.z=-8.0+2.0*sin(iTime/1.5);
    float d = RayMarch(ro, rd);

    vec3 p = ro + rd * d;

    float dif = GetLight(p);
    col = vec3(dif)*vec3(0.0,0.6,0.9);

    fragColor = vec4(col,1.0);
}

