    r.xz *= mat2(-cos(the), sin(the), -sin(the), -cos(the)); //Camera Rotation

    vec3 o = vec3(0, iTime, -iTime);
    float t = trace(o, r);



    float fog = 2.0 / (2.0 + t*t*0.10);
