//iq sdf
float sdCappedCylinder( vec3 p, float r, float h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

//own sdf from intuition (fastest to slowest(?))
float sdCappedCylinder2(vec3 p, float h, float r){

    vec2 v = vec2(length(p.xz)-r,abs(p.y)-h);
    return v.y<0.?v.x:v.x<0.?v.y:length(v);



    float a = length(p.xz)-r;
    float b = abs(p.y)-h;
    return b<0.?a:a<0.?b:sqrt(a*a+b*b);

    if(b<0.){
    	return a;
    }
    else if(a<0.){
    	return b;
    }
    else{
    	return sqrt(a*a+b*b);
    }
}



vec2 rotate(vec2 p, float t){
	float a = cos(t);
    float b = -sin(t);
    return mat2(a,-b,b,a)*p;
}

