float map(vec3 p){

    return sdCappedCylinder2(p, 0.5,0.2);
}

vec3 calcNormal(vec3 p){
	vec2 e = vec2(0.001,0.);
    return normalize(vec3(map(p+e.xyy)-map(p-e.xyy),
                          map(p+e.yxy)-map(p-e.yxy),
                          map(p+e.yyx)-map(p-e.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 ro = normalize(vec3(sin(iTime),sin(iTime)*5.,cos(iTime)));
    vec3 uu = -ro;
    vec3 vv = cross(uu,vec3(0.,1.,0.));
    vec3 ww = cross(vv,uu);
    float t = 0.;
    vec3 col = vec3(.1,.1,.1);
    vec3 p = ro;

    for(int n = 0; n<AA; n++){
        for(int m = 0; m<AA; m++){
            vec2 o = vec2(m,n)/float(AA)-.5;
            vec2 uv = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
			vec3 rd = normalize(uu+vv*uv.x + ww*uv.y);          
			vec3 n = vec3(0.,0.,0.);
        	for(int i = 0;i<1000;i++){

                p = ro+rd*t;
                float d = map(p);
                if(d<0.01){
                    p = p+d*rd;
                    n = calcNormal(p);
                    col += n*.5+.5;
                    break;
                }
                if(d>1000.)
                	break;
                t+=d;
            }
            for(int j = 1; j<5; j++){
                float d = 0.01 + 0.12*float(j)/4.0;
            	vec3 sp = p+n*d;
                float d2 = map(sp);
                float occ =d-d2;
                col-=abs(occ);
            }
        }
    }
    col/=vec3(AA*AA);


    fragColor = vec4(col,0.);
}

