// The Live Coders Conference - Online on twitch.tv April 9, 2020 - https://captcalli.github.io/LiveCodersConf/
// Created a few shaders inspired by the online event, these shaders are not officially adopted just made for fun and to promote the event.

// 2D shape created by combining 2D primitives from IQ's blog:
//  http://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
// pmin from IQ's blog on smooth minimum functions:
//  http://www.iquilezles.org/www/articles/smin/smin.htm
// warped fbms from IQ's blog:
//  https://iquilezles.org/www/articles/warp/warp.htm

#define PI     3.141592654
#define TAU    (2.0*PI)
#define SCA(a) vec2(sin(a), cos(a))
#define TIME   (iTime+120.0)
#define TTIME  (TIME*TAU)
#define PERIOD 600.0

const float a1 = PI/2.0;
const float a2 = PI*4.5/6.0;

const vec2 sca1 = SCA(a1);
const vec2 sca2 = SCA(a2);

const mat2 frot = mat2(0.80, 0.60, -0.60, 0.80);

void rot(inout vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(c*p.x + s*p.y, -s*p.x + c*p.y);
}

float noise(vec2 p) {
  float a = sin(p.x);
  float b = sin(p.y);
  float c = 0.5 + 0.5*cos(p.x + p.y);
  float d = mix(a, b, c);
  return d;
}

float fbm(vec2 p) {    
  float f = 0.0;
  float a = 1.0;
  float s = 0.0;
  float m = 2.0-0.1;
  for (int x = 0; x < 4; ++x) {
    f += a*noise(p); p = frot*p*m;
    m += 0.01;
    s += a;
    a *= 0.45;
  }
  return f/s;
}

float warp(vec2 p, float offset, out vec2 v, out vec2 w) {
  vec2 vx = vec2(0.0, 0.0);
  vec2 vy = vec2(3.2, 1.3);

  vec2 wx = vec2(1.7, 9.2);
  vec2 wy = vec2(8.3, 2.8);

  vec2 off = (1.75 + 0.5*cos(TTIME/60.0))*vec2(-5, 5);

  p += mix(vec2(0.0), off, 0.5 + 0.5*tanh(offset));

  rot(vx, TTIME/1000.0);
  rot(vy, TTIME/900.0);

  rot(wx, TTIME/800.0);
  rot(wy, TTIME/700.0);

  vec2 vv = vec2(fbm(p + vx), fbm(p + vy));  
  vec2 ww = vec2(fbm(p + 3.0*vv + wx), fbm(p + 3.0*vv + wy));

  float f = fbm(p + 2.25*ww);


  v = vv;
  w = ww;

//  return tanh(f);
  return f;
}

float mod1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize, size) - halfsize;
  return c;
}

vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

vec2 toRect(vec2 p) {
  return vec2(p.x*cos(p.y), p.x*sin(p.y));
}

float pmin(float a, float b, float k) {
  float h = max(k-abs(a-b), 0.0)/k;
  return min(a, b) - h*h*k*(1.0/4.0);
}

float circle(vec2 p, float r) {
  return length(p) - r;
}

float box(vec2 p, vec2 b, vec4 r) {
  r.xy = (p.x>0.0)?r.xy : r.zw;
  r.x  = (p.y>0.0)?r.x  : r.y;
  vec2 q = abs(p)-b+r.x;
  return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

float arc(vec2 p, vec2 sca, vec2 scb, float ra, float rb) {
  p *= mat2(sca.x,sca.y,-sca.y,sca.x);
  p.x = abs(p.x);
  float k = (scb.y*p.x>scb.x*p.y) ? dot(p.xy,scb) : length(p.xy);
  return sqrt(dot(p,p) + ra*ra - (2.0 - 0.000001)*ra*k) - rb;
}

float spokes(vec2 p, float s) {
  vec2 pp = toPolar(p);
  pp.y += TTIME*10.0/PERIOD;
  mod1(pp.y, TAU/10.0);
  pp.y += PI/2.0;
  p = toRect(pp);
  float ds = box(p, s*vec2(0.075, 0.5), s*vec4(0.04));
  return ds;
}

float arcs(vec2 p, float s) {

  float d1 = arc(p, sca1, sca2, s*0.275, s*0.025);
  float d2 = arc(p, sca1, sca2, s*0.18, s*0.025);

  return min(d1, d2);
}

float meeple(vec2 p, float s) {
  float dh = box(p - s*vec2(0.0, -0.035), s*vec2(0.07, 0.1), s*vec4(0.065));
  float dc = box(p - s*vec2(0.0, -0.22), s*vec2(0.15, 0.04), s*vec4(0.05, 0.02, 0.05, 0.02));

  return pmin(dh, dc, s*0.115);
}

float theLiveCoders(vec2 p, float s) {
  float ds = spokes(p, s);
  float dc = circle(p, 0.375*s);
  float da = arcs(p, s);
  float dm = meeple(p, s);

  float d = ds;
  d = min(d, dc);
  d = max(d, -da);
  d = max(d, -dm);

  return d;
}

float df(vec2 p) {  
  return theLiveCoders(p, 3.0);
}

vec3 normal(vec2 p, float offset) {
  vec2 v;
  vec2 w;
  vec2 e = vec2(0.0001, 0);

  vec3 n;
  n.x = warp(p + e.xy, offset, v, w) - warp(p - e.xy, offset, v, w);
  n.y = 2.0*e.x;
  n.z = warp(p + e.yx, offset, v, w) - warp(p - e.yx, offset, v, w);

  return normalize(n);
}


vec3 postProcess(vec3 col) {
  col=pow(clamp(col,0.0,1.0),vec3(0.75));
  col=col*0.6+0.4*col*col*(3.0-2.0*col);  // contrast
  col=mix(col, vec3(dot(col, vec3(0.33))), -0.4);  // satuation
  return col;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  vec2 q = fragCoord/iResolution.xy;
  vec2 p = -1. + 2. * q;
  p.x *= iResolution.x/iResolution.y;

  p *= 1.65;
  vec3 col = vec3(1.0);

  float d = df(p);
  p += -0.025*TTIME*vec2(-1.0, 1.0);

  vec2 v;
  vec2 w;

  float f = warp(p, d, v, w);
  vec3 n = normal(p, d);

  vec3 lig = normalize(vec3(0.6, -0.4, -0.4));
//  rot(lig.xz, TTIME/100.0);
  float dif = max(dot(lig, n), 0.5);

  const vec3 col1 = vec3(0.1, 0.3, 0.8);
  const vec3 col2 = vec3(0.7, 0.3, 0.5);

  float c1 = dot(normalize(lig.xz), v)/length(v);
  float c2 = dot(normalize(lig.xz), w)/length(w);

  col = pow(dif, 0.75)*tanh(pow(abs(f + 0.5), 1.5)) + c1*col1 + c2*col2;
  col += 0.25*vec3(smoothstep(0.0, -0.0125, d));

  col = postProcess(col);

  col *= smoothstep(0.0, 16.0, iTime*iTime);

  fragColor = vec4(col, 1.0);
}

#define MAX_STEPS 156
#define MIN_DISTANCE 0.001
#define MAX_DISTANCE 10.
#define LAYERS 10.
#define LAYER_SIZE 6.

struct TraceResult {
    vec3 id;
    float dt;
    float ds;
    float alpha;
};

float dPoint(vec3 ro, vec3 rd, vec3 p) {
    return length(cross(rd, p - ro))/length(rd);
}

float n21(vec2 p) {
    //return fract(sin(dot(n, vec2(12.9898, 4.1414 + iTime*0.00001))) * 43758.5453);
     return fract(sin(p.x*123.231 + p.y*1432.342 + iTime*0.01)*15344.22);
}

vec3 getIdColor(vec3 id) {
    float n = max(.2, n21(vec2(id.x + id.y, id.z)));
    vec3 rcol = vec3(n, fract(n*4567.433), fract(n*45689.33));
    return rcol;
}

TraceResult trace(vec3 ro, vec3 rd) {
    float ds,dt;
    float n;
    vec3 id, shift;
    float baseSize = .05;
    vec3 baseSpacing = vec3(baseSize * 4.);
    vec3 bounds = vec3(LAYER_SIZE, LAYER_SIZE, LAYERS);
    vec3 l = bounds;
    vec2 mouse = iMouse.xy/iResolution.xy;

    TraceResult res;
    res.alpha = 1.;

    for(int i = 0 ; i < MAX_STEPS ; i++) {
        vec3 p = ro + rd * ds;

        float a = 3.14/2.;
        p.yz *= mat2(vec2(sin(a), cos(a)), vec2(-cos(a), sin(a)));
        float aa = sin(iTime/4.)*6.26;//mouse.x * 3.14 * 2.;
        p.xy *= mat2(vec2(sin(aa), cos(aa)), vec2(-cos(aa), sin(aa)));

        vec3 rc1 = vec3(baseSpacing);
        vec3 q1 = p - rc1 * clamp(round(p/rc1), -l, l);


        id = round(p/rc1).xyz;

        float pa = sin(id.z + iTime * id.z*.05)*6.28;


        // z-layer interval scale
        rc1.xy *= (1. + (sin(id.z/5. + iTime*3.) * .5 + .5)*2.);
        // z-layer rotation
        p.xy *= mat2(vec2(sin(pa), cos(pa)), vec2(-cos(pa), sin(pa)));

        q1 = p - rc1 * clamp(round(p/rc1), -l, l);
        id = round(p/rc1).xyz;

        float n = n21(vec2(id.x * id.y, id.z * id.x*id.z));

        dt = length(q1 + vec3(.04 * n, .04 * fract(n*567.43), .01)) - baseSize;//- (.05 * fract(n*123.22));


        ds += dt * .5;
        if (abs(dt) < MIN_DISTANCE || dt > MAX_DISTANCE) {
            if (length(id.xy) > 1.6 && fract(n*718.54) > .5) {
                break;
            } else {
                 ds += .1;
            }
        }
    }



    res.id = id;
    res.dt = dt;
    res.ds = ds;

    return res;
}

void mainImage(out vec4 fragColor, in vec2 fragCoords) {
    vec2 uv = ((fragCoords.xy / iResolution.xy) - .5) * vec2(iResolution.x / iResolution.y, 1.);
    vec2 mouse = iMouse.xy/iResolution.xy;

    vec3 col = vec3(.0);

    vec3 ro = vec3(0.+sin(iTime), 0. + cos(iTime) , -3. + sin(iTime));
    vec3 lookat = vec3(0., 0., 0.);
    float zoom = .6;

    vec3 f = normalize(lookat - ro);
    vec3 r = normalize(cross(vec3(0., 1., 0.), f));
    vec3 u = cross(f, r);

    vec3 c = ro + f * zoom;
    vec3 I = c + uv.x * r + uv.y * u;

    vec3 rd = normalize(I - ro);

    TraceResult tr = trace(ro, rd);

    if (tr.dt < MIN_DISTANCE) {
        vec3 id = tr.id;
        vec3 rcol = getIdColor(id);
        col += rcol;
    }


    // col.rg = uv;

    fragColor = vec4(col, 1.);
}

#define M_PI 3.1415926535897932384626433832795

float dist (vec2 A, vec2 B)
{
	return sqrt(pow(A.x - B.x, 2.0) + pow(A.y - B.y, 2.0));
}

float getTheta (float x, float y)
{
    return (atan(y,x) * 180.0/M_PI) + 180.0;
}


vec3 getColor (float r, float theta,float x,float y)
{      
    vec3 bgLight = vec3(0.7,0.7,0.8);
    vec3 bgDark = vec3 (0.45,0.45,0.6);
    vec3 bg = vec3(0.6, 0.6, 0.7);
    vec3 color;
    if (r < 150.0)
    {
	    float s = r/60.0 + 0.1*(sin(r/150.0*8.0*2.0*3.14-iTime*10.0)*sin(x*0.1));	
        color = bgDark*s + bgLight*(1.0-s);
    }
    else
    {
        color = bg;
    }

    vec3 gate = vec3(0.35, 0.35, 0.42);
    vec3 symbols = vec3(0.45, 0.45, 0.55);
    vec3 chevronsGrey = vec3(0.3, 0.3, 0.4);
    vec3 chevronsRed = vec3 (0.4,  0.25,  0.27353);


    if (r < 150.0 && r > 120.0)
    {
        color = gate;
    }
    if (r < 140.0 && r > 125.0)
    {
        float theta2 = mod(theta+iTime*10.0,9.0);
        if (theta2<8.0)
        {
          color = symbols;
        }
        else
        {
         color = gate;
        }
    }
    if (r < 152.0 && r > 130.0 )
    {
        float theta3 = mod(theta,40.0);

        float hrana1 = 0.0;
        float hrana2 = 5.0;

        float t = (r-130.0)/(152.0-130.0);
        float hrana = t*hrana1+(1.0-t)*hrana2;


        if (theta3 >hrana && theta3 < 15.0-hrana)
            {
            	color = chevronsGrey; 
            }
        if (theta3 > hrana+2.0 && theta3 < 15.0-hrana-2.0)
        {
        	color = chevronsRed;
        }

    }

    return color;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Scaling
    vec2 xy = fragCoord.xy/(iResolution.xy/vec2(800.0,450.0));

    vec2 center = vec2(800.0,450.0) /2.0;
    float dx = center.x - xy.x;
    float dy = center.y - xy.y;
    float r = dist(xy, center);
    float theta = getTheta(dx, dy);

    vec3 col = getColor(r, theta,xy.x,xy.y);

    // Output to screen
    fragColor = vec4(col,1.0);
}

