const vec3 white = vec3(1., 1., 1.);
const vec3 black = vec3(0., 0., 0.);
const float EDGE_SCALE = .02;

// circleTest
//
// coord      - pixel to test
// center     - center of circle
// radius     - radius of circle
// width      - thickness of circle line
// pixelWidth - blendiness
vec3 circleTest(vec2 coord, vec2 center, float radius, float width, float pixelWidth) {
    float dist_to_center = sqrt(pow(coord.x - center.x, 2.) + pow(coord.y - center.y, 2.));
    float delta = dist_to_center-radius;

    // From shadertoy default shader
    vec2 uv = gl_FragCoord.xy/iResolution.xy;
    vec3 inside = .5 + .5*cos(iTime+uv.xyx+vec3(0,2,4));

    float blend = smoothstep(0., pixelWidth, abs(delta) - width);

    if (delta  < 0.) {
    	// rgb -> white (inside  edge)
        return mix(white, inside, blend);
    } else if (delta  > 0.) {
    	// white -> black (outside edge)
        return mix(white, black, blend);
    } else {
        // can't decide
        return white;
    }

}

void mainImage(out vec4 out_color, vec2 fragCoord) {
	vec2 center = iResolution.xy/2.;
    float pixelWidth;
    float width;

    if (iMouse.xy == vec2(0)) {
        pixelWidth = width = 1.;
    } else {
        pixelWidth = EDGE_SCALE * distance(iMouse.y, 0.);
        width = EDGE_SCALE * distance(iMouse.x, 0.);
    }

    vec3 c = circleTest(gl_FragCoord.xy, center, center.y/2., width, pixelWidth);

    out_color = vec4(c, 1.);
}

vec3 rotate(vec3 p, float angleX, float angleY)
{
    float cosA = cos(angleY);
	float sinA = sin(angleY);
	vec3 r = vec3(p.x, sinA * p.z + cosA * p.y, cosA * p.z - sinA * p.y);
    cosA = cos(angleX);
	sinA = sin(angleX);
	return (-vec3(cosA * r.x - sinA * r.z, r.y, sinA * r.x + cosA * r.z));
}

vec3 hsv2rgb(vec3 c)
{
    vec3 rgb = clamp(abs(mod(c.x * 6.0 + vec3(0.0,4.0,2.0),6.0) - 3.0) - 1.0, 0.0, 1.0);
	return (c.z * mix(vec3(1.0), rgb, c.y));
}

float distSphere(vec3 p, float r)
{
	return (length(p) - r);
}

bool backgr = false;

float distanceEstimate(vec3 p)
{
    float bailout = 2.0;
	float dSphere = -distSphere(p, 12.0);	
	vec3 v = p;
	float r = 0.0;
	float dr = 1.0;
	float power = abs(cos(iTime * 0.02)) * 10.0 + 2.0;
	for(float n = 0.0; n <= 8.0; n++)
	{
		r = length(v);
		if(r > bailout)
            break;
        // convert to polar coordinate
		float theta = acos(v.z / r);
		float phi = atan(v.y, v.x);
		dr = pow(r, power - 1.0) * power * dr + 1.0;

		// scale and rotate the point
		float vr = pow(r, power);
		theta = theta * power;
		phi = phi * power;

		// convert back to cartesian coordinates
		v = vr * vec3(sin(theta) * cos(phi), sin(phi) * sin(theta), cos(theta));

        v += p;
	}
	float dFractal = 0.50 * log(r) * r / dr;	
	backgr = dSphere < dFractal ? true : false;
	return (min(dFractal, dSphere));
}

vec3 getNormal(vec3 pos, float dist)
{
    vec3 eps = vec3(0.001, 0.0, 0.0);
	return normalize(vec3(
           distanceEstimate(pos + eps.xyy),
           distanceEstimate(pos + eps.yxy),
           distanceEstimate(pos + eps.yyx)) - dist);
}

float softShadow(in vec3 ro, in vec3 rd, in float k)
{
    float res = 1.0;
    float t = 0.0;
    for(int i = 0; i < 64; i++)
    {
        float d = distanceEstimate(ro + rd * t);
        res = min(res, k * d/t);
        if(res < 0.001)
            break;
        t += clamp(d, 0.01, 0.2);
    }
    return (clamp(res, 0.0, 1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 aspectRatio = vec2(iResolution.x/iResolution.y, 1.0);
	vec2 uv = aspectRatio * (fragCoord.xy / iResolution.xy - 0.5);
    vec2 mouse = 7.0 * (iMouse.xy / iResolution.xy - 0.5);
    vec3 ro = rotate(vec3(0.0, 0.0, 2.5), mouse.x, mouse.y);
    vec3 rd = -rotate(vec3(uv, 1.0), mouse.x, mouse.y);  
    vec3 light = rotate(vec3(0.0, 0.3, 0.77), mouse.x, mouse.y);
    vec3 lightColor = vec3(0.8, 0.9, 1.0);
    vec3 material;
    vec3 color;
    float eps = 0.002;
	float dist;
	for(float n = 0.0; n < 200.0; n++)
    {
		dist = distanceEstimate(ro);
		if(dist < eps)
			break;
		ro += rd * dist * 0.5;
	}
	if(backgr == true)
	{ 
        color = vec3(0.3, 0.8, 1.0) * (0.5 - 0.4 * uv.x);
		fragColor = vec4(color, 1.0);
		return;
	}
	vec3 norm = getNormal(ro, dist);
	material = hsv2rgb(vec3(dot(ro, ro) - 0.27, 1.2, 1.0));
	vec3 lightDir = normalize(light - rd);   
	float shadow = softShadow(ro + 0.001 * norm, light, 5.0);
    float ambient = 0.22;
	float diff = clamp(dot(light, norm), 0.0, 1.0) * shadow * 0.9;
	float spec = pow(clamp(dot(norm, lightDir), 0.0, 1.0), 32.0) * shadow * 1.8;
 	color = (lightColor * (ambient + diff + spec) * material);  
	color = pow(color, lightColor); 
	vec2 fd = (6.0 * fragCoord - iResolution.xy) / iResolution.y;   
    color *= 1.0 - length(fd) * 0.07;
    fragColor = vec4(color, 1.0);
}

#define R(a)a=vec2(a.x+a.y,a.x-a.y)*.7
void mainImage(out vec4 O, vec2 C) {
	for(float g,e,j,i;i++<50.;){
   		vec3 p=g*vec3((C*2.-iResolution.xy)/iResolution.y,1);
        p.z+=iTime;
        p=fract(p)-.5;
        p.xz=abs(p.xz)-.15-.05*(sin(iTime*2.));
        R(p.xz);R(p.yz);
        p.yz=abs(p.yz)-.1;
        R(p.yz);
        g+=e=.5*length(p.xy)-.01;
        e<.01?O.xyz+=.4/i:p;
    }
}

float rand(float s) {
    return fract(sin(s)*3908213.21321);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    vec2 q = p;
    q.y -= -0.4;

    float t = fract(iTime/4.0)*10.0 - 5.0;
    float c = floor(iTime/4.0);

    for(float i=0.0; i<=3.0; i+=1.0) {
    	q.y -= +0.3*smoothstep(0.0, rand(c+i*11.0)*0.5+0.05, abs(q.x-t-rand(c+i*13.0))-rand(c+i));
    	q.y -= -0.3*smoothstep(0.0, rand(c+i*17.0)*0.5+0.05, abs(q.x-t-rand(c+i*23.0))-rand(c+i*0.5));
    }

    vec3 col = vec3(0.02);
    float pi = acos(-1.0);
    float banner = smoothstep(0.64, 0.63, abs(q.y-0.45));
    col = mix(col, vec3(1.0), banner);
    col -=
        0.8*smoothstep(0.3, -0.3, sin( (q.x+iTime*0.2+floor(q.y*9.)*pi*0.5)*30. ))*
        banner;
    col = pow(col, vec3(0.45));

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float c = 1.45;
    float t = iTime * c;
    float s = t * 128.0; //sin(t/1.5) * 150.0 + 50000.0;

    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = 0.5 + 0.5 * cos(t + uv.xyx + vec3(0,2,4));

    vec2 center = vec2(iResolution.x / 2.0, iResolution.y/2.0);
    float x = fragCoord.x - center.x;
    float y = fragCoord.y - center.y;
    float xSquared = x * x;
    float ySquared = y * y;

    fragColor = vec4(col,1.0);
    fragColor.g *= 0.5;
    fragColor.g += sin(t * 12.0 * (xSquared + ySquared)/s - 2.001 * s);
    fragColor.b += sin(t * 12.0 * (xSquared + ySquared)/s - 2.0011 * s)/50.0;
    fragColor.r += sin(t * 12.0 * (xSquared + ySquared)/s - 2.0012 * s)/100.0;

}

const vec3 WORLD_UP = vec3(0.0, 1.0, 0.0);
const vec3 VIEW_TARGET = vec3(0.0, 0.0, 0.0);
const float ZOOM_FAC = 2.0;

/** Returns how far away `point` is from the ray line */
float distLine(vec3 rayOrigin, vec3 rayDir, vec3 point) {
    return length(cross(point - rayOrigin, rayDir)) / length(rayDir);
}

float drawPoint(vec3 rayOrigin, vec3 rayDir, vec3 point) {
    return smoothstep(0.06, 0.05, distLine(rayOrigin, rayDir, point));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float ratio = iResolution.x / iResolution.y;
    vec2 uv = fragCoord / iResolution.xy;

    uv -= 0.5; // Move the origin to the middle of the screen
    uv.x *= ratio; // Account for the aspect ratio

    vec3 rayOrigin = vec3(cos(iTime) * 4.0, sin(iTime) * 4.0, sin(iTime) * 4.0); // Camera position

    vec3 camForward = normalize(VIEW_TARGET - rayOrigin);
    vec3 camRight = normalize(cross(WORLD_UP, camForward));
    vec3 camUp = cross(camForward, camRight);

    vec3 screenCenter = rayOrigin + camForward * ZOOM_FAC; // Figure out where the center of the screen is in 3D space
    vec3 intersectPoint = screenCenter + (uv.x * camRight) + (uv.y * camUp);

    vec3 rayDir = intersectPoint - rayOrigin;

    // Make a color shifting gradient
    vec3 col = 0.5 + 0.5 * cos((iTime * 0.8) + uv.xyx + vec3(0.0, 2.0, 4.0));

    float mask = 0.0;

    // Add points of a cube
    // mask += drawPoint(rayOrigin, rayDir, vec3(-0.5, -0.5, -0.5));
    // mask += drawPoint(rayOrigin, rayDir, vec3(-0.5, -0.5,  0.5));
    // mask += drawPoint(rayOrigin, rayDir, vec3(-0.5,  0.5, -0.5));
    // mask += drawPoint(rayOrigin, rayDir, vec3(-0.5,  0.5,  0.5));
    // mask += drawPoint(rayOrigin, rayDir, vec3( 0.5, -0.5, -0.5));
    // mask += drawPoint(rayOrigin, rayDir, vec3( 0.5, -0.5,  0.5));
    // mask += drawPoint(rayOrigin, rayDir, vec3( 0.5,  0.5, -0.5));
    // mask += drawPoint(rayOrigin, rayDir, vec3( 0.5,  0.5,  0.5));

    // Add "lines" (composed of many points) of a cube
    for (float x = -0.5; x < 0.5; x += 0.05) {
        mask += drawPoint(rayOrigin, rayDir, vec3(x, -0.5, -0.5));
        mask += drawPoint(rayOrigin, rayDir, vec3(x,  0.5, -0.5));
        mask += drawPoint(rayOrigin, rayDir, vec3(x, -0.5,  0.5));
        mask += drawPoint(rayOrigin, rayDir, vec3(x,  0.5,  0.5));
    }

    for (float y = -0.5; y < 0.5; y += 0.05) {
        mask += drawPoint(rayOrigin, rayDir, vec3(-0.5, y, -0.5));
        mask += drawPoint(rayOrigin, rayDir, vec3( 0.5, y, -0.5));
        mask += drawPoint(rayOrigin, rayDir, vec3(-0.5, y,  0.5));
        mask += drawPoint(rayOrigin, rayDir, vec3( 0.5, y,  0.5));
    }

    for (float z = -0.5; z < 0.5; z += 0.05) {
        mask += drawPoint(rayOrigin, rayDir, vec3(-0.5, -0.5, z));
        mask += drawPoint(rayOrigin, rayDir, vec3( 0.5, -0.5, z));
        mask += drawPoint(rayOrigin, rayDir, vec3(-0.5,  0.5, z));
        mask += drawPoint(rayOrigin, rayDir, vec3( 0.5,  0.5, z));
    }

    col *= (min(mask, 1.0) * 0.8) + 0.2;

    // Output to screen
    fragColor = vec4(col, 1.0);
}

// inspired by https://en.wikipedia.org/wiki/Julia_set#/media/File:JSr07885.gif
// https://www.shadertoy.com/view/XljGzV
vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (y: -1 to 1 ;; x: -something to something)
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;

    vec2 z = uv;

    float my_const = 0.7885;
    float theta = iTime*0.4;
    vec2 c = vec2(my_const*cos(theta), my_const*sin(theta));

    const float max_steps = 65.;
    float boundary = 20.;
    float num_steps;

    for(num_steps = 0.; num_steps<max_steps; num_steps++) {
    	z = vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y) + c;
        if(length(z) > boundary) break;
    }

    float pixel_intensity = num_steps/max_steps;

    vec3 col = hsl2rgb(vec3(pixel_intensity + .6,1., smoothstep(1.,.8,pixel_intensity)*.5));

    // Output to screen
    fragColor = vec4(col, 1.0);
}

void mainImage(out vec4 O, vec2 T) {
    vec2 R=iResolution.xy,U=(T-.5*R.xy)/R.y;
    float D,t=iTime;
    vec3 P,B = normalize( vec3( U.x, U.y,1) );
    for(int i = 0; i<64;i++) {
        P = vec3(0, sin(U.x*5.+t), -5)+ B*D;
        P.z-=t*6.; 
        P=mod(P,3.)-1.5;
        P.z -= clamp( P.z, 0., 5. );
        D+=length(P) - .05;
    }
    O.r=O.g=((D< 16.) ? 1.:0.);
}

const float det=.005;
const float maxdist=50.;
float objcol, flo;
vec3 ldir=vec3(4.,-1.5,-1.);
const vec3 lcol=vec3(.7,.3,.2);
const vec3 ilcol=vec3(.6,.5,1.);


mat2 rot(float a) {
	float s=sin(a), c=cos(a);
    return mat2(c,s,-s,c);
}

vec3 fractal(vec2 p) {
    float m=100., l=100.;
    vec2 c=vec2(100.);
    p*=.2;
    for (int i=0; i<5; i++) {
    	p=abs(p+.75)-abs(p-.75)-p;
        p+=vec2(0.,2.);
        p=p*2.5/clamp(dot(p,p),.2,1.)-1.5;
        if (i>0) l=min(l,min(abs(p.x),abs(p.y)));
        m=min(m,length(p));
        c=min(c,abs(p));
    }
    l=exp(-6.*l)*pow(abs(.5-fract(m*.3+iTime))*2.,6.);
    c=normalize(exp(-1.*c));
    return l*vec3(c.x,length(p)*.015,c.y)*1.5;
}

vec3 rotate(vec3 p) {
    p.xz*=rot(iTime);
    p.yz*=rot(iTime*.5);
	return p;
}

float de_light(vec3 p) {
    return length(p)-1.5;
}


float de(vec3 p) {
    float op=smoothstep(.5,.7,sin(iTime*.5));
    //float op=1.;
    float r=-op*1.3;
	float w=cos(length(p.xz*2.)-iTime*5.)*.15*smoothstep(0.,3.,length(p.xz));
    w*=exp(-.1*length(p.xz));
    float f=p.y+3.+w-r;
    p=rotate(p);
    float c1=abs(p.x)-op;
    float c2=abs(p.y)-op;
    float c3=abs(p.z)-op;
	float c=min(min(c3,min(c1,c2)),length(p)-2.8+r);
    float s=length(p)-3.+r;
    float d=max(s,-c);
    p=fract(p*5.);
    float grid=min(abs(p.z),min(abs(p.x),abs(p.y)))*.5;
    d=min(d,f);
    objcol=max(step(c,d+.1),grid);
    flo=step(f,d);
    objcol=max(flo,objcol);
    return d;
}

vec3 normal(vec3 p) {
    vec3 e=vec3(0.,det*4.,0.);
	return normalize(vec3(de(p+e.yxx),de(p+e.xyx),de(p+e.xxy))-de(p));
}

vec3 triplanar(vec3 p, vec3 n) {
	p=rotate(p);
    return fractal(p.yz) * abs(n.x) + fractal(p.xz) * abs(n.y) + fractal(p.xy) * abs(n.z);
}

float shadow(vec3 p, vec3 dir) {
	float td=.0, sh=1., d=.2;
    for (int i=0; i<60; i++) {
        p-=d*dir;
        d=de(p);
        float dl=de_light(p);
        td+=min(d,dl);
        sh=min(sh,10.*d/td);
        if (sh<.01 || dl<1.) break;
    }
    return clamp(sh,0.,1.);
}


vec3 shade(vec3 p, vec3 n, vec3 dir) {
    float f=flo;
    float ocol=objcol;
    float sh=shadow(p,ldir);
    float ish=shadow(p,normalize(p));
    sh=max(sh,ish);
    float amb=max(0.,dot(dir,-n))*.1;
    float dif=max(0.,dot(ldir,-n))*sh;
    vec3 ref=reflect(ldir,-n);
    float spe=pow(max(0.,dot(dir,-ref)),50.);
    vec3 fcol=triplanar(p,n)*(1.-ocol);
    vec3 col=ocol*vec3(1.5)-f;
    vec3 ildir=normalize(p);
    float idif=max(0.,dot(ildir,-n))*exp(-.05*length(p));    
    vec3 lref=reflect(ildir,-n);
    float lspe=pow(max(0.,dot(dir,-lref)),30.)*ish;
    return max((amb+dif*lcol)*col+spe*lcol,idif*ilcol*ish+lspe)+fcol;
}


vec3 march(vec3 from, vec3 dir) {
	float d, dl, td=0., tdl=0., ref=0.;
    vec3 p=from, refrom=from, pl=p, col=vec3(0.), savecol=col, odir=dir;
    vec3 back=lcol*max(0.,1.-dir.y*3.)*.1;
    for (int i=0; i<80; i++) {
		p+=dir*d;
		pl+=dir*dl;
        d=de(p);
        td+=d;
        if (d<det && flo<.5) break;
        if (td>maxdist) break;
        if (d<det && flo>.5) {
	        p-=det*dir*2.;
            vec3 n=normal(p);
            savecol=mix(shade(p,n,dir),back,td/maxdist);
            dir=reflect(dir,n);
            ref=.7;
            refrom=p;
        }
    }
    if (d<det) {
        p-=det*dir*2.;
        vec3 n=normal(p);
    	col=shade(p,n,dir);
    } else {
      	back+=pow(max(0.,dot(dir,normalize(-ldir))),100.)*lcol;
	    back+=pow(max(0.,dot(dir,normalize(-ldir))),200.)*.5;
        col=back;
        td=maxdist;
    }
    float li1=pow(max(0.,dot(dir,-normalize(refrom))),120.);
    float li2=pow(max(0.,dot(odir,-normalize(from))),120.);
    float li=max(li1,li2);
    li*=step(length(from),distance(p,from));
    col=mix(col,savecol,ref)+li*ilcol*2.;

    int steps=70;
    float lmax=maxdist*.5;
    float st=lmax/float(steps);
    li=0.;
    for (int i=0; i<steps; i++) {
    	p=from+odir*tdl;
        tdl+=st;
        if (tdl>td) break;
        li+=shadow(p, normalize(p))*exp(-.25*length(p));
    }

    return col+li*.035*ilcol;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	ldir=normalize(ldir);
    vec2 uv=(fragCoord-iResolution.xy*.5)/iResolution.y;
	vec3 dir=normalize(vec3(uv,1.+sin(10.+iTime*.5)*.3));
    vec3 from=vec3(0.,0.,-12.);
    from.xz*=rot(iTime*.2);
    dir.xz*=rot(iTime*.2);
    dir.x+=sin(iTime*.5)*.3;
    dir=normalize(dir);
    vec3 col = march(from, dir);
    fragColor = vec4(col,1.0);
}

// based on...
// https://www.shadertoy.com/view/lssfW2

float nsin(float x)
{
    return .5+.5*sin(x);
}
float ncos(float x)
{
    return .5+.5*cos(x);
}

float map(float value, float min1, float max1, float min2, float max2) {
