    // r between 3.75 and 4.0 produces chaotic behavior
    float rr = .5*(ncos(.5*iTime))+3.5; // + .5*nsin(iTime*.5); //2.57 +nsin(iTime); //+cos(iTime);
    xy = uv;
    //int max_iter = int(6.0 + floor(8.0*nsin(iTime*.2)));
    for (int i = 0; i<9; i++)
    {
        xy = rr*xy*(vec2(1.0,1.0)-xy + .125*nsin(.1*iTime)*vec2(sin(.5*iTime + 6.28*xy.y),cos(.5*iTime-6.28*xy.x)));
        if(i==0)
        {
          xy0 = xy;  
