vec2 nextZ(vec2 z, vec2 z0) {
    const float c = 0.0;

    return vec2(z.x * z.x - z.y * z.y + z0.x, 2.0 * z.x * z.y + z0.y); 
}

float mandelbrot(vec2 z, vec2 z0) {    
    const float R2 = 1200.0;
    vec2 r = vec2(0.0, 0.0);
    float i = 0.0;
    for(; i < 1.0; i = i + 0.0056) {
    	z = nextZ(z, z0);
        r = z - z0;
        float dist2 = r.x * r.x + r.y * r.y;
        if(dist2 > R2) {
            break;
        }
    }
    return i;
}

float red(float i) {
    i *= 0.2;
	return sin(i * 3.14 * 10.0);   
}

float green(float i) {
    i *= 0.5;
	return sin(i * 3.14 * 3.0);   
}

float blue(float i) {
    i *= 1.3;
	return sin(i * 3.14 * 4.0);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 size = vec2(iResolution.x, iResolution.y);

    //z coordinate x : -1.0 .. 1.0, y : -1.0 .. 1.0
    vec2 z = 0.2 * (2.0 * (fragCoord - 0.5 * size) / size);
    float aspect = size.y / size.x;
    // y coordinate from -aspect .. aspect
    z.y *= aspect;

    // scale calculation by time
    float zeroone = 0.5 * sin(iTime * 0.1);
    float arg = zeroone * 550.0;
    float scale = pow(1.029, -arg);

    // dirty hack for scale increasing slow down
    if(scale > 1.0) {
        scale = pow(scale, 0.63);
    }

    // apply scale
    z = z * scale;

    // rotation
    float alpha = iTime * 0.13;
    float cosa = cos(alpha);
    float sina = sin(alpha);

    z = vec2(z.x * cosa + z.y * sina, z.y * cosa - z.x * sina);

    //center :: from wiki
    vec2 center = vec2(-1.88488933694469, 0.00000000081387);
    z += center;

    // distance to mandelbrot set
	float d = mandelbrot(z, z);
    // Output to screen
    fragColor = vec4(red(d), green(d), blue(d), 1.0);
}

#define F(s)p.s=abs(p.s)-.3+sin(iTime*9.)*.05
void mainImage(out vec4 O, vec2 C) {
	for(float g,e,i;i++<80.;){
    	vec3 p=g*vec3(C/iResolution.y-.7,1)+vec3(0,4,iTime*5.);
    	p=mod(p,8.)-4.;
    	for(int j;j++<3;)F(xy),F(yz),F(xz);
    	g+=e=length(cross(p,.2+p-p))-.02;
    	e<.01?O.xyz+=(cos(p+exp(-p*8.)))/i:p;
	}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;

    vec3 color = vec3(0.0, 0.0, 0.0);

    color = mix(color, vec3(0.0, 1.0, 1.0), step(0.0, st.x));
    color = mix(color, vec3(1.0, 1.0, 0.0), step(0.340, st.x));
    color = mix(color, vec3(1.0, 0.0, 1.0), step(0.674, st.x));

    float pi = 3.14;
    float c = 1.5;
    float v1 = abs(sin(iTime * (pi + 0.1) * c));
    float v2 = abs(sin(iTime * (pi + -0.756) * c));
    float v3 = abs(sin(iTime * (pi + 0.816) * c));

    color = color + vec3(1.0, 0.0, 0.0) * step(0.560 * v1, st.y);
    color = color + vec3(0.0, 1.0, 0.0) * step(0.880 * v2, st.y);
    color = color + vec3(0.0, 0.0, 1.0) * step(0.592 * v3, st.y);

    fragColor = vec4(color, 1.0);
}

