void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-iResolution.xy*0.5)/iResolution.y;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    col = vec3(0.,0.,0.);

	float rad = length(uv);
	float a = atan(uv.y, uv.x);
	float bound = 0.7;
	float circle = smoothstep(bound, bound-0.01, rad);
    float strength = 1.-((sin(iTime) + 1.) * 0.005);

	float blobmaskr = mix(cos(a*2.)+sin(a+iTime), 1.-rad, strength);
	float blobr = smoothstep(bound, bound+0.001, blobmaskr);

	float blobmaskg = mix(sin(a*2.)+cos(a+iTime-0.3), 1.-rad, strength);
	float blobg = smoothstep(bound, bound+0.001, blobmaskg);

	float blobmaskb = mix(cos(a*2.)-sin(a+iTime-0.7), 1.-rad, strength);
	float blobb = smoothstep(bound, bound+0.001, blobmaskb);

	col = vec3(blobr,blobg,blobb);

    fragColor = vec4(col,1.0);
}

