#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01
#define MAXITE 32.0

mat3 rotx(float a) { mat3 rot; rot[0] = vec3(1.0, 0.0, 0.0); rot[1] = vec3(0.0, cos(a), -sin(a)); rot[2] = vec3(0.0, sin(a), cos(a)); return rot; }
mat3 roty(float a) { mat3 rot; rot[0] = vec3(cos(a), 0.0, sin(a)); rot[1] = vec3(0.0, 1.0, 0.0); rot[2] = vec3(-sin(a), 0.0, cos(a)); return rot; }

float rnd(vec2 n){
  return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float mandel(vec2 p)
{    
    float zre=0.; //sin(iTime)/4.0;
    float zim=0.; //cos(iTime)/3.0;
    float cre=p.x;
    float cim=p.y;
    float col=0.;

    for(float i=0.;i<MAXITE;i++)
    {
        float dam=zre*zre-zim*zim+cre;
        zim=2.0*zre*zim+cim;
        zre=dam;
        col++;
        if( (zre*zre+zim*zim)>4.0 )
            break;
    }

    return (col/MAXITE);
}


float GetDist(vec3 p) {
    float v=mandel(p.xz/6.0);
	vec4 s1 = vec4(p.x,-v,p.z,0.5);

    float sphereDist1 =  length(p-s1.xyz)-s1.w;
    float planeDist = p.y;

    float d = min(sphereDist1, planeDist);
    return d;    
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;

        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 6, 2);
    //lightPos.xz += vec2(sin(iTime), cos(iTime))*2.;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
   // float d = RayMarch(p+n*SURF_DIST*2., l);
    //if(d<length(lightPos-p)) dif *= .1;

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);

    vec3 ro = vec3(-2., 6, -7.5);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    ro = ro*(rotx(-0.4));
    ro.x=-2.0+4.0*cos(iTime/2.0);
    ro.z=-7.0+5.0*sin(iTime/1.5);
    float d = RayMarch(ro, rd);

    vec3 p = ro + rd * d;

    float dif = GetLight(p);
    col = vec3(dif);

    fragColor = vec4(col,1.0);
}

// inspired by https://www.shadertoy.com/view/MdlXz8

#define TAU 30.3
#define MAX_ITER 4

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
	float time = iTime*1.0;

    vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 p = uv*TAU;

   	vec2 i = vec2(p);
	float c = 1.;
	float intensity = 0.01;

	for (int n = 1; n < MAX_ITER; n++) 
	{
		float t = time * (1./(float(n+1)));
       i = p + vec2(sin(time - i.y)* tan(t - i.x),sin(t - i.x)-tanh(t * i.xy))-(TAU*tanh(sin(time)*intensity),time);
		c -= 1./length(vec2(abs(tan(i.x+t)/intensity),p.y / (cosh(i.y-t)/intensity)));
	}
	c /= float(MAX_ITER);
	c = 1.0-(pow(c, 2.4));
	vec3 colour = vec3(pow((c),50.0));
    colour = clamp(colour + vec3(0.3, 0.0, .3), 0.0, 1.0);

    fragColor = vec4(colour, 1.0);
}

// Layers of Shapes
// Timothy Michael Walsh
// 2020

#define NUM_LAYERS 7.0
#define PI acos(-1.0)
#define PI_2 PI*2.0

mat2 rot(float a){
    float s = sin(a);
    float c = cos(a);
    return mat2(s,-c,c,s);
}

float rnd(vec2 uv){
    return fract(sin(uv.x+uv.y*7876.78)*97687.78);   //-iTime/800.
}

float pattern(vec2 uv, inout vec4 rColor, inout float grd){

    uv*=0.75;

    vec2 id = floor(uv);
    float rr = fract(sin(id.y*768.67)*7868.89)*1.3;
    uv.x += rr;
    //uv.x += (rr > .49) ? sin(3.0*iTime*fract(rr*rr)) : sin(3.0*-iTime*rr);

    id = floor(uv);
    vec2 gv = fract(uv)-.5;
    grd = step(.468,gv.x) + step(.468,gv.y);


    float rand =  fract(sin(id.x+id.y * 768.65) *7868.78);
    float rand2 = fract(sin(id.x * 768.65) *7868.78);

    float spin = rand<.49 ? -1.0 : 1.0;

    gv *= rot(spin*fract(rand+rand+14.8)*iTime*5.0);

    //rColor += vec4(rand2, fract(id.x*76.56), fract(rand2+rand2), rand2);
    rColor = vec4(rnd(id), rnd(id+234.56), rnd(id*67.67), rnd(id));

    rand *= 4.0;
    rand += 3.0;

    int num = int(rand); // 3;
    float angle = atan(gv.x,gv.y)+PI;
    float radius = PI_2/float(num);
    float shape = cos(floor(.5+angle/radius) * radius - angle) * length(gv);

    shape = smoothstep(.13,.1, shape);
    return shape;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * (fragCoord/iResolution.xy-.5));
    uv.x *= iResolution.x / iResolution.y;
    vec2 id = floor(uv);
	vec4 rColor = vec4(0.0);
    vec4 col = vec4(0.0);
    float grd = 0.0;
    float uvy = uv.y;

    for(float i = 0.0; i<1.; i+=1.0/NUM_LAYERS){
        float depth = fract(i+(iTime*.35));
        float scale = mix(25., .15, depth);
        uv *= rot(fract(sin(i))+sin(iTime*.5)*2.);
        float d = pattern(uv*scale+i*564., rColor, grd);
        float fade = (depth*smoothstep(.3,.99,depth));
        //col += mix(col, rColor, d)*fade;  //pattern(uv)  vec4(r, r1, r2, 1.0)
        col += rColor*d*fade;
        col.rgb += iMouse.z>0.1 ? grd*fade : 0.0;
    }

    fragColor = col;   //vec4(col,1.0)*pattern(uv);
    fragColor = (abs(uvy)<.95) ? fragColor : vec4(1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float t = (1.1 + sin(iTime * 0.5)) * 2.0;
    vec2 cellSize = vec2(t / 16.0, t / 9.0);
    //Cell coords:
    vec2 cell = vec2( int(uv.x / cellSize.x), int(uv.y / cellSize.y) );

    bool isWhite = false;

    if(int(cell.x) % 2 == 0)
        isWhite = true;

    if(int(cell.y) % 2 == 0)
        isWhite = !isWhite;

    if(isWhite)
        fragColor = vec4(0.9, 0.9, 1, 1);
    else
        fragColor = vec4(0.1, 0.1, 0, 1);
}

