float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}


//Box function
float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf 
}


float differenceSDF(float distA, float distB) 
{
    return max(distA, -distB);
}

///-----------------------------




vec2 isphere( in vec4 sph, in vec3 ro, in vec3 rd )
{
    vec3 oc = ro - sph.xyz;

	float b = dot(oc,rd);
	float c = dot(oc,oc) - sph.w*sph.w;
    float h = b*b - c;

    if( h<0.0 ) return vec2(-1.0);

    h = sqrt( h );

    return -b + vec2(-h,h);
}

#define ZERO (min(iFrame,0))


float map( in vec3 p, out vec4 resColor )
{
    vec3 w = p;
    float m = dot(w,w);

    vec4 trap = vec4(abs(w),m);
	float dz = 1.0;


	for( int i=0; i<4; i++ )
    {
        dz = 8.0*pow(sqrt(m),7.0)*dz + 1.0;
		//dz = 8.0*pow(m,3.5)*dz + 1.0;

        float r = length(w);
        float b = 8.0*acos( w.y/r);
        float a = 8.0*atan( w.x, w.z );
        w = p + pow(r,8.0) * vec3( sin(b)*sin(a), cos(b), sin(b)*cos(a) );


        trap = min( trap, vec4(abs(w),m) );

        m = dot(w,w);
		if( m > 256.0 )
            break;
    }

    resColor = vec4(m,trap.yzw);



    float fra =0.25*log(m)*sqrt(m)/dz;


    float sdb1 =sdBox(p- vec3(0.0), vec3(0.75) );

    float res=999.9 ;

    float dif1= differenceSDF(sdb1,fra) ;

    res=min(res,dif1);
    return res;

}

float intersect( in vec3 ro, in vec3 rd, out vec4 rescol, in float px )
{
    float res = -1.0;

    // bounding sphere
    vec2 dis = isphere( vec4(0.0,0.0,0.0,1.25), ro, rd );
    if( dis.y<0.0 )
        return -1.0;
    dis.x = max( dis.x, 0.0 );
    dis.y = min( dis.y, 10.0 );

    // raymarch fractal distance field
	vec4 trap;

	float t = dis.x;
	for( int i=0; i<128; i++  )
    { 
        vec3 pos = ro + rd*t;
        float th = 0.25*px*t;
		float h = map( pos, trap );
		if( t>dis.y || h<th ) break;
        t += h;
    }


    if( t<dis.y )
    {
        rescol = trap;
        res = t;
    }

    return res;
}

float softshadow( in vec3 ro, in vec3 rd, in float k )
{
    float res = 1.0;
    float t = 0.0;
    for( int i=0; i<64; i++ )
    {
        vec4 kk;
        float h = map(ro + rd*t, kk);
        res = min( res, k*h/t );
        if( res<0.001 ) break;
        t += clamp( h, 0.01, 0.2 );
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 calcNormal( in vec3 pos, in float t, in float px )
{
    vec4 tmp;
    vec2 e = vec2(1.0,-1.0)*0.5773*0.25*px;
    return normalize( e.xyy*map( pos + e.xyy,tmp ) + 
					  e.yyx*map( pos + e.yyx,tmp ) + 
					  e.yxy*map( pos + e.yxy,tmp ) + 
					  e.xxx*map( pos + e.xxx,tmp ) );
}

const vec3 light1 = vec3(  0.577, 0.577, -0.577 );
const vec3 light2 = vec3( -0.707, 0.000,  0.707 );


vec3 render( in vec2 p, in mat4 cam )
{
	// ray setup
    const float fle = 1.5;

    vec2  sp = (2.0*p-iResolution.xy) / iResolution.y;
    float px = 2.0/(iResolution.y*fle);

    vec3  ro = vec3( cam[0].w, cam[1].w, cam[2].w );
	vec3  rd = normalize( (cam*vec4(sp,fle,0.0)).xyz );

    // intersect fractal
	vec4 tra;
    float t = intersect( ro, rd, tra, px );

	vec3 col;

    // color sky
    if( t<0.0 )
    {
     	col  = vec3(0.8,.9,1.1)*(0.6+0.4*rd.y);
		col += 5.0*vec3(0.8,0.7,0.5)*pow( clamp(dot(rd,light1),0.0,1.0), 32.0 );
	}
    // color fractal
	else
	{
        // color
        col = vec3(0.01);
		col = mix( col, vec3(0.30,0.20,0.30), clamp(tra.y,0.0,1.0) );
	 	col = mix( col, vec3(0.2,0.10,0.30), clamp(tra.z*tra.z,0.0,1.0) );
        col = mix( col, vec3(0.30,0.10,0.62), clamp(pow(tra.w*0.5,6.0),0.0,1.0) );


        col *= 0.5;
		//col = vec3(0.1);

        // lighting terms
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos, t, px );
        vec3 hal = normalize( light1-rd);
        vec3 ref = reflect( rd, nor );
        float occ = clamp(0.05*log(tra.x),0.0,1.0);
        float fac = clamp(1.0+dot(rd,nor),0.0,1.0);

        // sun
        float sha1 = softshadow( pos+0.001*nor, light1, 32.0 );
        float dif1 = clamp( dot( light1, nor ), 0.0, 1.0 )*sha1;
        float spe1 = pow( clamp(dot(nor,hal),0.0,1.0), 32.0 )*dif1*(0.04+0.96*pow(clamp(1.0-dot(hal,light1),0.0,1.0),5.0));
        // bounce
        float dif2 = clamp( 0.5 + 0.5*dot( light2, nor ), 0.0, 1.0 )*occ;
        // sky
        float dif3 = (1.0+0.3*nor.y)*(0.2+0.8*occ);

		vec3 lin = vec3(0.0); 
		     lin += 7.0*vec3(1.50,1.10,0.70)*dif1;
		     lin += 4.0*vec3(0.25,0.20,0.15)*dif2;
        	 lin += 1.5*vec3(0.10,0.20,0.30)*dif3;
             lin += 2.5*vec3(0.35,0.30,0.25)*(0.05+0.95*occ); // ambient
        	 lin += 4.0*fac*occ;                          // fake SSS
		col *= lin;
		col = pow( col, vec3(0.7,0.9,1.0) );                  // fake SSS
        col += spe1*15.0;
        //col += 8.0*vec3(0.8,0.9,1.0)*(0.2+0.8*occ)*(0.03+0.97*pow(fac,5.0))*smoothstep(0.0,0.1,ref.y )*softshadow( pos+0.01*nor, ref, 2.0 );
        //col = vec3(occ*occ);
    }

    // gamma
	col = sqrt( col );

    // vignette
    col *= 1.0 - 0.05*length(sp);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime*.5;

    // camera
	float di = 1.4+0.1*cos(.29*time);
	vec3  ro = di * vec3( cos(.33*time), 0.8*sin(.37*time), sin(.31*time) );
	vec3  ta = vec3(0.0,0.1,0.0);
	float cr = 0.5*cos(0.1*time);

    // camera matrix
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
    vec3 cw = normalize(ta-ro);
	vec3 cu = normalize(cross(cw,cp));
	vec3 cv =          (cross(cu,cw));
    mat4 cam = mat4( cu, ro.x, cv, ro.y, cw, ro.z, 0.0, 0.0, 0.0, 1.0 );

	vec3 col = render(  fragCoord, cam );

	fragColor = vec4( col, 1.0 );
}

#define PI 3.1415926536

float d_circle(vec2 point, vec2 center, float radius) {
    return length(point - center) - radius;
}

float d_box(vec2 point, vec2 size, float rounding) {
    return length(max(vec2(0.0),abs(point) - size)) - rounding;
}

vec2 radial_symmetry(vec2 point, float ways) {
    vec2 prad = vec2(length(point), atan(point.x, point.y) + PI);
    prad.y = mod(prad.y, PI*2.0/ways) - PI/ways;
    return prad.x*vec2(cos(prad.y), sin(prad.y));
}

vec2 rotate(vec2 point, float angle) {
    return vec2(point.x * cos(angle) + point.y * sin(angle),
                point.x * sin(angle) - point.y * cos(angle));
}

vec3 color_rings(float d) {
    float level = floor(d) / 5.0;
    if (fract(d) < 0.1 && d<=5.2) {
        return vec3(level) * 0.75;
    } else {
        return vec3(level) * 0.5 + 0.5;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float range = 30.0;

    vec2 point  = (fragCoord/iResolution.xy - vec2(0.5,0.5))
        * vec2(1.0, iResolution.y / iResolution.x) * range
        ;
    vec2 cpoint = radial_symmetry(point, 5.0);

    vec2 center = vec2(5,0);
    float circ = d_circle(cpoint, center, 2.0);
    vec2 boxp = rotate(point, iTime*0.2) + 
        vec2(8.0*sin(iTime*0.19), 7.0*cos(iTime*0.17));
    float box = d_box(boxp, vec2(6, 1.2), 0.10);
//    float bevel = (box + circ) -0.5;
    float cornerbit = max(box,circ) - 0.5;
    float d = min(min(box,circ), cornerbit);
    // Time varying pixel color
    vec3 col = vec3(1);
    if (d < 0.0) {
        col = vec3(0.7,0.2,0.2);
    } else {
        col = color_rings(d);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define SQRT3 1.7320508

const vec2 s = vec2(1.0, SQRT3);
const vec2 ax0=vec2( .0  ,1.);
const vec2 ax1=vec2(-.866, .5);
const vec2 ax2=vec2( .866, .5);

vec4 getHex(vec2 p){
    //return xy = square 0-1 coords
    vec4 hC = floor(vec4(p, p - vec2(.5, 1))/s.xyxy) + .5;
    vec4 h = vec4(p - hC.xy*s, p - (hC.zw + .5)*s);
    return dot(h.xy, h.xy)<dot(h.zw, h.zw) ? vec4(h.xy, hC.xy) : vec4(h.zw, hC.zw + 9.73);
}

float distToNearestHexEdge(vec2 hv){
    vec3 adist = abs(vec3(dot(hv,ax0),dot(hv,ax1),dot(hv,ax2)));
    return 1.-(max(max(adist.x,adist.y),adist.z)*2.);
}

vec4 hexOutline(vec2 uv,float hexSize,float lineSize,float lineBlend){
	vec4 hex = getHex(uv/hexSize);
    //fragColor = vec4(abs(h.xy-.5), 0.,1.);
    float dist = distToNearestHexEdge(hex.yx);
    float linePower = smoothstep(lineSize,lineSize+lineBlend,dist);
    //fragColor=vec4(mix(1.,0.,linePower));
    hex.z = linePower;
	return hex;
}

float hexsz(){
    return (fract(iTime*.01)*0.5)+.1;
}

vec4 hexfn(in vec2 fragCoord){
	float hexSize = hexsz();
    float lineSize = .02;
    float lineBlend = .02;
    vec2 uv = (fragCoord /iResolution.xx).yx;
    vec4 hex = hexOutline(uv,hexSize,lineSize,lineBlend);
    float linePower = hex.z;
    return mix(texture(iChannel0,hex.xy),vec4(1.),1.-linePower);
}

