#define ITER 64.
#define PI 3.141592
#define TAU (2.*PI)
#define time iTime
#define mousepos (-1.+2.*(iMouse.xy / iResolution.xy))

// Comment this line to toggle mouse rotation
// you'll be able to see steven from every angle! 
#define ANIMATED

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

void mo (inout vec2 p ,vec2 d)
{
    p = abs(p)-d;
    if (p.y>p.x) p = p.yx;
}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r,abs(p.z)-h);}

/////////////////////////////////////////////////
// iq's website : https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float capsule( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

float ellipse( vec3 p, vec3 r )
{
    float k0 = length(p/r);
    float k1 = length(p/(r*r));
    return k0*(k0-1.0)/k1;
}
////////////////////////////////////////////////////

float head (vec3 p)
{
    p.y -= 0.85;
    float sphe = ellipse(p+vec3(0.,0.2,.2),vec3(0.4,0.25,0.4));
    float rx = (p.y > 0.)? 0.45 : 0.45 -sin(p.y)*0.4;  
    return smin(ellipse(p , vec3(rx,0.5,0.5)),sphe, 0.2);
}

float hair (vec3 p)
{
    p.y -= 0.9;
    vec3 pp = p;
    p.yz *= rot(TAU/6.);
    mo(p.xz, vec2(0.18));
    p.x -= 0.22;
    float s1 = length(p)-0.2;

    p = pp;
    p.z -= 0.2;
    p.yz *= rot(TAU/5.);
    mo(p.xz, vec2(0.17,0.25));
    float s2 = length(p)-0.28;

    p = pp;
    p.z -= 0.35;
    p.x = abs(p.x)-0.1;
    float s3 = length(p)-0.25;
    return min(min(s2,s3),s1);
}

float eyes (vec3 p)
{
    p.y -= 0.9;
    p.z += 0.48;
    p.x = abs(p.x)-0.12;
    return ellipse(p,vec3(0.05,0.08,0.05));
}

float belly (vec3 p)
{
    return ellipse(p-vec3(0.,0.,0.03), vec3(0.6,0.67,0.55));
}

float legs (vec3 p)
{
    float feet_position = 0.9;
    float r = (p.y>-feet_position) ? 0.2 : 0.2-sin(p.y+feet_position)*0.3;
    p.y += 0.6;
    p.x = abs(p.x)-.28;

    return cyl(p.xzy, r, 0.4);
}

float arms (vec3 p)
{
    p.x = abs(p.x) - 0.24;
    float thumb = capsule(p,vec3(0.37,-.0,-0.25),vec3(0.37,-0.13,-0.38), 0.05);
    p.x += sin(p.y*3.+5.)*0.2;
    return smin(thumb,capsule(p, vec3(0.18,.3,0.05), vec3(.25,-0.12,-0.2), 0.17),0.05);
}

float ey, bo;
vec3 new_p;
float SDF (vec3 p)
{
    p.y += 0.15;
    #ifdef ANIMATED
    	p.xz *= rot(time);
    #else
    	p.xz *= rot(PI*mousepos.x);
    	p.yz *= rot(PI*mousepos.y);
    #endif
    new_p = p;
    ey = eyes(p);
    bo = min(smin(belly(p),legs(p),0.05),smin(smin(head(p),hair(p),0.08),arms(p),0.15));
    return min(ey,bo);
}

vec3 steven_color (vec2 uv)
{
    uv.x = abs(uv.x)-PI/5.;
    vec2 guv = fract(uv)-0.5;
    vec2 id = floor(uv)-0.5;
    float mask = step(abs(guv.x),0.25+sin((uv.y+id.x)*15.)*0.05);
    return mix (vec3(0.8,0.9,0.8),vec3(0.2,0.8,0.5), mask);
}

///////////////////////////////////////////////////
// PIXEL SPIRIT DECK CODE 
// https://patriciogonzalezvivo.github.io/PixelSpiritDeck/
float starSDF2(vec2 uv, int V, float s) 
{
    vec2 st = uv;
    float a = atan(st.y, st.x) / TAU;
    float seg = a * float(V);
    a = ((floor(seg) + 0.5)/float(V) + mix(s, -s, step(.5, fract(seg)))) * TAU;
    return abs(dot(vec2(cos(a), sin(a)), st));
}
float fill(float x, float size) 
{
    return 1. - smoothstep(size,size*1.05, x);
}
///////////////////////////////////////////////////

vec3 background (vec2 uv)
{
    uv.y -= 0.07;
    uv *= rot(-PI/10.);

    float a = atan(uv.y,uv.x);
    a = mod(a,TAU/5.)-(TAU/5.)*0.5;
    uv = vec2(cos(a),sin(a)) * length(uv);
    uv.x += 0.05;
    float mask = fill(starSDF2(uv, 5, 0.1),0.13);
    return mix(vec3(0.9,0.25,0.45),vec3(0.9,0.8,0.),mask);
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.001,0.);
    return normalize(SDF(p)-vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    vec3 ro = vec3(0.,0.2,-3.3),
        rd = normalize(vec3(uv,1.)),
        p = ro,
        col = background(uv);

    float d,shad = 0.;
    bool hit = false;

    for (float i=0.; i<ITER; i++)
    {
        d = SDF(p);
        if (d<0.001)
        {
            hit = true;
            shad = i/ITER;
            break;
        }
        p += d*rd*0.85;
    }

    if (hit)
    {
        if (d == ey)
        {
            col = vec3(0.);
        }
        else if (d == bo)
        {

            col = steven_color (vec2(atan(new_p.z,new_p.x), new_p.y));     
        }

        vec3 norm = abs(getnorm(p));
        col  *= vec3(smoothstep(0.4,0.45,norm.z));
    }

    fragColor = vec4(col,1.);
}

