float map( in vec3 p )
{
    // triangle	


    vec3 v0 = vec3(-1.0,0.0, 1.0);
    vec3 v1 = vec3(1.0, 0.0, 1.0);    
    vec3 v2 = vec3(1.0, 0.0, -1.0);    
    vec3 v3 = vec3(-1.0,0.0, -1.0);
    vec3 v4 = vec3(0.0, 1.0, 0.0)*vec3(1.0,3.0,1.0);	


    v0= (mdv * vec4(v0,1.0)).xyz;
    v1= (mdv * vec4(v1,1.0)).xyz;
    v2= (mdv * vec4(v2,1.0)).xyz;
    v3= (mdv * vec4(v3,1.0)).xyz;
    v4= (mdv * vec4(v4,1.0)).xyz;

	//triangulos
	float d11 = udTriangle( v0, v1, v4, p ) - 0.01;
    float d12 = udTriangle( v1, v2, v4, p ) - 0.01;
    float d13 = udTriangle( v3, v0, v4, p ) - 0.01;
    float d14 = udTriangle( v3, v2, v4, p ) - 0.01;

     ///cuadro inferior
    float d15 = udTriangle( v0, v2, v1, p ) - 0.01;
    float d16 = udTriangle( v0, v3, v2, p ) - 0.01;


    // ground plane
	float d2 = p.y + 2.0;

	float find =10000.0;
    find =min(find, d2);
    find =min(find, d11);
    find =min(find, d12);
    find =min(find, d13);
    find =min(find, d14);

    find =min(find, d15);
   // find =min(find, d16);



    return find;
}

float intersect( in vec3 ro, in vec3 rd )
{
	const float maxd = 10.0;
	float h = 1.0;
    float t = 0.0;
    for( int i=0; i<50; i++ )
    {
        if( h<0.001 || t>maxd ) break;
	    h = map( ro+rd*t );
        t += h;
    }

    if( t>maxd ) t=-1.0;

    return t;
}

vec3 calcNormal( in vec3 pos )
{
    vec3 eps = vec3(0.002,0.0,0.0);

	return normalize( vec3(
           map(pos+eps.xyy) - map(pos-eps.xyy),
           map(pos+eps.yxy) - map(pos-eps.yxy),
           map(pos+eps.yyx) - map(pos-eps.yyx) ) );
}

float calcSoftshadow( in vec3 ro, in vec3 rd, float k )
{
    float res = 1.0;
    float t = 0.0;
	float h = 1.0;
    for( int i=0; i<20; i++ )
    {
        h = map(ro + rd*t);
        res = min( res, k*h/t );
		t += clamp( h, 0.01, 1.0 );
		if( h<0.0001 ) break;
    }
    return clamp(res,0.0,1.0);
}

float calcOcclusion( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.02 + 0.025*float(i*i);
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos );
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return 1.0 - clamp( occ, 0.0, 1.0 );
}

vec3 lig = normalize(vec3(1.0,0.9,0.7));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 mo = iMouse.xy/iResolution.xy;
	mdv = setTranslation( 0.0, 0.0, -3.0 ) * 
		       setRotation( 0.6-6.0*mo.y+iTime, 0.0,  0.6 ) * 
		       setRotation( 0.0, 20.0+1.0*iTime - 6.3*mo.x, 0.0 );



    vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

	vec3 ro = vec3(0.0, 0.25, 2.0 );
	vec3 rd = normalize( vec3(p,-1.0) );

	vec3 col = vec3(0.0);

    float t = intersect(ro,rd);
    if( t>0.0 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal(pos);
		float sha = calcSoftshadow( pos + nor*0.01, lig, 32.0 );
		float occ = calcOcclusion( pos, nor );
		col =  vec3(0.9,0.6,0.3)*clamp( dot( nor, lig ), 0.0, 1.0 ) * sha;
		col += vec3(0.5,0.1,0.7)*clamp( nor.y, 0.0, 1.0 )*occ;
        col += 0.03;
		col *= exp( -0.2*t );
        col *= 1.0 - smoothstep( 5.0, 10.0, t );
	}

	col = pow( clamp(col,0.0,1.0), vec3(0.45) );

    fragColor = vec4( col, 1.0 );
}

