// From IQ's SDF library
// -------------------------------------------------------------------------------------------------
float sdStar(in vec2 p, in float r, in int n, in float m) // m=[2,n]
{
    // these 4 lines can be precomputed for a given shape
    float an = 3.141593/float(n);
    float en = 3.141593/m;
    vec2  acs = vec2(cos(an),sin(an));
    vec2  ecs = vec2(cos(en),sin(en)); // ecs=vec2(0,1) and simplify, for regular polygon,

    // reduce to first sector
    float bn = mod(atan(p.x,p.y),2.0*an) - an;
    p = length(p)*vec2(cos(bn),abs(sin(bn)));

    // line sdf
    p -= r*acs;
    p += ecs*clamp( -dot(p,ecs), 0.0, r*acs.y/ecs.y);
    return length(p)*sign(p.x);
}

float sdHorseshoe( in vec2 p, in vec2 c, in float r, in vec2 w )
{
    p.x = abs(p.x);
    float l = length(p);
    p = mat2(-c.x, c.y, 
              c.y, c.x)*p;
    p = vec2((p.y>0.0)?p.x:l*sign(-c.x),
             (p.x>0.0)?p.y:l );
    p = vec2(p.x,abs(p.y-r))-w;
    return length(max(p,0.0)) + min(0.0,max(p.x,p.y));
}
// -------------------------------------------------------------------------------------------------
vec2 sdCD2(vec3 p) {
	vec2 res = vec2(0.);
    float r = .1;
    float c1 = 1.;
    float c2 = 2.;
    float h = .1;
    float ngs = -.0027;
    float ngr = .03;
    p *= .9;

    float t = 3.14/2.;

    p.z += .5;
    float li = sdHorseshoe(p.xz, vec2(cos(t),sin(t)),.081 + ngs,vec2(0.48,0.4));
    li = opE(p, li, h);
    vec2 lif = vec2(li - ngr, c1);


    p.z = -p.z;
    p.z += 1.;
    float ri = sdHorseshoe(p.xz, vec2(cos(t),sin(t)),.081 + ngs,vec2(0.48,0.4));    
    ri = opE(p, ri, h);
    vec2 rif = vec2(ri - ngr, c2);


    p.z = -p.z;
    p.z += 1.;    
    float mi = sdHorseshoe(p.xz, vec2(cos(t),sin(t)),.755 + ngs,vec2(0.48,0.23));
    mi = opE(p, mi, h);
    vec2 mif = vec2(mi - ngr, c2);   

    p.z = -p.z;
    p.z += 1.;
    float o1 = sdHorseshoe(p.xz, vec2(cos(t),sin(t)),.78 + ngs,vec2(0.48,0.26));
    o1 = opE(p, o1, h);
    vec2 o1f = vec2(o1- ngr, c1);

    p.z = -p.z;
    p.z += 1.;
    float o2 = sdHorseshoe(p.xz, vec2(cos(t),sin(t)),1.035 + ngs,vec2(0.52,0.005));
    o2 = opE(p, o2, h);
    vec2 o2f = vec2(o2- ngr, c1);

    res = opU(lif, rif);
    res = opU(res, mif);
    res = opU(res, o1f);
    res = opU(res, o2f);        

    return res;
}


// -------------------------------------------------------------------------------------------------
vec2 GetDist(vec3 p) {
	vec2 res = vec2(0.);

    p *= .7;
	res = sdCD2(p);
    p /= .7;


    return res;
}

// -------------------------------------------------------------------------------------------------
vec2 RayMarch(vec3 ro, vec3 rd) {
	vec2 dO= vec2(0);
    vec2 dS=vec2(0);

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO.x;
        dS = GetDist(p);
        dO.x += dS.x;
        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    dO.y = dS.y;

    return dO;
}

vec3 GetNormal(vec3 p) {
	vec2 d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d.x - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}
