vec3 ambientColor = vec3(0.05, 0.15, 0.2);
vec3 diffuseColor = vec3(0.2, 0.6, 0.8);
vec3 specularColor = vec3(1.0, 1.0, 1.0);
vec3 lightDir = normalize(vec3(0.0, 4.0, 5.0));
vec3 spherePos = vec3(0.0, 0.5, 0.0);
// -------------------------------------------------------------------------------------------------


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;
    vec3 col = vec3(1);

    vec3 tot = vec3(0.0);
#if AA>1
    for( int ma=0; ma<AA; ma++ )
    for( int na=0; na<AA; na++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(ma),float(na)) / float(AA) - 0.5;
        uv = (fragCoord+o-.5*iResolution.xy)/iResolution.y;
#endif
    vec3 ro = vec3(0,6, -1);
    //ro.yz *= Rot(-m.y*3.14+1.);        
    //ro.xz *= Rot(-m.x*6.2831);
    ro.xz *= 5.*Rot(iTime/4.);

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);

    vec2 d = RayMarch(ro, rd);

    if(d.x<MAX_DIST) {

        if(d.y==1.) {
            ambientColor = vec3(0.);
            diffuseColor = vec3(0.);
        }
        if(d.y==2.){
            ambientColor = vec3(.7);
            diffuseColor = vec3(1.);
        }
        if(d.y==3.){
            ambientColor = vec3(.3, .0, .0);
            diffuseColor = vec3(1., .0, .0);
        }   
        if(d.y==4.){
            ambientColor = vec3(.0, .3, .0);
            diffuseColor = vec3(.0, 1.0, .0);
        }         
        vec3 p = ro + rd * d.x;
    	vec3 n = GetNormal(p);

        float occ = 0.5 + 0.5 * n.y;

        float amb = clamp(0.5 + 0.5 * n.y, 0.0, 1.0);
        float dif = clamp(dot(lightDir, n), 0.0, 1.0);

        vec3 h = normalize(-rd + lightDir);
        float spe = pow(clamp(dot(h, n), 0.0, 1.0), 64.0);

        col = amb * ambientColor * occ;        
        col += dif * diffuseColor * occ;
        col += dif * spe * specularColor * occ;


    }

    col = pow(col, vec3(.4545));	// gamma correction

    tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif

    fragColor=vec4(tot,1.0);

}

#define PI 3.14159265
#define MAXIT 128
#define EPSILON 0.001

#define minx(a, b) ((a.w) < (b.w) ? (a) : (b))
#define maxx(a, b) ((a.w) > (b.w) ? (a) : (b))

float t;

float sphere(vec3 r, float a)
{
	return length(r) - a;
}

float bat(vec3 r, float a)
{
	r *= mat3(
		cos((r.x * sin(t * 20.) * 0.5) * 1.), -sin((r.x * sin(t * 20.) * 0.5) * 1.), 0., 
		sin((r.x * sin(t * 20.) * 0.5) * 1.), cos((r.x * sin(t * 20.) * 0.5) * 1.), 0.,
		0., 0., 1.
	);
	float d = (abs(r.x*r.y)*3. + 1./a) * (abs(sqrt(r.y+0.1) * r.y) + sin(r.y) + 1./a) * (abs(r.z) + 1./a);
	return length(r) - 1./ d;
}

float plane(vec3 r, vec3 o, vec3 n) {
	return dot(r - o, n);
}

float box(vec3 r, vec3 a)
{
	r.x += sin(r.y * PI) * 0.1;
	vec3 p = abs(r) - a * (sin(length(abs(abs(r)*4. - 2.) * 8.)) + 2.);
	return length(max(p, 0.)) + min(max(p.x, max(p.y, p.z)), 0.);
}

float shade(vec3 n, vec3 rd)
{
	return clamp(max(dot(n, -rd), 0.) + 1., 0., 1.);
}

vec3 fog(float z, vec3 col, vec3 fogCol)
{
	return mix(fogCol, col, exp(-z));
}

mat3 obj;

vec4 map(vec3 r)
{
	obj = mat3(
		cos(sin(-t + r.z * 0.2) * 0.31), -sin(sin(-t+r.z * 0.2) * 0.51), 0., 
		sin(sin(-t + r.z * 0.2) * 0.41), cos(sin(-t+r.z * 0.2) * 0.51), 0.,
		0., 0., 1.
	);

	float z = r.z;

	vec4 s0 = vec4(
		vec3(0.0, 0.8, 0.) * 0.8,
		bat(mat3(3.) * (obj * mod(r + 1. - vec3(cos(t*2. * 0.785 + z) * 0.5+ 0.5, sin(t*2. * 0.785 + z) * 0.25, t) + 0.45, 4.) - 1.), 1.3)
	);
	vec4 b1 = vec4(
		vec3(0.3, 0.1, 2.3) * 0.12,
		box((mod(r + 3.0, 4.) - 1.0), vec3(0.1, 4., 0.1))
	);

	return minx(s0, b1);
}

float hash(vec2 r) {
	return fract(sin(dot(r, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	t = iTime;

	vec2 uv = fragCoord.xy / iResolution.xy;
	uv -= 0.5;
	uv.x *= iResolution.x / iResolution.y;

	mat3 cam = mat3(
		cos(sin(t) * 0.1), -sin(sin(t) * 0.1), 0., 
		sin(sin(t) * 0.1), cos(sin(t) * 0.1), 0.,
		0., 0., 1.
	) * mat3(
		1., 0., 0.,
		0., cos(0.15), -sin(0.15), 
		0., sin(0.15), cos(0.15)
	);

	vec3 ro = vec3(0., - 0.5, - 1.0 + t);
	vec3 rd = cam * normalize(vec3(uv, -1.));
	vec3 r = ro;

	vec4 col = vec4(1.);
	vec3 bcol = vec3(1., 0.0, 0.2) + 0.0;

	vec4 c = vec4(bcol, 1.);

	float sh = 1.;

	float rs = 0.;

	for (int i = 0; i < MAXIT; ++i) {
		vec4 d = map(r);
		float z = length(r - ro);

		vec4 nc = minx(c, d);
		c.rgb = mix(nc.rgb, c.rgb, nc.w / c.w);
		c.w = nc.w;

		if (d.w < EPSILON) {
			col.rgb = d.rgb * shade(normalize(r), rd);
			col.rgb = d.rgb;
			col.rgb = fog(z * 0.1, col.rgb, bcol);
			break;
		}

		d.w *= 0.7 - 0.1 * hash(uv);
		r += rd * clamp(d.w, -0.5, 0.5) * 0.7;

		sh += (float(i) / float(MAXIT));
	}

	col.rgb *= exp(-sh * sh * 0.02);

	if (c.w > EPSILON) {
		col.rgb = fog(c.w, c.rgb, bcol * 0.1) * 0.2;
	}

	fragColor = vec4(col.rgb, 1.);
}

float circ(vec2 p, float radius) {
    return step(length(p - 0.5), radius);   
}
float distanceBetween = 0.8;
// http://www.iquilezles.org/www/articles/palettes/palettes.htm
// As t runs from 0 to 1 (our normalized palette index or domain), 
//the cosine oscilates c times with a phase of d. 
//The result is scaled and biased by a and b to meet the desired constrast and brightness.
vec3 cosPalette( float t, vec3 a, vec3 b, vec3 c, vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}
float blendOverlay(float base, float blend) {
	return base<0.5?(2.0*base*blend):(1.0-2.0*(1.0-base)*(1.0-blend));
}
vec3 blendOverlay(vec3 base, vec3 blend) {
	return vec3(blendOverlay(base.r,blend.r),blendOverlay(base.g,blend.g),blendOverlay(base.b,blend.b));
}
vec3 blendOverlay(vec3 base, vec3 blend, float opacity) {
	return (blendOverlay(base, blend) * opacity + base * (1.0 - opacity));
}
vec3 blendColors(vec3 c1, vec3 c2) {
    if(length(c1) == 0.) { return c2; }
    if(length(c2) == 0.) { return c1; }
    return blendOverlay(c1, c2);
}
vec3 shapes(vec2 position, vec3 color) {
    float radius = 0.3;
    position = mod(position, distanceBetween);
    float inCircle = circ(position, radius);
    return color * inCircle;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 position = ( 2.* fragCoord - iResolution.xy ) /iResolution.y;

    float time = iTime;
     float drift =  mod(time/5., distanceBetween);
    vec3 col = cosPalette(0.5,vec3(0.1),vec3(0.3),vec3(1),vec3(time*0.01,time*0.1,time*.2));
    vec3 col2 = cosPalette(0.3,vec3(0.4),vec3(0.5),vec3(1),vec3(time*0.1,time*0.1,time*.3));
    vec3 col3 = cosPalette(0.1,vec3(0.2),vec3(0.1),vec3(1),vec3(time*0.2,time*0.4,time*.3));
    vec3 col4 = cosPalette(0.2,vec3(0.6),vec3(0.3),vec3(1),vec3(time*0.4,time*0.2,time*.5));
    vec3 shape = shapes(vec2(position.x + drift,                          position.y + distanceBetween/4.), vec3(.2, .62, 1.0));
    vec3 shape2 = shapes(vec2(position.x + distanceBetween/3. - 2.*drift, position.y), col);
    vec3 shape3 = shapes(vec2(position.x - drift,                         position.y + distanceBetween/2.), col3);
    vec3 shape4 = shapes(vec2(position.x + 2.*drift,                      position.y + 3.*distanceBetween/4.), col2);
     fragColor = vec4(blendColors(blendColors(blendColors(shape, shape2), shape3), shape4), 1.0);

}

