                  dot(x, vec3(-0.3333333333333333, -0.4714045207910317, -0.816496580927726)));
    float a = noise(seed, p.yzw);
    float b = noise(seed + 42.0, p.xzw + a * 0.5);
    float c = noise(seed + 84.0, p.xyw + b * 0.5);
    float d = noise(seed + 126.0, p.xyz + c * 0.5);
