#define nrm normalize
#define len length
#define inv inverse
#define tim iTime

float vol = 80.;

float sa(float x) { return 0.; }
float si(float x) { return 0.; }
mat2 r2(float x) { return mat2(cos(x), -sin(x), sin(x), cos(x)); }
mat3 r3x(float x) { return mat3(1,0,0,0,cos(x),-sin(x),0,sin(x),cos(x)); }
mat3 r3y(float x) { return mat3(cos(x),0,sin(x),0,1,0,-sin(x),0,cos(x)); }
mat3 r3z(float x) { return mat3(cos(x),-sin(x),0,sin(x),cos(x),0,0,0,1); }

float sdt(vec3 p, vec2 t) { vec2 q = vec2(length(p.xz)-t.x,p.y); return len(q) - t.y; }
vec4 ope(vec3 p, vec3 h) { vec3 q = abs(p)-h; return vec4(max(q,0.),min(max(q.x,max(q.y,q.z)),0.)); }
vec3 rpz(vec3 p, float c) { return vec3(p.xy, mod(p.z+.5*c,c)-.5*c); }
float sds(vec3 p, float s){ return len(p)-s; }

float map(vec3 p) {

  p*=r3z(tim+si(.2)*.5);
  p.y += cos(p.z*.1)*.1;
  p.x += sin(p.z*.1+2.5)*.1;

  vec3 pp=p;


  p = rpz(p, 12.);
  vec4 w = ope(p, vec3(.2, 0, 4.2));
  float d = w.w+sdt(w.xzy, vec2(.8, .05));


  for (float i=0.; i<12.;i++) {
    float ic = .4;
    float as = sa(.1+i*.02);
    float is = si(.1+i*.02);
    as = pow(as,.8);
    as*= .2;
    d = min(d, sds(p+vec3(
      vec2(cos(i*ic),sin(i*ic))*.95 * (.9+(cos(i+tim)*.5+.5)*.2),

      sin(i*10.+tim*2.+is*.1)*3.)

    *.8, // scale

    .2+as));
  }

  return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  // Normalized pixel coordinates (from 0 to 1)
  vec2 uv = fragCoord/iResolution.xy;
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1.);


  vec3 ro = vec3(0,sin(tim*2.)*.1,tim*5.+si(.1));
  vec3 rd = normalize(vec3(uv, 1));

  float sc = 64.;
  vec3 p = ro;
  float i, d;
  float e = 1e-3;
  bool hit=false;
  for (;i<sc;i++) { d=map(p); if(d<e){hit=true;break;} p+=d*rd;}

  vec2 h = vec2(e,0);
  vec3 n = nrm(vec3(
    map(p+h.xyy)-map(p-h.xyy),
    map(p+h.yxy)-map(p-h.yxy),
    map(p+h.yyx)-map(p-h.yyx)
  ));

  vec3 c = vec3(i/sc*1.2) * vec3(.44,.1,.14)*3.;
  if (hit) {
    c += .2*max(vec3(0), dot(n,nrm(vec3(1,2,-2))));
  } else {
    c.r = mix(c.r, c.b, len(uv));
    c.g = mix(c.g, c.b, len(uv));
  }
  c = pow(c, vec3(1.2));

    // Output to screen
    fragColor = vec4(c,1.0);
}

float metaball(vec2 p, float r)
{
    return r / dot(p, p);
}

void mainImage( out vec4 fragColor, in vec2 p )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = p/iResolution.xy * 2.0 - 1.0;


    vec2 p1 = uv + vec2(0.2*sin(iTime), -0.25*cos(iTime));
    vec2 p2 = uv + vec2(0.25*sin(iTime+2.0), 0.1*cos(iTime));
    vec2 p3 = uv + vec2(0.1*sin(iTime), 0.3*cos(iTime));

    float d1 = metaball(p1, .2);
    float d2 = metaball(p2, .2);
    float d3 = metaball(p3, .3);
    float d = d1*d2*d3;

    if (d > 6.6) {
        fragColor = mix(vec4(0.5, 0.8, 0.3, 1), vec4(0.5, 0.2, 0.7, 1), uv.x);
    } else if (d > 5.0) {
        fragColor = vec4(1.0);
    }
    else {
        fragColor = mix(vec4(0.5), vec4(0.3), sin(iTime));
    }    
}

//https://gizma.com/easing/
float ease(float a, float b, float t)
{
	t *= 2.0;
	if (t < 1.0) return (b-a)/2.0*t*t*t + a;
	t -= 2.0;
	return (b-a)/2.0*(t*t*t + 2.0) + a;
}

float pulse(float time, float bpm, float signature, float offset)
{
	return fract((time * bpm) / (60.0 * signature) - offset);
}

float cycle(float time, float bpm, float signature, float release, float offset)
{
	float pulse = pulse(time, bpm, signature, offset);
	return step(pulse, release) * (pulse / release);
}

float envelope(float t, float attack, float decay, float sustain)
{
	float attack_segment = (t / attack) * step(t, attack);
	float decay_segment = mix(1.0, sustain, (t-attack)/decay) * step(t-attack, decay) * step(attack, t);
	float sustain_segment = sustain * step(decay, t-attack);
	return attack_segment + decay_segment + sustain_segment;
}


vec3 hash3(vec3 p)
{
    mat3 seed = mat3(742.342, 823.457, 242.086,
                     247.999, 530.343, 634.112,
                     437.652, 139.485, 484.348);

    return fract(seed * sin(p)) * 2.0 - vec3(1.0);
}


float noise3(vec3 p)
{
    float f = (sqrt(4.0) - 1.0) / 3.0;
    mat3 skew = mat3(1.0 + f, f, f,
                     f, 1.0 + f, f,
					 f, f, 1.0 + f);
    float g = (1.0 - 1.0/sqrt(4.0)) / 3.0;
    mat3 invskew = mat3(1.0-g, -g, -g,
                        -g, 1.0-g, -g,
						-g, -g, 1.0-g);

    vec3 sp = skew * p;
    vec3 cell = floor(sp);
    vec3 d0 = fract(sp);

    float x0 = step(d0.x, d0.y);
	float x1 = step(d0.y, d0.z);
	float x2 = step(d0.z, d0.x);
    vec3 s0 = vec3(x2*(1.-x0), x0*(1.-x1), x1*(1.-x2));
    vec3 s1 = min(vec3(1.0), vec3(1.0) + vec3(x2-x0, x0-x1, x1-x2));

    vec3 sv[4] = vec3[4](cell,
                         cell + s0,
                         cell + s1,
                         cell + vec3(1.0));
    vec3 wv[4] = vec3[4](invskew * sv[0],
                         invskew * sv[1],
                         invskew * sv[2],
                         invskew * sv[3]);
    vec3 d[4] = vec3[4](p - wv[0],
                        p - wv[1],
                        p - wv[2],
                        p - wv[3]);

    vec4 weights = max(vec4(0.0), vec4(0.6) - vec4(dot(d[0], d[0]),
                                                   dot(d[1], d[1]),
                                                   dot(d[2], d[2]),
                                                   dot(d[3], d[3])));
    weights = weights * weights * weights * weights;

    return (dot(hash3(sv[0]), d[0]) * weights[0] +
            dot(hash3(sv[1]), d[1]) * weights[1] +
            dot(hash3(sv[2]), d[2]) * weights[2] +
            dot(hash3(sv[3]), d[3]) * weights[3]) * 16.0;
}

vec3 target_half_diagonal_hfov(float n, float alpha, float aspect)
{
	float half_width = tan(alpha * 0.5) * n;
	float half_height = half_width * aspect;
	return vec3(half_width, half_height, n);
}

vec3 target_half_diagonal_vfov(float n, float alpha, float aspect)
{
	float half_height = tan(alpha * 0.5) * n;
	float half_width = half_height * aspect;
	return vec3(half_width, half_height, n);
}


vec3 compute_ray_plane(vec3 half_diagonal, vec2 clip_coord)
{
	vec3 target = half_diagonal * vec3(clip_coord, 1.0);
	return normalize(target);
}

float sample_noise(vec3 p, float octave)
{
    float cv = (cycle(iTime, 60.0, 1.0, 0.5, 0.5) + cycle(iTime, 13.44, 1.0, 1.0, 0.25))/2.0;
    float ev = ease(19.0, 20.0, envelope(cv, 0.25, 0.4, 0.4));

    float density = 0.0;
    float max_i = exp2(octave);
    float bd = ev;
    float expo = 0.3;
    float scale = 4.5;
    float offset = +0.115;
    float of = 1.2;

    vec3 d2 = hash3(vec3(-1.0, -13.0, 0.0)) * iTime * 2.25;

    for (float i = 1.0; i < max_i; i *= 2.0)
        density += (bd * pow((noise3(((p*scale + d2/(i*of))*i)) + offset), expo)) / i;

    return max(0.0, density);
}

void mainImage(out vec4 frag_color, vec2 frag_coord)
{
	float aspect_ratio = iResolution.y / iResolution.x;
	float fov = 85.0 * 3.1415926536 / 180.0;
	float near = 0.1;
	vec3 half_diagonal = target_half_diagonal_vfov(near, fov, 1.0/aspect_ratio);
	vec2 clip_coord = ((frag_coord / iResolution.xy) - 0.5) * 2.0;

    mat3 view = mat3(cos(iTime), -sin(iTime), 0.0,
                     sin(iTime), cos(iTime), 0.0,
                     0.0, 0.0, 1.0);
    vec3 ro = vec3(0.0, 0.0, -2.0);
	vec3 rd = compute_ray_plane(half_diagonal, clip_coord);

    vec3 pc = ro + dot(rd, -ro) * rd;
    float x0 = length(pc);
    float x1 = 1.0 - x0*x0;
    float x2 = length(pc - ro) - x1;

    vec3 i0 = ro + rd*x2;
    vec3 i1 = i0 + 2.0*(pc - i0);

    float t0 = x2;
    float t1 = length(ro - i1);

    float stepcount = 8.0;
    float dt0 = (t1-t0)/stepcount;
    float t = 0.0;
    float density = 0.0;
    float i;

    //vec3 Ld = normalize(vec3(0.2 * cos(iTime), 0.2 * sin(iTime), 0.1));
    //vec3 Ld = normalize(vec3(0.0, 0.1, 0.1));
    vec3 Ld = normalize(vec3(0.2 * cos(1.0), 1.0, 0.2 * sin(1.0)));

    vec3 Lo = vec3(0.0);

    vec3 Li_sky = vec3(0.7, 0.35, 0.12) * 1.2;
    vec3 Li_light = vec3(0.97, 0.95, 0.92) * 1.5;

    float st0_count = stepcount + 1.0;
    float Tr0 = 1.0;
    float St0 = 0.0;

    for (i = 0.0; i < st0_count; i += 1.0)
    {
        float tx = iTime;
        float bpm = 20.0;
        float cv =(
            cycle(tx, bpm, 1.0, 0.05, 0.6) +
            //cycle(tx, bpm, 1.0, 0.2, 0.0) +
            cycle(tx, bpm, 1.33, 0.05, 0.1)) / 3.0;
        float ev0 = ease(0.05, 3.0, envelope(cv, 0.1, 0.8, 0.003));

        t = t0 + dt0*i*
            (1.0 -
             hash3(vec3(fract(frag_coord*iTime*1.5), iTime*20.0)).y
             *ev0);
        vec3 p = ro + t*rd;

        float st0 = sample_noise(p, 3.0);
        St0 += st0 * dt0;

        float t_max = 2.0;
        {
            vec3 pc = p + dot(Ld, -p) * Ld;
            float x0 = length(pc);
            float x1 = 1.0 - x0*x0;
            float x2 = length(pc - p) - x1;
            vec3 i0 = ro + rd*x2;
            vec3 i1 = i0 + 2.0*(pc - i0);
            float t0 = x2;
            float t1 = length(ro - i1);
            t_max = t1/2.0;
        }

        float st1_count = 64.0;
        float dt1 = (t_max / st1_count);
        float Tr1 = 1.0;
        float St1 = 0.0;
        for (float j = 0.0; j < st1_count; ++j)
        {
            float st1_t = (j + 1.0 + 0.152*(hash3(vec3(frag_coord*0.001, 0.0)).x * 0.5 + 0.5))*dt1;

            vec3 p1 = p + Ld*st1_t;
            float st1 = 0.0;
            if (length(p1) <= 1.0)
                st1 = sample_noise(p1, 1.0);
            else
            {
                float Tr2 = exp(-(St1+St0));
                Lo += (Tr2 * Li_sky * st0 * dt0);
                break;
            }

            St1 += st1 * dt1;
        }

        Tr1 = exp(-(St1+St0));
        Lo += (Tr1 * Li_light * st0 * dt0);
    }

    Tr0 = exp(-St0);
    Lo += (Tr0 * Li_sky);

    frag_color.xyz = Lo;
}

#define R(_)\
	mat2(\
    	2.0,\
      	(sin(t)/cos(t))\
        -sin(2.5),\
         sin(t)/cos(t)\
        -sin(2.5)\
    ,2.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 r = iResolution.xy;
	vec2 p=(gl_FragCoord.xy*2.-r)/min(r.y,r.x);
    for(int i=0;i<4;++i){
        p=abs(p)-0.34;p*=R(t);
    }
    float v = 1.0/(p.y)*(5.0/-log(p.y/.3));
    fragColor = vec4(v*vec3(1.0,1.0,1.0),1);
}

#define MOD3 vec3(.1031,.11369,.13787)

float dist(vec2 c1, vec2 c2) {
return pow((c1.x-c2.x)*(c1.x-c2.x)+(c1.y - c2.y)*(c1.y - c2.y),0.5);
}

vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * MOD3);
    p3 += dot(p3, p3.yxz+19.19);
    return -1.0 + 2.0 * fract(vec3((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y, (p3.y+p3.z)*p3.x));
}

float noise(vec3 p)
{
    vec3 pi = floor(p);
    vec3 pf = p - pi;

    vec3 w = pf * pf * (3.0 - 2.0 * pf);

    return 	mix(
        		mix(
                	mix(dot(pf - vec3(0, 0, 0), hash33(pi + vec3(0, 0, 0))), 
                        dot(pf - vec3(1, 0, 0), hash33(pi + vec3(1, 0, 0))),
                       	w.x),
                	mix(dot(pf - vec3(0, 0, 1), hash33(pi + vec3(0, 0, 1))), 
                        dot(pf - vec3(1, 0, 1), hash33(pi + vec3(1, 0, 1))),
                       	w.x),
                	w.z),
        		mix(
                    mix(dot(pf - vec3(0, 1, 0), hash33(pi + vec3(0, 1, 0))), 
                        dot(pf - vec3(1, 1, 0), hash33(pi + vec3(1, 1, 0))),
                       	w.x),
                   	mix(dot(pf - vec3(0, 1, 1), hash33(pi + vec3(0, 1, 1))), 
                        dot(pf - vec3(1, 1, 1), hash33(pi + vec3(1, 1, 1))),
                       	w.x),
                	w.z),
    			w.y);
}

